import React, { useState } from "react";
import { Pencil, Trash2 } from "lucide-react";
import PurchaseItemModal from "./PurchaseItemModal";

const PurchaseItemSection = () => {
  const [items, setItems] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editItem, setEditItem] = useState(null);
  const [deleteId, setDeleteId] = useState(null);

  const handleSave = (data) => {
    if (editItem) {
      setItems((prev) =>
        prev.map((i) =>
          i.id === editItem.id ? { ...i, ...data } : i
        )
      );
    } else {
      setItems((prev) => [
        ...prev,
        { id: Date.now(), ...data },
      ]);
    }

    setShowModal(false);
    setEditItem(null);
  };

  const confirmDelete = () => {
    setItems((prev) => prev.filter((i) => i.id !== deleteId));
    setDeleteId(null);
  };

  return (
    <div
      className="card"
      style={{
        background: "hsl(215,25%,12%)",
        border: "1px solid hsl(215,20%,25%)",
        color: "white",
      }}
    >
      {/* HEADER */}
      <div className="card-header d-flex justify-content-between">
        <h6 className="fw-bold mb-">Purchase Items</h6>
        <button
          className="btn btn-sm btn-success"
          onClick={() => setShowModal(true)}
        >
          + Purchase Item
        </button>
      </div>

      {/* LIST */}
      <div
        className="card-body p-0"
        style={{ maxHeight: "150px", overflowY: "auto" }}
      >
        {items.length === 0 ? (
          <p className="text-muted p-3 mb-0">
            No purchase items
          </p>
        ) : (
          items.map((item) => (
            <div
              key={item.id}
              className="p-3"
              style={{
                borderBottom:
                  "1px solid hsl(215,20%,25%)",
              }}
            >
              <div className="d-flex justify-content-between">
                <div>
                  <div className="fw-semibold">
                    {item.name}{" "}
                    <span className="text-info">
                      (Qty: {item.qty})
                    </span>
                  </div>
                  <div className="small text-muted">
                    {item.description}
                  </div>
                </div>

                <div>
                  <button
                    className="btn btn-sm btn-outline-primary me-2"
                    onClick={() => {
                      setEditItem(item);
                      setShowModal(true);
                    }}
                  >
                    <Pencil size={14} />
                  </button>
                  <button
                    className="btn btn-sm btn-outline-danger"
                    onClick={() => setDeleteId(item.id)}
                  >
                    <Trash2 size={14} />
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      {/* ADD / EDIT MODAL */}
      {showModal && (
        <PurchaseItemModal
          show={showModal}
          onClose={() => {
            setShowModal(false);
            setEditItem(null);
          }}
          onSave={handleSave}
          editItem={editItem}
        />
      )}

      {/* DELETE CONFIRM */}
      {deleteId && (
        <div className="modal fade show d-block">
          <div className="modal-dialog modal-dialog-centered">
            <div
              className="modal-content"
              style={{
                background: "hsl(215,25%,18%)",
                color: "white",
              }}
            >
              <div className="modal-body text-center">
                <p>Are you sure you want to delete?</p>
                <button
                  className="btn btn-secondary me-2"
                  onClick={() => setDeleteId(null)}
                >
                  Cancel
                </button>
                <button
                  className="btn btn-danger"
                  onClick={confirmDelete}
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PurchaseItemSection;
