import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const DueDatesCard = ({ containers = [] }) => {
  const dueItems = [];

  // Collect all taken history from all items in all slots
  containers.forEach((rack) => {
    rack.slots.forEach((slot) => {
      slot.items?.forEach((item) => {
        item.taken?.forEach((t) => {
          const overdue =
            t.returnDate
              ? Math.max(
                  0,
                  Math.floor(
                    (new Date() - new Date(t.returnDate)) /
                      (1000 * 60 * 60 * 24)
                  )
                )
              : 0;

          dueItems.push({
            item: item.name,
            user: t.user,
            takenDate: t.date,
            returnDate: t.returnDate,
            overdue,
          });
        });
      });
    });
  });

  const limitedList = dueItems.slice(0, 3); // show only 3

  return (
    <div
      className="card shadow-sm mb-4"
      style={{
        backgroundColor: "hsl(215, 25%, 14%)",
        color: "white",
        border: "1px solid hsl(215, 20%, 25%)",
      }}
    >
      <div className="card-header">
        <h5 className="mb-0">Item Due Dates</h5>
      </div>

      <div
        className="card-body"
        style={{ maxHeight: "230px", overflowY: "auto" }}
      >
        {limitedList.length === 0 ? (
          <p className="text-muted">No due items.</p>
        ) : (
          <table className="table table-dark table-sm">
            <thead>
              <tr>
                <th>Item</th>
                <th>Taken By</th>
                <th>Taken</th>
                <th>Return</th>
                <th>Overdue</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {limitedList.map((r, i) => (
                <tr key={i}>
                  <td>{r.item}</td>
                  <td>{r.user}</td>
                  <td>{r.takenDate}</td>
                  <td>{r.returnDate || "-"}</td>
                  <td>{r.overdue > 0 ? `${r.overdue} days` : "0"}</td>
                  <td>
                    <button className="btn btn-warning btn-sm">
                      Reminder
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default DueDatesCard;
