import React, { useState } from "react";
import { Pencil, Trash2 } from "lucide-react";
import PurchaseItemModal from "./PurchaseItemModal";

const PurchaseItemSection = () => {
  const [showModal, setShowModal] = useState(false);

  const [items, setItems] = useState([
    {
      id: 1,
      name: "LEDs",
      qty: 50,
      description: "Local vendor purchase",
    },
    {
      id: 2,
      name: "Resistors",
      qty: 100,
      description: "Bulk order",
    },
    {
      id: 3,
      name: "Capacitors",
      qty: 30,
      description: "Emergency stock",
    },
  ]);

  const handleDelete = (id) => {
    setItems((prev) => prev.filter((i) => i.id !== id));
  };

  return (
    <div
      className="card shadow-sm"
      style={{
        background: "hsl(215,25%,12%)",
        border: "1px solid hsl(215,20%,25%)",
        color: "white",
      }}
    >
      {/* HEADER */}
      <div
        className="card-header d-flex justify-content-between align-items-center"
        style={{
          background: "transparent",
          borderBottom: "1px solid hsl(215,20%,25%)",
        }}
      >
        <h6 className="mb-0 fw-bold">Purchase Items</h6>

        <button
          className="btn btn-sm btn-success"
          onClick={() => setShowModal(true)}
        >
          + Purchase Item
        </button>
      </div>

      {/* TABLE HEADER */}
      <div
        className="px-3 py-2 small text-muted"
        style={{ borderBottom: "1px solid hsl(215,20%,25%)" }}
      >
        <div className="row">
          <div className="col-3">Item</div>
          <div className="col-2">Qty</div>
          <div className="col-5">Description</div>
          <div className="col-2 text-end">Actions</div>
        </div>
      </div>

      {/* LIST (SCROLLABLE â€“ 2 ROWS) */}
      <div
        className="card-body p-0"
        style={{
          maxHeight: "120px", // ðŸ‘ˆ shows only ~2 rows
          overflowY: "auto",
        }}
      >
        {items.length === 0 ? (
          <p className="text-muted p-3 mb-0">No purchase items</p>
        ) : (
          items.map((item) => (
            <div
              key={item.id}
              className="px-3 py-2"
              style={{
                borderBottom: "1px solid hsl(215,20%,25%)",
              }}
            >
              <div className="row align-items-center">
                <div className="col-3 fw-semibold">
                  {item.name}
                </div>

                <div className="col-2 text-info">
                  {item.qty}
                </div>

                <div className="col-5 small text-muted">
                  {item.description}
                </div>

                <div className="col-2 text-end">
                  <button
                    className="btn btn-sm btn-outline-primary me-2"
                    title="Edit"
                  >
                    <Pencil size={14} />
                  </button>

                  <button
                    className="btn btn-sm btn-outline-danger"
                    title="Delete"
                    onClick={() => handleDelete(item.id)}
                  >
                    <Trash2 size={14} />
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      {/* MODAL */}
      {showModal && (
        <PurchaseItemModal
          show={showModal}
          onClose={() => setShowModal(false)}
        />
      )}
    </div>
  );
};

export default PurchaseItemSection;
