import React from "react";
import { Card, Badge, Row, Col } from "react-bootstrap";

const ContainerData = [
  { name: "Container A", slots: "12", occupied: "8", empty: "4" },
  { name: "Container B", slots: "12", occupied: "11", empty: "1" },
  { name: "Container C", slots: "12", occupied: "7", empty: "5" },
];

const ContainerSlot = () => {
  return (
    <Card className="bg-dark text-white border-0 shadow-sm p-4">
      {/* Header */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h4 className="fw-bold">Container and Slot Overview</h4>
        <Badge bg="secondary" pill>
          {ContainerData.length} Containers
        </Badge>
      </div>

      {/* Container Boxes */}
      <Row className="g-4">
        {ContainerData.map((container, index) => (
          <Col key={index} md={4}>
            <Card className=" border-0 text-white p-4 rounded-4 h-100 shadow-sm ">
              <h5 className="fw-semibold mb-3 text-center">{container.name}</h5>
              <div className="text-center ">
                <div className="small mb-2">
                  <strong>Total Slots:</strong> {container.slots}
                </div>
                <div className="small mb-2">
                  <strong>Occupied Slots:</strong> {container.occupied}
                </div>
                <div className="small">
                  <strong>Empty Slots:</strong> {container.empty}
                </div>
              </div>
            </Card>
          </Col>
        ))}
      </Row>
    </Card>
  );
};

export default ContainerSlot;
