import React from "react";

const LowStockCard = ({ containers, onJumpToSlot }) => {
  const lowItems = [];

  containers.forEach((rack) => {
    rack.slots.forEach((slot) => {
      slot.items?.forEach((item) => {
        const totalTaken =
          item.taken?.reduce((n, t) => n + Number(t.qty), 0) || 0;

        const remaining = item.quantity - totalTaken;

        if (remaining < 6) {
          lowItems.push({
            itemName: item.name,
            remaining,
            rack: rack.name,
            slot: slot.slotNumber,
            containerId: rack.id,
          });
        }
      });
    });
  });

  return (
    <div
      className="card shadow-sm"
      style={{
        background: "hsl(215, 25%, 12%)",
        border: "1px solid hsl(215, 20%, 25%)",
        color: "white",
        height: "100%",
      }}
    >
      <div className="card-header fw-bold">Limited Stock Items</div>

      <div
        className="card-body"
        style={{
          maxHeight: "320px",
          overflowY: "auto",
          paddingRight: "6px",
        }}
      >
        {lowItems.length === 0 ? (
          <p className="text-muted">All items have sufficient stock.</p>
        ) : (
          lowItems.map((it, idx) => (
            <div
              key={idx}
              className="p-3 mb-3 rounded"
              style={{
                background: "hsl(215, 25%, 16%)",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            >
              <strong>{it.itemName}</strong>
              <div className="text-muted small">
                Remaining: {it.remaining}
              </div>
              <div className="text-muted small">
                {it.rack} â€¢ Slot {it.slot}
              </div>

              <button
                className="btn btn-primary btn-sm mt-2"
                onClick={() => onJumpToSlot(it.containerId, it.slot)}
              >
                + Add
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default LowStockCard;
