// components/admin/racks/SlotCard.jsx
//------------------------------------------------------
// Shows ONLY: Slot number, item name, and status.
// If slot has items → "Occupied"
// If slot empty → "Empty"
// No returnable/non-returnable badge.
//------------------------------------------------------

import React from "react";
import { Package } from "lucide-react";

const SlotCard = ({ slot }) => {
  const items = slot.items || [];
  const hasItems = items.length > 0;

  return (
    <div
      className="p-3 rounded shadow-sm h-100"
      style={{
        backgroundColor: "hsl(215, 25%, 14%)",
        border: "1px solid hsl(215, 20%, 25%)",
        cursor: "pointer",
      }}
    >
      {/* ICON + SLOT NUMBER */}
      <div className="d-flex align-items-center gap-2 mb-2">
        <Package size={18} className="text-info" />
        <strong>Slot {slot.slotNumber}</strong>
      </div>

      {/* MAIN CONTENT */}
      {hasItems ? (
        <>
          <div className="text-info fw-bold">
            {items[0].name}
          </div>

          <div className="small text-success fw-bold">
            Occupied
          </div>
        </>
      ) : (
        <div className="small text-muted fw-bold">
          Empty
        </div>
      )}
    </div>
  );
};

export default SlotCard;
