import React, { useEffect, useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  User,
  Mail,
  Shield,
  Calendar,
  Trash2,
  PlusCircle,
  Key,
  Copy,
} from "lucide-react";

const generatePassword = (length = 12) => {
  const upper = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
  const lower = "abcdefghijklmnopqrstuvwxyz";
  const digits = "0123456789";
  const symbols = "!@#$%&*()_=+[]{}?";
  const all = upper + lower + digits + symbols;

  let pwd = [
    upper[Math.floor(Math.random() * upper.length)],
    lower[Math.floor(Math.random() * lower.length)],
    digits[Math.floor(Math.random() * digits.length)],
    symbols[Math.floor(Math.random() * symbols.length)],
  ];

  while (pwd.length < length) {
    pwd.push(all[Math.floor(Math.random() * all.length)]);
  }

  return pwd.sort(() => 0.5 - Math.random()).join("");
};

const AllUsers = () => {
  const [users, setUsers] = useState([]);
  const [showModal, setShowModal] = useState(false);

  const [editMode, setEditMode] = useState(false);
  const [editUserId, setEditUserId] = useState(null);

  const [form, setForm] = useState({
    name: "",
    email: "",
    role: "User",
    phone: "",
  });

  const [generatedPassword, setGeneratedPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});
  const [alert, setAlert] = useState(null);

  const token = localStorage.getItem("token");

  // Fetch Users from Backend
  const fetchUsers = async () => {
    try {
      const res = await axios.get(
        "http://localhost:8000/api/admin/getUsers",
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setUsers(res.data.data);
    } catch (err) {
      console.error("Fetch Users Error:", err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // Validation
  const validate = () => {
    const e = {};
    if (!form.name.trim()) e.name = "Name required";
    if (!form.email.trim()) e.email = "Email required";
    if (!form.phone.trim()) e.phone = "Phone required";
    return e;
  };

  const resetForm = () => {
    setForm({ name: "", email: "", role: "User", phone: "" });
    setGeneratedPassword("");
    setShowPassword(false);
    setErrors({});
  };

  const openEditModal = (user) => {
    setEditMode(true);
    setEditUserId(user._id);

    setForm({
      name: user.name,
      email: user.email,
      role: user.role,
      phone: user.phone,
    });

    setGeneratedPassword("");
    setShowModal(true);
  };

  // Add + Edit User Integration
  const handleSaveUser = async (e) => {
    e.preventDefault();
    const eValidate = validate();
    if (Object.keys(eValidate).length > 0) {
      setErrors(eValidate);
      return;
    }

    try {
      const payload = {
        name: form.name,
        email: form.email,
        phone: form.phone,
        role: form.role,
        ...(generatedPassword && { password: generatedPassword }),
      };

      const url = editMode
        ? `http://localhost:8000/api/admin/editUser/${editUserId}`
        : "http://localhost:8000/api/admin/addUser";

      await axios({
        url,
        method: editMode ? "PUT" : "POST",
        data: payload,
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setAlert({
        type: "success",
        message: editMode
          ? "User updated successfully"
          : "User added successfully",
      });

      fetchUsers();
      resetForm();
      setShowModal(false);
      setEditMode(false);
      setEditUserId(null);

      setTimeout(() => setAlert(null), 2500);
    } catch (error) {
      console.error(error);
      setAlert({ type: "danger", message: "Error saving user" });
    }
  };

  const handleGenerate = () => {
    const pw = generatePassword(12);
    setGeneratedPassword(pw);
    setShowPassword(true);
  };

  const handleCopyPassword = async () => {
    navigator.clipboard.writeText(generatedPassword);
    setAlert({ type: "success", message: "Copied!" });
    setTimeout(() => setAlert(null), 2000);
  };

  return (
    <div className="container my-4">

      {/* Header */}
      <div className="d-flex justify-content-between mb-3">
        <h3 className="fw-bold">All Users</h3>
        <button
          className="btn btn-primary d-flex align-items-center"
          onClick={() => {
            resetForm();
            setEditMode(false);
            setShowModal(true);
          }}
        >
          <PlusCircle size={16} className="me-2" /> Add User
        </button>
      </div>

      {alert && (
        <div className={`alert alert-${alert.type}`}>{alert.message}</div>
      )}

      {/* User Cards */}
      {users.map((u) => (
        <div
          key={u._id}
          className="card p-3 mb-2 shadow-sm"
          style={{ background: "#141a21", color: "#fff" }}
        >
          <div className="d-flex justify-content-between align-items-center">
            <div>
              <h5 className="fw-bold mb-1">{u.name}</h5>
              <p className="small d-flex align-items-center gap-2">
                <Mail size={14} /> {u.email}
              </p>
              <p className="small">
                ðŸ“ž {u.phone} â€” <strong>{u.role}</strong>
              </p>
            </div>

            <button
              className="btn btn-secondary btn-sm"
              onClick={() => openEditModal(u)}
            >
              Edit
            </button>
          </div>
        </div>
      ))}

      {/* Add/Edit Modal */}
      {showModal && (
        <div className="modal d-block" style={{ background: "#00000088" }}>
          <div className="modal-dialog modal-dialog-centered modal-lg">
            <div className="modal-content bg-dark text-white">
              <form onSubmit={handleSaveUser}>
                <div className="modal-header">
                  <h5 className="modal-title">
                    {editMode ? "Edit User" : "Add User"}
                  </h5>
                  <button
                    type="button"
                    className="btn-close"
                    onClick={() => setShowModal(false)}
                  />
                </div>

                <div className="modal-body row g-3">
                  {/* Name */}
                  <div className="col-md-6">
                    <label>Name</label>
                    <input
                      className="form-control"
                      value={form.name}
                      onChange={(e) =>
                        setForm({ ...form, name: e.target.value })
                      }
                    />
                  </div>

                  {/* Email */}
                  <div className="col-md-6">
                    <label>Email</label>
                    <input
                      type="email"
                      className="form-control"
                      value={form.email}
                      onChange={(e) =>
                        setForm({ ...form, email: e.target.value })
                      }
                    />
                  </div>

                  {/* Role */}
                  <div className="col-md-6">
                    <label>Role</label>
                    <select
                      className="form-select"
                      value={form.role}
                      onChange={(e) =>
                        setForm({ ...form, role: e.target.value })
                      }
                    >
                      <option value="User">User</option>
                      <option value="Manager">Manager</option>
                      <option value="Admin">Admin</option>
                    </select>
                  </div>

                  {/* Phone */}
                  <div className="col-md-6">
                    <label>Phone</label>
                    <input
                      className="form-control"
                      value={form.phone}
                      onChange={(e) =>
                        setForm({ ...form, phone: e.target.value })
                      }
                    />
                  </div>

                  {/* Password */}
                  <div className="col-12">
                    <label>Password</label>
                    <div className="input-group">
                      <input
                        className="form-control"
                        readOnly
                        type={showPassword ? "text" : "password"}
                        value={generatedPassword}
                        placeholder={
                          editMode
                            ? "Click Generate to change password"
                            : "Click Generate"
                        }
                      />

                      <button
                        type="button"
                        className="btn btn-outline-secondary"
                        onClick={handleGenerate}
                      >
                        <Key size={14} /> Generate
                      </button>

                      <button
                        type="button"
                        className="btn btn-outline-secondary"
                        onClick={() => setShowPassword(!showPassword)}
                      >
                        {showPassword ? "Hide" : "Show"}
                      </button>

                      <button
                        type="button"
                        className="btn btn-outline-secondary"
                        onClick={handleCopyPassword}
                        disabled={!generatedPassword}
                      >
                        <Copy size={14} />
                      </button>
                    </div>
                  </div>
                </div>

                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowModal(false)}
                  >
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    {editMode ? "Save Changes" : "Add User"}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

    </div>
  );
};

export default AllUsers;
