// src/pages/dashboard/UniversalDashboard.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { UserCircle } from "lucide-react";
import api from "../../api/axios";

/* ==== Admin Components ==== */
import ContainerSummaryCard from "../../components/admin/dashboard/ContainerSummaryCard";
import PermissionRequests from "../../components/admin/dashboard/PermissionRequests";
import ItemDueCard from "../../components/admin/dashboard/DueDatesCard";
import LowStockCard from "../../components/admin/dashboard/LowStockCard";

/* ==== Manager Components ==== */
import ManagerPermissionModal from "../../components/manager/ManagerPermissionModal";

/* ==== User Components ==== */
import UserBorrowedItems from "../users/UserBorrowedItems";
import { dummyBorrowedItems } from "../../data/Mockdata";
import { Modal, Button, Form } from "react-bootstrap";

const StatusTag = ({ status }) => {
  const base = {
    padding: "4px 10px",
    borderRadius: 14,
    fontSize: 12,
    fontWeight: 700,
    display: "inline-block",
    minWidth: 72,
    textAlign: "center",
  };

  if (status === "Pending")
    return <span style={{ ...base, background: "hsl(45,100%,65%)", color: "#222" }}>{status}</span>;
  if (status === "Approved")
    return <span style={{ ...base, background: "hsl(140,40%,30%)", color: "white" }}>{status}</span>;
  if (status === "Rejected")
    return <span style={{ ...base, background: "hsl(10,80%,40%)", color: "white" }}>{status}</span>;
};

const UniversalDashboard = ({
  role,
  containers = [],
  permissionRequests = [],
  approvedPermissions = [],
  onPermissionApprove,
  onPermissionReject,
  onCreateRequest,
  onCancelRequest,
  currentUser,
}) => {

  const navigate = useNavigate();

  // Safe Hooks (Top Level)
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [form, setForm] = useState({
    itemName: "",
    quantity: 1,
    projectName: "",
    returnable: false,
    returnDate: "",
    message: "",
  });

  const [myRequests, setMyRequests] = useState([]); // backend data for user list
  const [selectedReq, setSelectedReq] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [showManagerModal, setShowManagerModal] = useState(false);

  // Load requests if user
  useEffect(() => {
    if (role === "user") {
      const fetchRequests = async () => {
        try {
          const res = await api.get("/users/requests");
          setMyRequests(res.data.data);
        } catch (err) {
          console.error("Failed to load requests", err);
        }
      };
      fetchRequests();
    }
  }, [role]);

  const jumpToSlot = (containerId, slotNumber) => {
    navigate(`/${role}/racks?jumpRack=${containerId}&jumpSlot=${slotNumber}`);
  };

  const openDetail = (req) => {
    setSelectedReq(req);
    setShowDetailModal(true);
  };

  const handleCreateSubmit = async (e) => {
    e.preventDefault();

    if (!form.itemName || !form.projectName || !form.quantity) {
      alert("Please fill required fields");
      return;
    }

    try {
      await api.post("/users/requestNewItem", {
        itemName: form.itemName,
        quantity: Number(form.quantity),
        project: form.projectName,
        message: form.message,
        isReturnable: form.returnable,
        returnDate: form.returnable ? form.returnDate : null,
      });

      const updated = await api.get("/users/requests");
      setMyRequests(updated.data.data);

      alert("Request submitted!");
      setShowCreateModal(false);

      setForm({
        itemName: "",
        quantity: 1,
        projectName: "",
        returnable: false,
        returnDate: "",
        message: "",
      });
    } catch (err) {
      console.error("Failed", err);
      alert("Something went wrong!");
    }
  };

  /* ------------------ ADMIN UI ------------------ */
  if (role === "admin") {
    const pending = permissionRequests.filter((r) => r.status === "Pending");

    return (
      <div className="container my-4">
        <h2 className="fw-bold mb-1">Dashboard</h2>

        {pending.length > 0 ? (
          <PermissionRequests
            permissionRequests={pending}
            onApprove={onPermissionApprove}
            onReject={onPermissionReject}
          />
        ) : (
          <p className="text-muted">No pending requests</p>
        )}

        <div className="row g-3 mt-4">
          {containers.map((c) => (
            <div key={c.id} className="col-sm-6 col-lg-4">
              <ContainerSummaryCard container={c} />
            </div>
          ))}
        </div>
      </div>
    );
  }

  /* ------------------ MANAGER UI ------------------ */
  if (role === "manager") {
    return (
      <div className="container my-4">
        <h2 className="fw-bold">Manager Dashboard</h2>
        <ManagerPermissionModal
          show={showManagerModal}
          onClose={() => setShowManagerModal(false)}
          request={selectedReq}
          onGiven={() => window.location.reload()}
        />
      </div>
    );
  }

  /* ------------------ USER UI ------------------ */
  if (role === "user") {
    const userRequests = myRequests;

    return (
      <div className="container my-4">
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h3 className="fw-bold">Welcome, {currentUser?.name}</h3>
          <Button className="btn-primary" onClick={() => setShowCreateModal(true)}>
            Request Item
          </Button>
        </div>

        <div className="card p-3 mb-4">
          <h5>My Requests</h5>

          {userRequests.length === 0 ? (
            <p className="text-muted">No requests yet</p>
          ) : (
            userRequests.map((req) => (
              <div key={req._id} className="p-3 my-2 rounded bg-dark" onClick={() => openDetail(req)}>
                <strong>{req.itemName}</strong> — {req.project} — {req.quantity} pcs
                <br />
                <StatusTag status={req.status} />
              </div>
            ))
          )}
        </div>

        <div className="card p-2">
          <UserBorrowedItems borrowed={dummyBorrowedItems} />
        </div>

        {/* Request Modal */}
        <Modal show={showCreateModal} onHide={() => setShowCreateModal(false)} centered>
          <Modal.Header closeButton><Modal.Title>New Request</Modal.Title></Modal.Header>
          <Modal.Body>
            <Form onSubmit={handleCreateSubmit}>
              <Form.Group className="mb-3">
                <Form.Label>Item Name *</Form.Label>
                <Form.Control
                  value={form.itemName}
                  onChange={(e) => setForm({ ...form, itemName: e.target.value })}
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Project *</Form.Label>
                <Form.Control
                  value={form.projectName}
                  onChange={(e) => setForm({ ...form, projectName: e.target.value })}
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Quantity *</Form.Label>
                <Form.Control
                  type="number"
                  min="1"
                  value={form.quantity}
                  onChange={(e) => setForm({ ...form, quantity: e.target.value })}
                />
              </Form.Group>

              <Button type="submit" className="btn-primary w-100">Submit</Button>
            </Form>
          </Modal.Body>
        </Modal>

        {/* Detail Modal */}
        <Modal show={showDetailModal} onHide={() => setShowDetailModal(false)} centered>
          <Modal.Header closeButton><Modal.Title>Request Details</Modal.Title></Modal.Header>
          <Modal.Body>
            {selectedReq && (
              <>
                <p><strong>Item:</strong> {selectedReq.itemName}</p>
                <p><strong>Qty:</strong> {selectedReq.quantity}</p>
                <p><strong>Project:</strong> {selectedReq.project}</p>
                <p><strong>Status:</strong> {selectedReq.status}</p>
              </>
            )}
          </Modal.Body>
        </Modal>
      </div>
    );
  }

  return null;
};

export default UniversalDashboard;
