// src/pages/Containers.jsx
import React, { useState, useEffect } from "react";
import api from "../api/axios";
import SearchBar from "../components/admin/SearchBar";
import AddRackModal from "../components/admin/racks/AddRackModal";
import ContainerGrid from "../components/admin/racks/ContainerGrid";
import { useLocation } from "react-router-dom";

const Containers = () => {
  const location = useLocation();
  const [containers, setContainers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showRackModal, setShowRackModal] = useState(false);
  const [searchResult, setSearchResult] = useState("");

  // Load all racks from backend
  const fetchRacks = async () => {
    try {
      const res = await api.get("/racks/overview");
      setContainers(res.data.data);
      setLoading(false);
    } catch (err) {
      console.error("Failed to load racks:", err);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRacks();
  }, []);

  // ADD RACK
  const handleCreateRack = async (rackName, slotsCount) => {
    try {
      const res = await api.post("/racks/addRack", {
        rackName,
        slotsCount,
      });
      setSearchResult("Rack Added");
      fetchRacks();
      setTimeout(() => setSearchResult(""), 2000);
    } catch (err) {
      alert("Failed to Add Rack");
    }
  };

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between mb-4">
        <h3 className="fw-bold">Rack Management</h3>
        <button className="btn btn-primary" onClick={() => setShowRackModal(true)}>
          + Rack
        </button>
      </div>

      {searchResult && (
        <div className="alert alert-info py-2">{searchResult}</div>
      )}

      {loading ? (
        <p className="text-light">Loading racks...</p>
      ) : (
        containers.map((rack) => (
          <ContainerGrid
            key={rack.rackId}
            rack={rack}
            refresh={fetchRacks}
          />
        ))
      )}

      <AddRackModal
        show={showRackModal}
        onClose={() => setShowRackModal(false)}
        onCreate={handleCreateRack}
      />
    </div>
  );
};

export default Containers;
