import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Clock } from "lucide-react";
import { format } from "date-fns";

const RecentActivity = ({ containers }) => {
  // Extract taken items from new structure
  const recentItems = containers
    .flatMap((container) =>
      container.slots.flatMap((slot) =>
        slot.items
          ? slot.items.flatMap((item) =>
              (item.taken || []).map((t) => ({
                itemName: item.name,
                user: t.user,
                qty: t.qty,
                takenDate: t.date,
                returnDate: t.returnDate,
                containerName: container.name,
                slotNumber: slot.slotNumber,
                isReturnable: item.isReturnable,
              }))
            )
          : []
      )
    )
    .sort(
      (a, b) =>
        new Date(b.takenDate).getTime() - new Date(a.takenDate).getTime()
    )
    .slice(0, 5);

  return (
    <div
      className="card shadow-sm border-0 my-3"
      style={{
        backgroundColor: "hsl(215, 25%, 14%)",
        color: "hsl(210, 40%, 98%)",
        border: "1px solid hsl(215, 20%, 25%)",
      }}
    >
      <div
        className="card-header d-flex align-items-center gap-2"
        style={{
          backgroundColor: "hsl(215, 25%, 12%)",
          color: "hsl(210, 40%, 98%)",
          borderBottom: "1px solid hsl(215, 20%, 25%)",
        }}
      >
        <Clock className="text-primary" size={18} />
        <h5 className="mb-0">Recent Activity</h5>
      </div>

      <div className="card-body">
        {recentItems.length === 0 ? (
          <p className="text-center text-secondary py-4 mb-0">
            No recent activity
          </p>
        ) : (
          recentItems.map((it, idx) => (
            <div
              key={idx}
              className="d-flex justify-content-between align-items-center p-3 mb-2 border rounded shadow-sm"
              style={{
                backgroundColor: "hsl(215, 25%, 12%)",
                borderColor: "hsl(215, 20%, 25%)",
                transition: "background-color 0.2s ease, transform 0.2s ease",
              }}
            >
              <div>
                <p className="fw-semibold mb-1 small text-light">
                  {it.itemName}
                </p>
                <p className="text-secondary small mb-0">
                  {it.containerName} â€” Slot {it.slotNumber}
                </p>
              </div>

              <div className="text-end">
                <span
                  className={`badge ${
                    it.isReturnable ? "bg-primary" : "bg-danger"
                  }`}
                >
                  {it.isReturnable ? "Borrowed" : "Non-returnable"}
                </span>
                <p className="text-secondary small mb-0 mt-1">
                  {it.user} (Qty: {it.qty})
                </p>
                <p className="text-secondary small mb-0">
                  {format(new Date(it.takenDate), "MMM dd, HH:mm")}
                </p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default RecentActivity;
