// components/admin/SlotCard.jsx
import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Package, Calendar, User, Trash2, Plus } from "lucide-react";

/**
 * Props:
 *  - slot: { slotNumber, items: [ { id, name, quantity, isReturnable, taken: [ { user, qty, date, returnDate } ] } ] }
 *  - containerName
 *  - onDeleteSlot(containerId, slotNumber)
 *  - onAddItemButton(containerId, slotNumber)   // optional - triggers add modal from parent
 *  - containerId
 */
const SlotCard = ({ slot, containerName, containerId, onDeleteSlot, onAddItemButton }) => {
  const { slotNumber, items = [] } = slot;
  const firstItem = items.length > 0 ? items[0] : null;

  // badge logic based on first item (if any)
  const getStatusBadge = () => {
    if (!firstItem) return <span className="badge bg-secondary">Empty</span>;
    if (!firstItem.isReturnable) return <span className="badge bg-warning text-dark">Non-Returnable</span>;
    return <span className="badge bg-success">Occupied</span>;
  };

  return (
    <div
      className={`card p-3 mb-3 border-1 shadow-sm position-relative ${!firstItem ? "opacity-75" : ""}`}
      style={{
        cursor: "pointer",
        transition: "0.3s",
        borderColor: !firstItem ? "#ccc" : "#0d6efd50",
      }}
      onMouseEnter={(e) => (e.currentTarget.style.boxShadow = "0 4px 15px rgba(0,0,0,0.1)")}
      onMouseLeave={(e) => (e.currentTarget.style.boxShadow = "0 2px 8px rgba(0,0,0,0.05)")}
    >
      {/* Header */}
      <div className="d-flex justify-content-between align-items-start mb-3">
        <div className="d-flex align-items-center gap-2">
          <div
            className="rounded bg-light d-flex justify-content-center align-items-center"
            style={{ width: "40px", height: "40px" }}
          >
            <Package size={18} className="text-primary" />
          </div>
          <div>
            <p className="text-muted small mb-0">Slot {slotNumber}</p>
            {containerName && <p className="fw-semibold mb-0">{containerName}</p>}
          </div>
        </div>

        {getStatusBadge()}
      </div>

      {/* Body */}
      {firstItem ? (
        <div>
          {/* show top item summary */}
          <p className="fw-semibold mb-1">{firstItem.name}</p>
          <p className="small text-info mb-1">Total Qty: {firstItem.quantity}</p>




          {/* If multiple different items exist in this slot, show a small hint */}
          {items.length > 1 && (
            <p className="text-muted small mt-2 mb-0">{items.length} item types inside this slot</p>
          )}
        </div>
      ) : (
        <p className="text-muted small mb-0">Available slot</p>
      )}

      {/* bottom-right controls: Add item (+) and Delete slot (trash) */}
      <div style={{ position: "absolute", bottom: 8, right: 8, display: "flex", gap: 8 }}>
        {/* Add item: appears always so admin can add another item even if slot occupied */}
        <button
          onClick={(e) => {
            e.stopPropagation();
            if (onAddItemButton) onAddItemButton(containerId, slotNumber);
          }}
          title="Add item to this slot"
          className="btn p-1"
          style={{ background: "transparent", border: "none", color: "hsla(210, 40%, 98%, 0.9)" }}
        >
          <Plus size={18} />
        </button>

        {/* Delete slot */}
        <button
          onClick={(e) => {
            e.stopPropagation();
            if (onDeleteSlot) onDeleteSlot(containerId, slotNumber);
          }}
          title="Delete slot"
          className="btn p-1"
          style={{ background: "transparent", border: "none", color: "hsla(0, 8%, 37%, 1.00)" }}
        >
          <Trash2 size={18} />
        </button>
      </div>
    </div>
  );
};

export default SlotCard;
