import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import SlotCard from "./SlotCard";
import { Modal, Button, Form } from "react-bootstrap";
import { Trash2 } from "lucide-react";

const ContainerGrid = ({
  container,
  activeFilter = "All",
  onAddSlot,
  onDeleteRack,
  onDeleteSlot,      // ✅ ADDED BACK
  onExternalSlotClick
}) => {

  const occupiedSlots = container.slots.filter((s) => s.items.length > 0).length;
  const totalSlots = container.slots.length;

  const [selectedSlot, setSelectedSlot] = useState(null);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isDetailModalOpen, setIsDetailModalOpen] = useState(false);

  const [newItem, setNewItem] = useState({
    name: "",
    quantity: "",
    isReturnable: true,
    returnDate: "",
  });

  // ------------------ SLOT CLICK ------------------
  const handleSlotClick = (slotNumber) => {
    const slot = container.slots.find((s) => s.slotNumber === slotNumber);
    setSelectedSlot(slotNumber);

    if (slot.items.length > 0) setIsDetailModalOpen(true);
    else setIsAddModalOpen(true);

    if (onExternalSlotClick) onExternalSlotClick(container.id, slotNumber);
  };

  const selectedSlotData = container.slots.find(
    (s) => s.slotNumber === selectedSlot
  );

  // ------------------ FILTER LOGIC ------------------
  const matchesFilter = (slot) => {
    const item = slot.items[0];

    switch (activeFilter) {
      case "Returnable":
        return item && item.isReturnable === true;
      case "Non-returnable":
        return item && item.isReturnable === false;
      case "Occupied":
        return !!item;
      case "Empty":
        return !item;
      default:
        return true;
    }
  };

  // ==================================================
  // ================ UI BELOW ========================
  // ==================================================

  return (
    <>
      {/* MAIN CARD */}
      <div
        className="card shadow-sm my-3"
        style={{
          background:
            "linear-gradient(90deg, hsl(215, 25%, 12%) 0%, hsl(215, 25%, 10%) 100%)",
          color: "hsl(210, 40%, 98%)",
          border: "1px solid hsl(215, 20%, 25%)",
          borderRadius: "10px",
        }}
      >
        {/* HEADER */}
        <div className="card-header d-flex flex-wrap justify-content-between align-items-center gap-2">
          <div className="d-flex align-items-center gap-3">
            <h5 className="mb-0 text-light">{container.name}</h5>
            <span className="text-muted small">
              {occupiedSlots}/{totalSlots} occupied
            </span>
          </div>

          <div className="d-flex align-items-center gap-2">

            {/* ADD SLOT */}
            <button
              className="btn btn-sm btn-outline-light"
              onClick={(e) => {
                e.stopPropagation();
                if (onAddSlot) onAddSlot(container.id);
              }}
            >
              + Slot
            </button>

            {/* DELETE RACK */}
            {onDeleteRack && (
              <button
                className="btn btn-sm btn-secondary border border-secondary"
                onClick={(e) => {
                  e.stopPropagation();
                  onDeleteRack(container.id);
                }}
              >
                <Trash2 size={14} />
              </button>
            )}

          </div>
        </div>

        {/* BODY */}
        <div className="card-body">
          <div className="row g-3">
            {container.slots.map((slot) => {
              const slotId = `rack-${container.id}-slot-${slot.slotNumber}`;
              const visible = matchesFilter(slot);

              return (
                <div
                  key={slotId}
                  id={slotId}
                  className="col-12 col-sm-6 col-md-4 col-lg-3"
                  onClick={() => handleSlotClick(slot.slotNumber)}
                  style={{
                    cursor: "pointer",
                    opacity: visible ? 1 : 0.35,
                    transition: "opacity 0.15s ease",
                    position: "relative",
                  }}
                >
                  <SlotCard
                    slot={slot}
                    containerName={container.name}
                    onDeleteSlot={(slotNum) =>
                      onDeleteSlot(container.id, slotNum)
                    }
                  />
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {/* ADD ITEM MODAL */}
      <Modal show={isAddModalOpen} onHide={() => setIsAddModalOpen(false)} centered size="md">
        <Modal.Header
          closeButton
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            color: "white",
          }}
        >
          <Modal.Title>Add Item to Slot {selectedSlot}</Modal.Title>
        </Modal.Header>

        <Modal.Body
          style={{
            backgroundColor: "hsl(215, 25%, 14%)",
            color: "white",
          }}
        >
          <Form>

            <Form.Group className="mb-3">
              <Form.Label>Item Name</Form.Label>
              <Form.Control
                type="text"
                value={newItem.name}
                onChange={(e) =>
                  setNewItem({ ...newItem, name: e.target.value })
                }
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Quantity</Form.Label>
              <Form.Control
                type="number"
                min="1"
                value={newItem.quantity}
                onChange={(e) =>
                  setNewItem({ ...newItem, quantity: e.target.value })
                }
              />
            </Form.Group>

          </Form>
        </Modal.Body>

        <Modal.Footer
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
          }}
        >
          <Button
            variant="secondary"
            onClick={() => setIsAddModalOpen(false)}
          >
            Cancel
          </Button>
          <Button variant="primary">Add Item</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default ContainerGrid;
