import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import api from "../../../api/axios";

const AddRackModal = ({ show, onClose, reload }) => {
  const [rackName, setRackName] = useState("");
  const [slotCount, setSlotCount] = useState(1);

  const handleCreate = async () => {
    if (!rackName.trim()) {
      alert("Please enter rack name");
      return;
    }

    try {
      await api.post("/racks/addRack", {
        rackName,
        slotsCount: slotCount,
      });

      alert("Rack created successfully!");
      setRackName("");
      setSlotCount(1);
      onClose();
      reload(); // ðŸ”¥ refresh UI
    } catch (err) {
      console.error(err);
      alert("Failed to create rack!");
    }
  };

  return (
    <Modal show={show} onHide={onClose} centered>
      <Modal.Header closeButton>
        <Modal.Title>Add New Rack</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group>
            <Form.Label>Rack Name</Form.Label>
            <Form.Control
              placeholder="Enter rack name"
              value={rackName}
              onChange={(e) => setRackName(e.target.value)}
            />
          </Form.Group>

          <Form.Group className="mt-3">
            <Form.Label>Slots Count</Form.Label>
            <Form.Control
              type="number"
              min="1"
              value={slotCount}
              onChange={(e) => setSlotCount(Number(e.target.value))}
            />
          </Form.Group>
        </Form>
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>Cancel</Button>
        <Button variant="primary" onClick={handleCreate}>Create Rack</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddRackModal;
