import React, { useState } from "react";
import { Button, Modal } from "react-bootstrap";
import { StatusTag } from "../../utils/StatusTag"; // keep this or import locally

const MyRequests = ({ requests = [], onCancel }) => {
  const [selectedReq, setSelectedReq] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);

  const openDetail = (req) => {
    setSelectedReq(req);
    setShowDetailModal(true);
  };

  const closeDetail = () => {
    setSelectedReq(null);
    setShowDetailModal(false);
  };

  const StatusTag = ({ status }) => {
  const base = {
    padding: "4px 10px",
    borderRadius: 14,
    fontSize: 12,
    fontWeight: 700,
    display: "inline-block",
    minWidth: 72,
    textAlign: "center",
  };

  if (status === "Pending")
    return <span style={{ ...base, background: "hsl(45,100%,65%)", color: "#222" }}>{status}</span>;
  if (status === "Approved")
    return <span style={{ ...base, background: "hsl(140,40%,30%)", color: "white" }}>{status}</span>;
  if (status === "Rejected")
  return <span style={{ ...base, background: "hsl(10,80%,40%)", color: "white" }}>{status}</span>;
};

  return (
    <div
      className="card p-3 mb-4"
      style={{
        background: "hsl(215,25%,12%)",
        border: "1px solid hsl(215,20%,25%)",
        color: "white",
      }}
    >
      <h5 className="fw-bold mb-3">My Requests</h5>

      {requests.length === 0 ? (
        <p className="text-muted">You have not made any requests yet.</p>
      ) : (
        requests.map((req) => (
          <div
            key={req._id}
            className="p-3 mb-3 rounded"
            style={{
              background: "hsl(215,25%,16%)",
              border: "1px solid hsl(215,20%,25%)",
              cursor: "pointer",
            }}
            onClick={() => openDetail(req)}
          >
            <div className="d-flex justify-content-between align-items-start">
              <div>
                <p className="mb-1"><strong>Item:</strong> {req.itemName}</p>
                <p className="mb-1"><strong>Project:</strong> {req.project}</p>
                <p className="mb-1"><strong>Qty:</strong> {req.quantity}</p>
              </div>

              <div className="text-center">
                <p className="mb-4 d-flex align-items-center justify-content-end gap-2">
                  <strong>Status:</strong>
                  <StatusTag status={req.status} />
                </p>

                {req.status === "Pending" && (
                  <Button
                    size="sm"
                    variant="secondary"
                    onClick={(e) => {
                      e.stopPropagation();
                      onCancel(req._id);
                    }}
                  >
                    Cancel
                  </Button>
                )}
              </div>
            </div>
          </div>
        ))
      )}

      {/* Detail Modal */}
      <Modal show={showDetailModal} onHide={closeDetail} centered>
        <Modal.Header closeButton style={{ background: "hsl(215,25%,12%)", color: "white" }}>
          <Modal.Title>Request Details</Modal.Title>
        </Modal.Header>

        <Modal.Body style={{ background: "hsl(215,25%,14%)", color: "white" }}>
          {selectedReq && (
            <div>
              <p><strong>Item:</strong> {selectedReq.itemName}</p>
              <p><strong>Quantity:</strong> {selectedReq.quantity}</p>
              <p><strong>Project:</strong> {selectedReq.project}</p>
              <p>
                <strong>Return Date:</strong>{" "}
                {selectedReq.returnDate || "Non-Returnable"}
              </p>
              <p><strong>Status:</strong> <StatusTag status={selectedReq.status} /></p>
              <hr className="text-muted" />

              <p><strong>Message:</strong></p>
              <div className="p-2 rounded" style={{ background: "hsl(215,25%,12%)" }}>
                {selectedReq.message || "No message"}
              </div>

              <hr className="text-muted" />
              <div className="mt-3 d-flex justify-content-end">
                {selectedReq.status === "Pending" && (
                  <Button
                    variant="primary"
                    onClick={() => {
                      onCancel(selectedReq._id);
                      closeDetail();
                    }}
                  >
                    Cancel Request
                  </Button>
                )}
              </div>
            </div>
          )}
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default MyRequests;
