// Slot Detail + Add Item Modal (Return Enabled - No UI Change)

import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import api from "../../../api/axios";

const SlotDetailModal = ({
  show,
  onClose,
  slot,
  containerId,
  onAddItem,
}) => {
  const [showAddForm, setShowAddForm] = useState(false);

  const [newItem, setNewItem] = useState({
    name: "",
    quantity: 1,
  });

  useEffect(() => {
    if (!show) {
      setShowAddForm(false);
      setNewItem({ name: "", quantity: 1 });
    }
  }, [show]);

  if (!slot) return null;

  const items =
    Array.isArray(slot.items) && slot.items.length > 0
      ? slot.items
      : slot.item
      ? [
          {
            id: slot.item.itemId,
            name: slot.item.itemName,
            quantity: slot.item.total,
            remaining: slot.item.remaining,
            takenHistory: slot.item.takenHistory || []
          },
        ]
      : [];

  const remainingQty = (it) => Number(it.remaining || it.quantity || it.total || 0);

  const submitAddItem = () => {
    if (!newItem.name.trim()) return;
    if (!slot.slotId) return;

    onAddItem(containerId, slot.slotId, {
      name: newItem.name.trim(),
      quantity: Number(newItem.quantity),
    });

    setShowAddForm(false);
    onClose();
  };

  // Return Item Action
  const handleReturnItem = async (item) => {
    const lastIssued = item.takenHistory?.find((t) => !t.returnDate);
    if (!lastIssued) return;

    try {
      alert(`Returning ${item.name} for ${lastIssued.userName}`);

      // Uncomment when backend API is ready:
      // await api.post(`/manager/returnItem/${containerId}/${slot.slotId}/${item.id || item.itemId}`, {
      //   historyId: lastIssued.historyId
      // });

      onClose();
    } catch (err) {
      console.error("Return failed:", err);
    }
  };

  return (
    <>
      <Modal show={show} onHide={onClose} centered size="lg">
        <Modal.Header closeButton>
          <Modal.Title>
            Slot {slot.slotNumber} <strong className="text-muted">-Details</strong>
          </Modal.Title>
        </Modal.Header>

        <Modal.Body
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            color: "white",
          }}
        >
          {!showAddForm &&
            items.map((it) => (
              <div
                key={it.id || it.itemId}
                className="p-3 mb-3 rounded"
                style={{
                  backgroundColor: "hsl(215, 25%, 16%)",
                  border: "1px solid hsl(215, 20%, 25%)",
                }}
              >
                <div className="d-flex justify-content-between">
                  <div>
                    <strong>{it.name || it.itemName}</strong>
                    <div className="small text-muted">
                      Total: {it.quantity || it.total}
                    </div>
                  </div>

                  <div className="text-end small">
                    <div>Remaining: {remainingQty(it)}</div>
                  </div>
                </div>

                {/* History */}
                {it.takenHistory?.length > 0 && (
                  <div className="mt-3 small">
                    <strong>Taken history:</strong>
                    {[...it.takenHistory]
                      .slice(-5)
                      .reverse()
                      .map((t, idx) => (
                        <div key={idx} className="text-muted">
                          • {t.userName} — Qty {t.quantity} —{" "}
                          {new Date(t.takenDate).toLocaleDateString("en-GB")}{" "}
                          {t.returnDate
                            ? `→ Return: ${new Date(t.returnDate).toLocaleDateString("en-GB")}`
                            : "(Not returned)"}
                        </div>
                      ))}
                  </div>
                )}

                {/* Return Button - replaces Give */}
                {remainingQty(it) < (it.quantity || it.total) && (
                <div className="d-flex justify-content-end mt-2 gap-2">
                <Button
                   variant="warning"
                   onClick={() => handleReturnItem(it)}
    >
      Return
    </Button>
  </div>
)}

              </div>
            ))}

          {/* Add new item */}
          {showAddForm && (
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Item Name</Form.Label>
                <Form.Control
                  value={newItem.name}
                  onChange={(e) =>
                    setNewItem({ ...newItem, name: e.target.value })
                  }
                  placeholder="Enter item name"
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Quantity</Form.Label>
                <Form.Control
                  type="number"
                  min="1"
                  value={newItem.quantity}
                  onChange={(e) =>
                    setNewItem({
                      ...newItem,
                      quantity: Number(e.target.value),
                    })
                  }
                />
              </Form.Group>
            </Form>
          )}
        </Modal.Body>

        <Modal.Footer>
          {!showAddForm && (
            <Button variant="primary" onClick={() => setShowAddForm(true)}>
              + Add Item
            </Button>
          )}

          {showAddForm && (
            <>
              <Button variant="secondary" onClick={() => setShowAddForm(false)}>
                Back
              </Button>
              <Button variant="primary" onClick={submitAddItem}>
                Save Item
              </Button>
            </>
          )}

          <Button variant="secondary" onClick={onClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default SlotDetailModal;
