// src/pages/users/UserBorrowedItems.jsx
import React, { useEffect, useState } from "react";
import api from "../../api/axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { Calendar, Package } from "lucide-react";

const UserBorrowedItems = () => {
  const [borrowed, setBorrowed] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchBorrowedItems = async () => {
      try {
        const res = await api.get("/users/borrowed");

        const formatted = res.data.data.map((item, index) => ({
          id: index,
          itemName: item.itemName,
          project: item.project,
          quantity: item.quantity,
          takenDate: item.takenDate ? item.takenDate.split("T")[0] : "-",
          returnDate:
            item.returnDate && item.returnDate !== "-"
              ? item.returnDate.split("T")[0]
              : "-",
          status: item.status,
          daysLeft: item.daysLeft,
          overdueDays: item.overdueDays,
        }));

        setBorrowed(formatted);
      } catch (err) {
        console.error(err);
        setError("Failed to load borrowed items");
      }
    };

    fetchBorrowedItems();
  }, []);

  return (
    <div className="container my-4">
      {/* Header */}
      <div className="mb-4">
        <h2 className="fw-bold mb-1">My Borrowed Items</h2>
        <p className="text-muted">Items you must return</p>
      </div>

      {/* Borrowed Items List */}
      <div className="d-flex flex-column gap-3">
        {borrowed.length === 0 && !error && (
          <p className="text-muted text-center">No active borrowed items.</p>
        )}

        {error && <p className="text-danger text-center">{error}</p>}

        {borrowed.map((item) => (
          <div
            key={item.id}
            className="card p-3"
            style={{
              background:
                "linear-gradient(90deg, hsl(215,25%,12%) 0%, hsl(215,25%,10%) 100%)",
              border: "1px solid hsl(215,20%,25%)",
            
              color: "white",
            }}
          >
            <div className="d-flex justify-content-between">
              {/* LEFT SIDE */}
              <div>
                <h6 className="fw-bold mb-1">
                  <Package size={16} className="me-1" />
                  {item.itemName}
                </h6>

                <small className="text-muted d-flex align-items-center gap-2">
                  <Calendar size={14} /> Taken: {item.takenDate}
                </small>

                <small className="text-muted d-flex align-items-center gap-2">
                  <Calendar size={14} /> Return:{" "}
                  {item.returnDate !== "-" ? item.returnDate : "—"}
                </small>
              </div>

              {/* RIGHT SIDE Logic */}
              <div className="text-end fw-bold">
                {item.daysLeft !== null ? (
                  <span style={{ color: "lightgreen" }}>
                    {item.daysLeft} Days Left
                  </span>
                ) : item.overdueDays !== null ? (
                  <span style={{ color: "red" }}>
                    Overdue by {item.overdueDays} days
                  </span>
                ) : (
                  <span className="text-muted">--</span>
                )}

                {item.status === "non-returnable" && (
                  <div className="badge bg-info mt-2">Non-Returnable</div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserBorrowedItems;
