import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { ArrowLeft } from "lucide-react";
import { mockContainers } from "../data/Mockdata";
import SlotCard from "../components/admin/SlotCard";
import { Modal, Button, Form } from "react-bootstrap";

const ContainerDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const container = mockContainers.find((c) => c.id === id);

  const [selectedSlot, setSelectedSlot] = useState(null);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isDetailModalOpen, setIsDetailModalOpen] = useState(false);

  const [newItem, setNewItem] = useState({
    name: "",
    takenBy: "",
    status: "occupied",
    returnDate: "",
  });

  if (!container) {
    return (
      <div
        className="container py-5 text-center"
        style={{
          backgroundColor: "hsl(215, 30%, 10%)",
          color: "hsl(210, 40%, 98%)",
          minHeight: "100vh",
        }}
      >
        <p className="text-muted">Container not found</p>
      </div>
    );
  }

  const handleSlotClick = (slotNumber) => {
    const slot = container.slots.find((s) => s.slotNumber === slotNumber);
    setSelectedSlot(slotNumber);

    if (slot?.item) setIsDetailModalOpen(true);
    else setIsAddModalOpen(true);
  };

  const handleAddItem = () => {
    alert(`${newItem.name} added to Slot ${selectedSlot}`);
    setIsAddModalOpen(false);
    setNewItem({ name: "", takenBy: "", status: "occupied", returnDate: "" });
  };

  const selectedSlotData = container.slots.find(
    (s) => s.slotNumber === selectedSlot
  );

  return (
    <div
      className="py-4"
      style={{
        minHeight: "100vh",
        backgroundColor: "hsl(215, 30%, 10%)",
        color: "hsl(210, 40%, 98%)",
      }}
    >
      {/* Header */}
      <div className="container mb-4">
        <div className="d-flex align-items-center mb-4">
          <Button
            variant="outline-light"
            onClick={() => navigate("/admin")}
            style={{
              backgroundColor: "transparent",
              border: "1px solid hsl(215, 20%, 25%)",
              color: "hsl(210, 40%, 90%)",
            }}
          >
            <ArrowLeft size={18} className="me-1" />
            Back
          </Button>
          <div className="ms-3">
            <h3 className="fw-bold mb-0">{container.name}</h3>
            <small className="text-secondary">
              {container.slots.filter((s) => s.item).length} of{" "}
              {container.slots.length} slots occupied
            </small>
          </div>
        </div>

        {/* Slots Grid */}
        <div className="row">
          {container.slots.map((slot) => (
            <div
              key={slot.slotNumber}
              className="col-6 col-md-4 col-lg-3 mb-3"
              onClick={() => handleSlotClick(slot.slotNumber)}
              style={{ cursor: "pointer" }}
            >
              <div
                className="p-3 rounded text-center"
                style={{
                  backgroundColor: slot.item
                    ? "hsl(215, 25%, 20%)"
                    : "hsl(215, 25%, 12%)",
                  border: "1px solid hsl(215, 20%, 25%)",
                  transition: "background-color 0.2s ease, transform 0.2s ease",
                }}
                onMouseEnter={(e) =>
                  (e.currentTarget.style.transform = "scale(1.03)")
                }
                onMouseLeave={(e) =>
                  (e.currentTarget.style.transform = "scale(1)")
                }
              >
                <h6 className="fw-bold mb-1 text-info">Slot {slot.slotNumber}</h6>
                {slot.item ? (
                  <p className="mb-0 small text-light">
                    {slot.item.name || "Occupied"}
                  </p>
                ) : (
                  <p className="mb-0 small text-muted">Empty</p>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Detail Modal */}
      <Modal
        show={isDetailModalOpen}
        onHide={() => setIsDetailModalOpen(false)}
        centered
        contentClassName="bg-dark text-light border-0"
      >
        <Modal.Header closeButton>
          <Modal.Title>Slot {selectedSlot} - Item Details</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedSlotData?.item ? (
            <>
              <div className="mb-3">
                <Form.Label className="text-info">Item Name</Form.Label>
                <p className="fw-semibold mb-0">
                  {selectedSlotData.item.name}
                </p>
              </div>
              <div className="mb-3">
                <Form.Label className="text-info">Status</Form.Label>
                <p className="text-capitalize mb-0">
                  {selectedSlotData.item.status.replace("-", " ")}
                </p>
              </div>
              <div className="mb-3">
                <Form.Label className="text-info">Taken By</Form.Label>
                <p className="mb-0">{selectedSlotData.item.takenBy}</p>
              </div>
              {selectedSlotData.item.takenDate && (
                <div className="mb-3">
                  <Form.Label className="text-info">Taken Date</Form.Label>
                  <p className="mb-0">
                    {new Date(
                      selectedSlotData.item.takenDate
                    ).toLocaleDateString()}
                  </p>
                </div>
              )}
              {selectedSlotData.item.returnDate && (
                <div className="mb-3">
                  <Form.Label className="text-info">Expected Return</Form.Label>
                  <p className="mb-0">
                    {new Date(
                      selectedSlotData.item.returnDate
                    ).toLocaleDateString()}
                  </p>
                </div>
              )}
            </>
          ) : (
            <p className="text-muted">No item found in this slot.</p>
          )}
        </Modal.Body>
      </Modal>

      {/* Add Item Modal */}
      <Modal
        show={isAddModalOpen}
        onHide={() => setIsAddModalOpen(false)}
        centered
        contentClassName="bg-dark text-light border-0"
      >
        <Modal.Header closeButton>
          <Modal.Title>Add Item to Slot {selectedSlot}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label className="text-info">Item Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter item name"
                value={newItem.name}
                onChange={(e) =>
                  setNewItem({ ...newItem, name: e.target.value })
                }
                style={{
                  backgroundColor: "hsl(215, 25%, 12%)",
                  color: "hsl(210, 40%, 98%)",
                  border: "1px solid hsl(215, 20%, 25%)",
                }}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label className="text-info">Taken By</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter user name"
                value={newItem.takenBy}
                onChange={(e) =>
                  setNewItem({ ...newItem, takenBy: e.target.value })
                }
                style={{
                  backgroundColor: "hsl(215, 25%, 12%)",
                  color: "hsl(210, 40%, 98%)",
                  border: "1px solid hsl(215, 20%, 25%)",
                }}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label className="text-info">Status</Form.Label>
              <Form.Select
                value={newItem.status}
                onChange={(e) =>
                  setNewItem({ ...newItem, status: e.target.value })
                }
                style={{
                  backgroundColor: "hsl(215, 25%, 12%)",
                  color: "hsl(210, 40%, 98%)",
                  border: "1px solid hsl(215, 20%, 25%)",
                }}
              >
                <option value="occupied">Returnable</option>
                <option value="non-returnable">Non-Returnable</option>
              </Form.Select>
            </Form.Group>

            {newItem.status === "occupied" && (
              <Form.Group className="mb-3">
                <Form.Label className="text-info">Return Date</Form.Label>
                <Form.Control
                  type="date"
                  value={newItem.returnDate}
                  onChange={(e) =>
                    setNewItem({ ...newItem, returnDate: e.target.value })
                  }
                  style={{
                    backgroundColor: "hsl(215, 25%, 12%)",
                    color: "hsl(210, 40%, 98%)",
                    border: "1px solid hsl(215, 20%, 25%)",
                  }}
                />
              </Form.Group>
            )}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => setIsAddModalOpen(false)}
            style={{
              backgroundColor: "hsl(215, 25%, 18%)",
              color: "#fff",
              border: "1px solid hsl(215, 20%, 25%)",
            }}
          >
            Cancel
          </Button>
          <Button
            variant="primary"
            onClick={handleAddItem}
            style={{
              backgroundColor: "hsl(210, 40%, 50%)",
              color: "hsl(215, 25%, 12%)",
              border: "1px solid hsl(215, 20%, 25%)",
            }}
          >
            Add Item
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ContainerDetail;
