import React, { useState, useEffect } from "react";
import { UserCircle } from "lucide-react";
import { useNavigate } from "react-router-dom";
import api from "../../api/axios";

// Manager Components
import ManagerPermissionModal from "../../components/manager/ManagerPermissionModal";
import ContainerSummaryCard from "../../components/admin/dashboard/ContainerSummaryCard";
import ItemDueCard from "../../components/admin/dashboard/DueDatesCard";
import LowStockCard from "../../components/admin/dashboard/LowStockCard";

const ManagerDashboard = () => {
  const navigate = useNavigate();

  const [approvedPermissions, setApprovedPermissions] = useState([]);
  const [racks, setRacks] = useState([]);

  const [showManagerModal, setShowManagerModal] = useState(false);
  const [selectedReq, setSelectedReq] = useState(null);

  // Navigate to slot in a rack
  const jumpToSlot = (rackId, slotNumber) => {
    navigate(`/manager/racks?jumpRack=${rackId}&jumpSlot=${slotNumber}`);
  };

  // Load Approved Permissions
  const loadApproved = async () => {
    try {
      const res = await api.get("/manager/approvedPermissions");
      setApprovedPermissions(res.data.data || []);
    } catch (err) {
      console.error("Failed to fetch approved permissions:", err);
    }
  };

  // Load Containers/Racks
  const loadRacks = async () => {
    try {
      const res = await api.get("/manager/racks");
      setRacks(res.data.data || []);
    } catch (err) {
      console.error("Failed to fetch racks:", err);
    }
  };

  useEffect(() => {
    loadApproved();
    loadRacks();
  }, []);

  return (
    <div className="container my-4">
      <h2 className="fw-bold mb-3">Manager Dashboard</h2>
      <p className="text-muted">Overview of Racks, Stock & User Permissions</p>

      {/* Approved Permissions Card */}
      <div
        className="card p-3 mb-4"
        style={{
          background: "hsl(215,25%,12%)",
          border: "1px solid hsl(215,20%,25%)",
          color: "white",
        }}
      >
        <h5 className="fw-bold mb-3">Approved User Permissions</h5>

        {approvedPermissions.length === 0 ? (
          <p className="text-muted">No approved permissions</p>
        ) : (
          approvedPermissions.map((req) => (
            <div
              key={req.id}
              className="d-flex justify-content-between align-items-center p-3 mb-3 rounded"
              style={{
                background: "hsl(215,25%,16%)",
                border: "1px solid hsl(215,20%,25%)",
                cursor: "pointer",
              }}
              onClick={() => {
                setSelectedReq(req);
                setShowManagerModal(true);
              }}
            >
              <div>
                <div className="fw-bold">
                  <UserCircle size={20} className="me-2" />
                  {req.userName}
                </div>
                <div className="text-muted">{req.userEmail}</div>
              </div>

              <div>
                <div className="text-muted">Item: {req.itemName}</div>
                <div className="text-muted">Qty: {req.quantity}</div>
              </div>

              <div className="text-end text-secondary fw-bold">View â†’</div>
            </div>
          ))
        )}
      </div>

      {/* Racks Summary */}
      <div className="row g-3 mb-4">
        {racks.map((rack) => (
          <div key={rack._id} className="col-12 col-sm-6 col-lg-4">
            <ContainerSummaryCard container={rack} basePath="/manager/racks" />
          </div>
        ))}
      </div>

      {/* Stock Alerts */}
      <div className="row g-4">
        <div className="col-lg-6">
          <ItemDueCard containers={racks} />
        </div>
        <div className="col-lg-6">
          <LowStockCard containers={racks} onJumpToSlot={jumpToSlot} />
        </div>
      </div>

      {/* PERMISSION MODAL */}
      <ManagerPermissionModal
        show={showManagerModal}
        onClose={() => setShowManagerModal(false)}
        request={selectedReq}
        onGiven={() => loadApproved()}
      />
    </div>
  );
};

export default ManagerDashboard;
