import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import api from "../../api/axios";
import NotificationToast from "../common/NotificationToast";

const ManagerPermissionModal = ({ show, onClose, request, onGiven }) => {
  if (!request) return null;

  const [rackId, setRackId] = useState("");
  const [slotId, setSlotId] = useState("");

  const [toast, setToast] = useState({
    show: false,
    message: "",
    bg: "success",
  });

  const handleGiveItem = async () => {
    if (!rackId || !slotId) {
      return setToast({
        show: true,
        message: "Rack ID & Slot ID required!",
        bg: "danger"
      });
    }

    try {
      await api.post(`/manager/giveItem/${request._id}`, {
        rackId,
        slotId,
      });

      setToast({
        show: true,
        message: "Item successfully given!",
        bg: "success"
      });

      setTimeout(() => {
        onGiven();
        onClose();
      }, 1000);

    } catch (err) {
      console.error(err);
      return setToast({
        show: true,
        message: "Failed to issue item!",
        bg: "danger"
      });
    }
  };

  return (
    <Modal show={show} onHide={onClose} centered>
      <Modal.Header closeButton className="bg-dark text-white">
        <Modal.Title>Give Item To User</Modal.Title>
      </Modal.Header>

      <Modal.Body className="bg-secondary text-white">
        <p><strong>User:</strong> {request.user.name}</p>
        <p><strong>Email:</strong> {request.user.email}</p>
        <p><strong>Item:</strong> {request.itemName}</p>
        <p><strong>Qty:</strong> {request.quantity}</p>

        <Form.Group className="mt-3">
          <Form.Label>Rack ID</Form.Label>
          <Form.Control
            type="text"
            value={rackId}
            onChange={(e) => setRackId(e.target.value)}
            placeholder="Enter Rack ID"
          />
        </Form.Group>

        <Form.Group className="mt-3">
          <Form.Label>Slot ID</Form.Label>
          <Form.Control
            type="text"
            value={slotId}
            onChange={(e) => setSlotId(e.target.value)}
            placeholder="Enter Slot ID"
          />
        </Form.Group>
      </Modal.Body>

      <Modal.Footer className="bg-dark">
        <Button variant="success" onClick={handleGiveItem}>
          Issue Item âœ“
        </Button>
      </Modal.Footer>

      {/* Toast */}
      <NotificationToast
        show={toast.show}
        message={toast.message}
        bg={toast.bg}
        onClose={() => setToast({ ...toast, show: false })}
      />
    </Modal>
  );
};

export default ManagerPermissionModal;
