// Handles: Add Slot, Delete Rack, Delete Slot,
// Slot Detail Modal, Add Item Modal, Filters

import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import api from "../../../api/axios";

import SlotCard from "./SlotCard";
import SlotDetailModal from "./SlotDetailModal";
import DeleteConfirmModal from "./DeleteConfirmModal";

const ContainerGrid = ({
  container,
  activeFilter = "All",
  onAddSlot,
  onDeleteRack,
  onDeleteSlot,
  onAddItem,
}) => {
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [showSlotModal, setShowSlotModal] = useState(false);
  const [showDeleteRackModal, setShowDeleteRackModal] = useState(false);
  const [showDeleteSlotModal, setShowDeleteSlotModal] = useState(false);
  const [slotToDelete, setSlotToDelete] = useState(null);

  const matchesFilter = (slot) => {
    const items = Array.isArray(slot.items) ? slot.items : [];
    return activeFilter === "Occupied"
      ? items.length > 0
      : activeFilter === "Empty"
      ? items.length === 0
      : true;
  };

  const handleSlotClick = async (slotNumber) => {
    const slotObj = container.slots.find((s) => s.slotNumber === slotNumber);
    if (!slotObj) return;

    try {
      const res = await api.get(
        `/racks/getSlotDetails/${container.rackId}/${slotObj.slotId}`
      );

      const slotData = res.data.data;

      setSelectedSlot({
        ...slotData,
        items:
          slotData.items?.length > 0
            ? slotData.items
            : slotData.item
            ? [
                {
                  id: slotData.item.itemId,
                  name: slotData.item.itemName,
                  quantity: slotData.item.total,
                  remaining: slotData.item.remaining,
                },
              ]
            : [],
        takenHistory: slotData.takenHistory || [],
      });

    } catch (err) {
      console.error("Failed to load slot details:", err);
      setSelectedSlot(slotObj);
    }

    setShowSlotModal(true);
  };

  const confirmDeleteSlot = () => {
    if (slotToDelete) {
      onDeleteSlot(container.rackId, slotToDelete);
    }
    setShowDeleteSlotModal(false);
  };

  const confirmDeleteRack = () => {
    onDeleteRack(container.rackId);
    setShowDeleteRackModal(false);
  };

  return (
    <>
      <div className="card shadow-sm my-3"
        style={{
          background: "linear-gradient(90deg, hsl(215, 25%, 12%), hsl(215, 25%, 10%))",
          color: "white",
          borderRadius: "10px",
        }}
      >
        <div className="card-header d-flex justify-content-between">
          <h5>{container.rackName}</h5>

          <div className="d-flex gap-2">
            <button
              className="btn btn-sm btn-outline-light"
              onClick={() => onAddSlot(container.rackId)}
            >
              + Slot
            </button>

            <button
              className="btn btn-sm btn-secondary"
              onClick={() => setShowDeleteRackModal(true)}
            >
              ðŸ—‘
            </button>
          </div>
        </div>

        <div className="card-body">
          <div className="row g-3">
            {container.slots.map((slot) => (
              <div
                key={slot.slotId}
                className="col-12 col-sm-6 col-md-4 col-lg-3"
                id={`rack-${container.rackId}-slot-${slot.slotNumber}`}
                onClick={() => handleSlotClick(slot.slotNumber)}
                style={{ cursor: "pointer", opacity: matchesFilter(slot) ? 1 : 0.3 }}
              >
                <SlotCard
                  slot={slot}
                  containerName={container.rackName}
                  containerId={container.rackId}
                  onDeleteSlot={(id, slotId) => {
                    setSlotToDelete(slot.slotId);
                    setShowDeleteSlotModal(true);
                  }}
                />
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Slot Detail Modal */}
      <SlotDetailModal
        show={showSlotModal}
        onClose={() => setShowSlotModal(false)}
        slot={selectedSlot}
        containerId={container.rackId}
        onAddItem={onAddItem}
        onDeleteSlot={() => {
          setSlotToDelete(selectedSlot?.slotId);
          setShowSlotModal(false);
          setShowDeleteSlotModal(true);
        }}
      />

      <DeleteConfirmModal
        show={showDeleteRackModal}
        title="Delete Rack?"
        message={`Are you sure you want to delete rack "${container.rackName}"?`}
        onConfirm={confirmDeleteRack}
        onCancel={() => setShowDeleteRackModal(false)}
      />

      <DeleteConfirmModal
        show={showDeleteSlotModal}
        title="Delete Slot?"
        message={`Delete slot permanently?`}
        onConfirm={confirmDeleteSlot}
        onCancel={() => setShowDeleteSlotModal(false)}
      />
    </>
  );
};

export default ContainerGrid;
