// src/pages/manager/ManagerLayout.jsx
import React, { useState, useEffect } from "react";
import { Outlet } from "react-router-dom";
import ManagerNavbar from "../../components/manager/ManagerNavbar";
import SidebarManager from "../../components/manager/SidebarManager";

const ManagerLayout = () => {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 992);

  // Detect screen width for mobile sidebar behavior
  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 992);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
    <div
      className="d-flex position-relative"
      style={{ minHeight: "100vh", overflow: "hidden" }}
    >
      {/* Sidebar */}
      <SidebarManager
        isOpen={sidebarOpen}
        onClose={() => setSidebarOpen(!sidebarOpen)}
      />

      {/* Main Area */}
      <div
        className="d-flex flex-column flex-grow-1"
        style={{
          transition: "margin-left 0.3s ease",
          marginLeft: isMobile ? 0 : sidebarOpen ? "240px" : "70px",
        }}
      >
        {/* Navbar */}
        <ManagerNavbar
          manager={{ name: "Manager User", email: "manager@gmail.com" }}
          onMenuClick={() => setSidebarOpen(!sidebarOpen)}
          isMobile={isMobile}
        />

        {/* Page Content */}
        <main
          className="flex-grow-1 p-4"
          style={{
            backgroundColor: "hsl(215,25%,14%)",
            minHeight: "100vh",
            overflowY: "auto",
          }}
        >
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default ManagerLayout;
