// src/pages/AllUsers.jsx
import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import api from "../api/axios"; // IMPORTANT: using axios instance
import {
  User,
  Mail,
  Shield,
  Calendar,
  Trash2,
  PlusCircle,
  Key,
  Copy,
} from "lucide-react";

const generatePassword = (length = 12) => {
  const upper = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
  const lower = "abcdefghijklmnopqrstuvwxyz";
  const digits = "0123456789";
  const symbols = "!@#$%&*()_=+[]{}?";
  const all = upper + lower + digits + symbols;

  let pwd = [
    upper[Math.floor(Math.random() * upper.length)],
    lower[Math.floor(Math.random() * lower.length)],
    digits[Math.floor(Math.random() * digits.length)],
    symbols[Math.floor(Math.random() * symbols.length)],
  ];

  for (let i = pwd.length; i < length; i++) {
    pwd.push(all[Math.floor(Math.random() * all.length)]);
  }

  pwd.sort(() => Math.random() - 0.5);
  return pwd.join("");
};

const AllUsers = () => {
  const [users, setUsers] = useState([]);
  const [alert, setAlert] = useState(null);

  const [showModal, setShowModal] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editUserId, setEditUserId] = useState(null);

  const [form, setForm] = useState({
    name: "",
    email: "",
    role: "User",
    phone: "",
  });

  const [generatedPassword, setGeneratedPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});

  // ==================== LOAD USERS FROM BACKEND ====================
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await api.get("/admin/getAllUsers");
        setUsers(response.data.data);
      } catch (error) {
        setAlert({
          type: "danger",
          message: error.response?.data?.message || "Failed to load users",
        });
      }
    };
    fetchUsers();
  }, []);

  const validate = () => {
    const e = {};
    if (!form.name.trim()) e.name = "Name required";
    if (!form.email.trim()) e.email = "Email required";
    if (!/^\S+@\S+\.\S+$/.test(form.email)) e.email = "Invalid email";
    if (!form.phone.trim()) e.phone = "Phone required";
    return e;
  };

  const resetForm = () => {
    setForm({ name: "", email: "", role: "User", phone: "" });
    setShowPassword(false);
    setGeneratedPassword("");
    setErrors({});
  };

  // ==================== ADD OR EDIT USER SUBMIT ====================
  const handleAddUser = async (ev) => {
    ev.preventDefault();
    const e = validate();
    if (Object.keys(e).length > 0) {
      setErrors(e);
      return;
    }

    const convertedRole = form.role.toLowerCase();

    if (!editMode) {
      // ADD USER
      try {
        const response = await api.post("/admin/addUser", {
          name: form.name,
          email: form.email,
          phone: form.phone,
          password: generatedPassword || generatePassword(12),
          role: convertedRole,
        });

        const addedUser = {
          ...response.data.data,
          joinDate:
            response.data.data.joined?.split("T")[0] ||
            new Date().toISOString().slice(0, 10),
        };

        setUsers((prev) => [addedUser, ...prev]);

        setAlert({ type: "success", message: "User added successfully" });
      } catch (error) {
        setAlert({
          type: "danger",
          message: error.response?.data?.message || "Failed to add user",
        });
        return;
      }
    }

    resetForm();
    setShowModal(false);
    setEditMode(false);
    setTimeout(() => setAlert(null), 2000);
  };

  const handleRemove = async (id) => {
    if (!window.confirm("Are you sure?")) return;
    try {
      await api.delete(`/admin/deleteUser/${id}`);
      setUsers((prev) => prev.filter((u) => u._id !== id));
      setAlert({ type: "success", message: "User removed" });
    } catch (error) {
      setAlert({
        type: "danger",
        message: error.response?.data?.message || "Delete failed",
      });
    }
    setTimeout(() => setAlert(null), 2000);
  };

  const handleGenerate = () => {
    setGeneratedPassword(generatePassword(12));
    setShowPassword(true);
  };

  const handleCopyPassword = async () => {
    await navigator.clipboard.writeText(generatedPassword);
    setAlert({ type: "success", message: "Copied" });
    setTimeout(() => setAlert(null), 1500);
  };

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-start mb-4">
        <div>
          <h2>All Users</h2>
          <p className="text-muted mb-0">Manage access</p>
        </div>

        <div className="d-flex gap-2 align-items-center">
          {alert && (
            <div className={`alert alert-${alert.type} py-2 mb-0`}>
              {alert.message}
            </div>
          )}

          <button
            className="btn btn-primary d-flex align-items-center"
            onClick={() => {
              resetForm();
              setEditMode(false);
              setShowModal(true);
            }}
          >
            <PlusCircle size={16} className="me-2" /> Add User
          </button>
        </div>
      </div>

      {/* ================= USERS LIST ================= */}
      <div className="d-flex flex-column gap-3">
        {users.map((user) => (
          <div
            key={user._id}
            className="card p-3 shadow-sm"
            style={{ backgroundColor: "#141A28" }}
          >
            <div className="d-flex justify-content-between align-items-center">
              <div className="d-flex align-items-center gap-3">
                <div
                  className="rounded-circle d-flex justify-content-center align-items-center"
                  style={{
                    width: 45,
                    height: 45,
                    backgroundColor: "#1E2635",
                  }}
                >
                  <User />
                </div>
                <div>
                  <h6 className="fw-bold mb-1">{user.name}</h6>
                  <small className="text-muted d-flex gap-2 align-items-center">
                    <Mail size={14} /> {user.email}
                  </small>
                  <small className="text-muted d-flex gap-2 align-items-center">
                    ðŸ“ž {user.phone}
                  </small>
                </div>
              </div>
              <div>
                <div className="d-flex align-items-center gap-2 small">
                  <Shield size={14} />
                  {user.role}
                </div>
                <div className="d-flex align-items-center gap-2 text-muted small">
                  <Calendar size={14} />
                  Joined: {user.joinDate}
                </div>
              </div>
              <button
                className="btn btn-danger btn-sm"
                onClick={() => handleRemove(user._id)}
              >
                <Trash2 size={14} className="me-1" /> Remove
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* ================= ADD USER MODAL ================= */}
      {showModal && (
        <div className="modal d-block">
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
              <form onSubmit={handleAddUser}>
                <div className="modal-header">
                  <h5 className="modal-title">Add User</h5>
                  <button
                    type="button"
                    className="btn-close"
                    onClick={() => setShowModal(false)}
                  ></button>
                </div>
                <div className="modal-body">
                  <div className="row g-3">
                    <div className="col-md-6">
                      <label>Name</label>
                      <input
                        className="form-control"
                        value={form.name}
                        onChange={(e) =>
                          setForm({ ...form, name: e.target.value })
                        }
                      />
                    </div>
                    <div className="col-md-6">
                      <label>Email</label>
                      <input
                        className="form-control"
                        value={form.email}
                        onChange={(e) =>
                          setForm({ ...form, email: e.target.value })
                        }
                      />
                    </div>
                    <div className="col-md-6">
                      <label>Role</label>
                      <select
                        className="form-select"
                        value={form.role}
                        onChange={(e) =>
                          setForm({ ...form, role: e.target.value })
                        }
                      >
                        <option>User</option>
                        <option>Manager</option>
                      </select>
                    </div>
                    <div className="col-md-6">
                      <label>Phone</label>
                      <input
                        className="form-control"
                        value={form.phone}
                        onChange={(e) =>
                          setForm({ ...form, phone: e.target.value })
                        }
                      />
                    </div>

                    {/* PASSWORD UI */}
                    <div className="col-12">
                      <label>Password (Auto)</label>
                      <div className="input-group">
                        <input
                          type={showPassword ? "text" : "password"}
                          className="form-control"
                          value={generatedPassword}
                          readOnly
                          placeholder="Click generate"
                        />
                        <button
                          type="button"
                          className="btn btn-outline-secondary"
                          onClick={handleGenerate}
                        >
                          <Key size={14} className="me-1" />
                          Generate
                        </button>
                        <button
                          type="button"
                          className="btn btn-outline-secondary"
                          disabled={!generatedPassword}
                          onClick={handleCopyPassword}
                        >
                          <Copy size={14} />
                        </button>
                      </div>
                    </div>
                  </div>
                </div>

                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowModal(false)}
                  >
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Add user
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AllUsers;
