// Slot Detail + Add Item + Edit Item + Return Item
import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import { Trash2 } from "lucide-react";
import api from "../../../api/axios";
import NotificationToast from "../../common/NotificationToast";

import AddItemModal from "./AddItemModal";


const SlotDetailModal = ({
  show,
  onClose,
  slot,
  containerId,
  onAddItem,
  refresh,
}) => {
  // MODALS
  const [showAddItemModal, setShowAddItemModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showReturnModal, setShowReturnModal] = useState(false);

  // DATA
  const [editItem, setEditItem] = useState(null);
  const [deleteItem, setDeleteItem] = useState(null);
  const [returnContext, setReturnContext] = useState(null);
  const [returnQty, setReturnQty] = useState(1);
  const [selectedBorrower, setSelectedBorrower] = useState("");

  // TOAST
  const [toast, setToast] = useState({
    show: false,
    message: "",
    bg: "success",
  });

  useEffect(() => {
    if (!show) {
      setShowAddItemModal(false);
      setShowEditModal(false);
      setShowDeleteModal(false);
      setShowReturnModal(false);
      setEditItem(null);
      setDeleteItem(null);
      setReturnContext(null);
      setReturnQty(1);
      setSelectedBorrower("");
    }
  }, [show]);

  if (!slot) return null;

  // ITEMS
  const items = Array.isArray(slot.items) ? slot.items : [];

  const remainingQty = (it) =>
    Number(
      it.remaining ?? it.quantity ?? it.total ?? 0
    );

  const totalQty = (it) =>
    Number(it.quantity ?? it.total ?? remainingQty(it));

  // ================= ADD ITEM =================
  const handleAddItemSubmit = (itemData) => {
    if (!slot.slotId) return;

    onAddItem(containerId, slot.slotId, itemData);
    refresh();
    setShowAddItemModal(false);
    onClose();

    setToast({
      show: true,
      message: "Item added successfully!",
      bg: "success",
    });
  };

  // ================= EDIT ITEM =================
  const openEditModal = (item) => {
    setEditItem({
      ...item,
      name: item.itemName,
      quantity: totalQty(item),
    });
    setShowEditModal(true);
  };

  const handleEditSave = async () => {
    if (!editItem) return;

    try {
      await api.put(
        `/racks/updateItem/${containerId}/${slot.slotId}/${editItem.itemId}`,
        {
          itemName: editItem.name,
          total: editItem.quantity,

          itemCategory: editItem.itemCategory,
          componentType: editItem.componentType,
          package: editItem.package,
          partNumber: editItem.partNumber,
          manufacturer: editItem.manufacturer,
          value: editItem.value,
          tolerance: editItem.tolerance,
          voltageRating: editItem.voltageRating,
          currentRating: editItem.currentRating,
          displayType: editItem.displayType,
          displayInterface: editItem.displayInterface,
          displayColor: editItem.displayColor,
          protectionType: editItem.protectionType,
          protectionLocation: editItem.protectionLocation,
          moduleType: editItem.moduleType,
        }
      );

      setToast({
        show: true,
        message: "Item updated successfully",
        bg: "success",
      });

      setShowEditModal(false);
      setEditItem(null);
      refresh();
      onClose();
    } catch (err) {
      setToast({
        show: true,
        message: err.response?.data?.message || "Update failed",
        bg: "danger",
      });
    }
  };

  // ================= DELETE ITEM =================
  const openDeleteModal = (item) => {
    setDeleteItem(item);
    setShowDeleteModal(true);
  };

  const handleDeleteItem = async () => {
    if (!deleteItem) return;

    try {
      await api.delete(
        `/racks/deleteItem/${containerId}/${slot.slotId}/${deleteItem.itemId}`
      );

      setToast({
        show: true,
        message: "Item deleted successfully",
        bg: "success",
      });

      setShowDeleteModal(false);
      setDeleteItem(null);
      refresh();
      onClose();
    } catch {
      setToast({
        show: true,
        message: "Failed to delete item",
        bg: "danger",
      });
    }
  };

  // ================= RETURN ITEM =================
  const openReturnModal = (item) => {
    if (!item.takenHistory?.length) {
      setToast({
        show: true,
        message: "No borrow history found",
        bg: "warning",
      });
      return;
    }

    setReturnContext({
      item,
      historyList: item.takenHistory,
    });
    setShowReturnModal(true);
  };

  const confirmReturn = async () => {
    const borrower = returnContext?.historyList.find(
      (h) => h.borrowId === selectedBorrower
    );

    if (!borrower) {
      setToast({
        show: true,
        message: "Select borrower",
        bg: "warning",
      });
      return;
    }

    if (returnQty < 1 || returnQty > borrower.quantity) {
      setToast({
        show: true,
        message: "Invalid quantity",
        bg: "danger",
      });
      return;
    }

    try {
      await api.post(`/manager/returnItem/${borrower.borrowId}`, {
        rackId: slot.rackId,
        rackName: slot.rackName,
        slotNumber: slot.slotNumber,
        returnQuantity: returnQty,
      });

      setToast({
        show: true,
        message: "Item returned successfully",
        bg: "success",
      });

      refresh();
      setShowReturnModal(false);
      setReturnContext(null);
      setReturnQty(1);
      setSelectedBorrower("");
      onClose();
    } catch {
      setToast({
        show: true,
        message: "Return failed",
        bg: "danger",
      });
    }
  };

  return (
    <>
      <NotificationToast
        show={toast.show}
        message={toast.message}
        bg={toast.bg}
        onClose={() => setToast((p) => ({ ...p, show: false }))}
      />

      {/* SLOT DETAILS */}
      <Modal show={show} onHide={onClose} centered size="lg">
        <Modal.Header closeButton>
          <Modal.Title>
            Slot {slot.slotNumber} <span className="text-muted">Details</span>
          </Modal.Title>
        </Modal.Header>

        <Modal.Body style={{ background: "#0f172a", color: "white" }}>
          {items.length === 0 && (
            <p className="text-muted">This slot is empty.</p>
          )}

          {items.map((it) => (
            <div
              key={it.itemId}
              className="p-3 mb-3 rounded"
              style={{ background: "#111827", border: "1px solid #1f2933" }}
            >
              <div className="d-flex justify-content-between">
                <div>
                  <strong>{it.itemName}</strong>
                  <div className="small text-muted">
                    Total: {totalQty(it)} | Remaining: {remainingQty(it)}
                    {it.itemCategory && ` | ${it.itemCategory}`}
                  </div>
                </div>

                <div className="d-flex gap-2">
                  <Button size="sm" onClick={() => openEditModal(it)}>
                    Edit
                  </Button>
                  <Button
                    size="sm"
                    variant="danger"
                    onClick={() => openDeleteModal(it)}
                  >
                    <Trash2 size={16} />
                  </Button>
                  {remainingQty(it) < totalQty(it) && (
                    <Button
                      size="sm"
                      variant="warning"
                      onClick={() => openReturnModal(it)}
                    >
                      Return
                    </Button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </Modal.Body>

        <Modal.Footer>
          <Button onClick={() => setShowAddItemModal(true)}>
            + Add Item
          </Button>
        </Modal.Footer>
      </Modal>

      {/* ADD ITEM */}
      <AddItemModal
        show={showAddItemModal}
        onClose={() => setShowAddItemModal(false)}
        onSubmit={handleAddItemSubmit}
        slotNumber={slot.slotNumber}
      />

      

      {/* DELETE CONFIRM */}
      <Modal show={showDeleteModal} onHide={() => setShowDeleteModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>Delete Item</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Delete <strong>{deleteItem?.itemName}</strong>?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>
            Cancel
          </Button>
          <Button variant="danger" onClick={handleDeleteItem}>
            Delete
          </Button>
        </Modal.Footer>
      </Modal>

      {/* RETURN MODAL */}
      <Modal show={showReturnModal} onHide={() => setShowReturnModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>Return Item</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {returnContext && (
            <>
              <Form.Select
                className="mb-3"
                value={selectedBorrower}
                onChange={(e) => setSelectedBorrower(e.target.value)}
              >
                <option value="">Select borrower</option>
                {returnContext.historyList.map((h) => (
                  <option key={h.borrowId} value={h.borrowId}>
                    {h.userName} ({h.quantity})
                  </option>
                ))}
              </Form.Select>

              <Form.Control
                type="number"
                min="1"
                value={returnQty}
                onChange={(e) => setReturnQty(Number(e.target.value))}
              />
            </>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowReturnModal(false)}>
            Cancel
          </Button>
          <Button variant="success" onClick={confirmReturn}>
            Confirm
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default SlotDetailModal;
