import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import { User } from "lucide-react";

const UserProfile = ({ currentUser, onUpdateUser }) => {
  const [editMode, setEditMode] = useState(false);

  const [form, setForm] = useState({
    name: currentUser?.name || "",
    email: currentUser?.email || "",
    phone: currentUser?.phone || "",
    role: currentUser?.role || "User",
  });

  const handleSave = () => {
    if (!form.name || !form.email || !form.phone) {
      alert("Please fill all fields.");
      return;
    }

    onUpdateUser(form); // parent updates mockUsers
    setEditMode(false);
  };

  return (
    <div className="container my-4">
      <h2 className="fw-bold mb-3">My Profile</h2>
      <p className="text-muted">View and edit your personal details</p>

      <div
        className="card p-4"
        style={{
          background: "hsl(215,25%,12%)",
          border: "1px solid hsl(215,20%,25%)",
          color: "white",
          borderRadius: 10,
          maxWidth: 600,
        }}
      >
        <div className="d-flex align-items-center gap-3 mb-4">
          <div
            className="rounded-circle d-flex justify-content-center align-items-center"
            style={{
              width: 60,
              height: 60,
              background: "hsl(215,25%,16%)",
              border: "1px solid hsl(215,20%,25%)",
            }}
          >
            <User size={32} />
          </div>

          <div>
            <h4 className="fw-bold mb-0">{form.name}</h4>
            <small className="text-muted">{form.role}</small>
          </div>
        </div>

        <Form>
          {/* NAME */}
          <Form.Group className="mb-3">
            <Form.Label>Name</Form.Label>
            <Form.Control
              disabled={!editMode}
              value={form.name}
              onChange={(e) => setForm({ ...form, name: e.target.value })}
              style={{
                backgroundColor: "hsl(215, 25%, 12%)",
                color: "white",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            />
          </Form.Group>

          {/* EMAIL */}
          <Form.Group className="mb-3">
            <Form.Label>Email</Form.Label>
            <Form.Control
              disabled={!editMode}
              value={form.email}
              onChange={(e) => setForm({ ...form, email: e.target.value })}
              style={{
                backgroundColor: "hsl(215, 25%, 12%)",
                color: "white",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            />
          </Form.Group>

          {/* PHONE */}
          <Form.Group className="mb-3">
            <Form.Label>Phone Number</Form.Label>
            <Form.Control
              disabled={!editMode}
              value={form.phone}
              onChange={(e) => setForm({ ...form, phone: e.target.value })}
              style={{
                backgroundColor: "hsl(215, 25%, 12%)",
                color: "white",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            />
          </Form.Group>

          {/* ROLE (NON-EDITABLE) */}
          <Form.Group className="mb-3">
            <Form.Label>Role</Form.Label>
            <Form.Control
              disabled
              value={form.role}
              style={{
                backgroundColor: "hsl(215, 25%, 12%)",
                color: "white",
                border: "1px solid hsl(215, 20%, 25%)",
                opacity: 0.6,
              }}
            />
          </Form.Group>
        </Form>

        {/* ACTION BUTTONS */}
        <div className="d-flex justify-content-end gap-2 mt-3">
          {!editMode ? (
            <Button onClick={() => setEditMode(true)} className="btn-primary">
              Edit Profile
            </Button>
          ) : (
            <>
              <Button variant="secondary" onClick={() => setEditMode(false)}>
                Cancel
              </Button>
              <Button variant="success" onClick={handleSave}>
                Save Changes
              </Button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
