// pages/Dashboard.jsx
//-----------------------------------------------------------
// UPDATED TO USE LIVE CONTAINER DATA, NOT STATIC mockContainers
// Shows:
//  - Rack summary cards
//  - Permission requests (unchanged)
//  - Recent Activity (with updated items[])
//-----------------------------------------------------------

import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import ContainerSummaryCard from "../components/admin/dashboard/ContainerSummaryCard";
//import RecentActivity from "../components/admin/dashboard/RecentActivity";
import PermissionRequests from "../components/admin/dashboard/PermissionRequests";
import ItemDueCard from "../components/admin/dashboard/DueDatesCard";
import LimitedStockItems from "../components/admin/dashboard/LimitedStockItems";


const Dashboard = ({
  containers,               // <-- LIVE data coming from App.jsx
  permissionRequests,       // unchanged
  onPermissionApprove,
  onPermissionReject,
}) => {

  // ---------------------------
// LOW STOCK ITEMS (< 6 Left)
// ---------------------------
const lowStockItems = [];

containers.forEach((container) => {
  container.slots.forEach((slot) => {
    if (!slot.items) return;

    slot.items.forEach((item) => {
      const used = (item.taken || []).reduce((sum, t) => sum + Number(t.qty), 0);
      const remaining = item.quantity - used;

      if (remaining < 6) {
        lowStockItems.push({
          id: item.id,
          name: item.name,
          remaining,
          containerId: container.id,
          containerName: container.name,
          slotNumber: slot.slotNumber,
        });
      }
    });
  });
});

  // JUMP TO RACK SLOT (same as search-bar jump)
const jumpToSlot = (containerId, slotNumber) => {
  const el = document.getElementById(`rack-${containerId}-slot-${slotNumber}`);
  if (el) {
    el.scrollIntoView({ behavior: "smooth", block: "center" });

    // highlight effect
    el.style.transition = "box-shadow 0.4s ease";
    el.style.boxShadow = "0 0 12px 5px yellow";

    setTimeout(() => {
      el.style.boxShadow = "none";
    }, 1500);
  }
};

  return (
    <div className="container my-4">
      {/* Header */}
      <div className="mb-4">
        <h2 className="fw-bold mb-1">Dashboard</h2>
        <p className="text-muted">
          Overview of your container management system
        </p>
      </div>

      {/* Permission Requests */}
      {permissionRequests?.length > 0 && (
        <div className="mb-4">
          <PermissionRequests
            permissionRequests={permissionRequests}
            onApprove={onPermissionApprove}
            onReject={onPermissionReject}
          />
        </div>
      )}

      {/* RACK CARDS */}
      <div className="row g-4 mb-4">
        {containers.map((container) => (
          <div
            key={container.id}
            className="col-12 col-sm-6 col-lg-4"
          >
            <ContainerSummaryCard container={container} />
          </div>
        ))}
      </div>
         
       {/* ====== ITEM DUE + LOW STOCK — SIDE BY SIDE ====== */}
<div className="row g-4 mt-4">

  {/* Left Card – Item Due Dates */}
  <div className="col-12 col-lg-6">
    <ItemDueCard containers={containers} />
  </div>

  {/* Right Card – Low Stock Items */}
  <div className="col-12 col-lg-6">
    <LimitedStockItems containers={containers} onJumpToSlot={jumpToSlot} />
  </div>

</div>

  
      {/* RECENT ACTIVITY */}
      {/* <RecentActivity containers={containers} /> */}
    </div>
  );
};

export default Dashboard;
