// src/pages/users/UserRacks.jsx
import React from "react";

const ItemCard = ({ item }) => {
  const totalTaken = (item.taken || []).reduce((acc, t) => acc + (t.qty || 0), 0);
  const remaining = item.quantity - totalTaken;
  return (
    <div style={{ background: "hsl(215,25%,14%)", padding: 10, borderRadius: 8, border: "1px solid hsl(215,20%,25%)" }}>
      <div className="fw-bold">{item.name}</div>
      <div className="small text-muted">Total: {item.quantity} â€¢ Remaining: {remaining}</div>
      {/* <div className="small text-muted">{item.isReturnable ? "Returnable" : "Non-returnable"}</div> */}
    </div>
  );
};

const UserRacks = ({ containers = [] }) => {
  return (
    <div className="container my-4">
      <h2 className="fw-bold mb-2">Racks</h2>
      <p className="text-muted">Read-only view of racks and items</p>

      <div className="row g-3">
        {containers.map((c) => (
          <div key={c.id} className="col-12">
            <div className="card p-3 mb-3" style={{ background: "hsl(215,25%,12%)", border: "1px solid hsl(215,20%,25%)", color: "white" }}>
              <div className="d-flex justify-content-between align-items-center mb-2">
                <h5 className="mb-0">{c.name}</h5>
                <div className="text-muted small">Slots: {c.slots.length}</div>
              </div>

              <div className="row g-2">
                {c.slots.map((slot) => (
                  <div key={slot.slotNumber} className="col-12 col-sm-6 col-md-4 col-lg-3">
                    <div style={{ padding: 10 }}>
                      <div className="text-muted small mb-1">Slot #{slot.slotNumber}</div>
                      {slot.items.length === 0 ? (
                        <div className="small text-muted">No items</div>
                      ) : (
                        slot.items.map((it) => <ItemCard key={it.id} item={it} />)
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserRacks;
