import React from "react";
import { Card, Row, Col, ProgressBar } from "react-bootstrap";
// Containers 

const ContainerStatus = () => {
  const containers = [
    { name: "Container A", occupied: 2, total: 12 },
    { name: "Container B", occupied: 2, total: 12 },
    { name: "Container C", occupied: 1, total: 12 },
  ];

  return (
    <div className="container py-4">
      <Row className="g-4">
        {containers.map((c, index) => {
          const available = c.total - c.occupied;
          const capacity = Math.round((c.occupied / c.total) * 100);

          return (
            <Col key={index} xs={12} sm={6} md={4}>
              <Card
                bg="dark"
                text="light"
                className="shadow-sm border border-secondary"
              >
                <Card.Body>
                  <Card.Title className="d-flex align-items-center mb-3">
                    <i className="bi bi-box-seam me-2 text-info"></i>
                    {c.name}
                  </Card.Title>

                  <div className="d-flex justify-content-between mb-1">
                    <small>Occupied</small>
                    <small className="text-info fw-bold">
                      {c.occupied}/{c.total}
                    </small>
                  </div>

                  <div className="d-flex justify-content-between mb-1">
                    <small>Available</small>
                    <small className="text-success fw-bold">{available}</small>
                  </div>

                  <ProgressBar
                    now={capacity}
                    variant="info"
                    className="mt-2"
                    style={{ height: "6px" }}
                  />

                  <Card.Text className="text-center text-muted mt-2 mb-0">
                    {capacity}% Capacity
                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>
          );
        })}
      </Row>
    </div>
  );
};

export default ContainerStatus;
