// components/admin/dashboard/LowStockCard.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const LowStockCard = ({ onJumpToSlot }) => {
  const [items, setItems] = useState([]);

  const fetchLowStock = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(
        "http://localhost:8000/api/manager/limitedStock",
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      if (res.data.data) {
        setItems(res.data.data);
      }
    } catch (err) {
      console.error("Low Stock Fetch Error:", err);
    }
  };

  useEffect(() => {
    fetchLowStock();
  }, []);

  return (
    <div
      className="card shadow-sm"
      style={{
        background: "hsl(215, 25%, 12%)",
        border: "1px solid hsl(215, 20%, 25%)",
        color: "white",
        height: "100%",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <div
        className="card-header fw-bold"
        style={{ background: "hsl(215, 25%, 18%)" }}
      >
        Limited Stock Items
      </div>

      <div
        className="card-body p-2"
        style={{
          flex: 1,
          overflowY: "auto",
        }}
      >
        {items.length === 0 ? (
          <div className="text-center text-muted mt-3">
            No low-stock items 
          </div>
        ) : (
          items.map((it, idx) => (
            <div
              key={idx}
              className="p-3 mb-3 rounded d-flex justify-content-between align-items-center"
              style={{
                background: "hsl(215, 25%, 16%)",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            >
              <div>
                <div className="fw-semibold">{it.itemName}</div>
                <div className="small text-muted">
                  Remaining:{" "}
                  <span
                    style={{
                      color: it.remaining < 3 ? "#ff4d4d" : "#ffd166",
                      fontWeight: "bold",
                    }}
                  >
                    {it.remaining}
                  </span>
                </div>

                <div className="text-muted small">
                  {it.rackName} • Slot {it.slotNumber}
                </div>
              </div>

              <button
                className="btn btn-outline-primary btn-sm"
                onClick={() =>
                  onJumpToSlot(it.containerId, it.slotNumber)
                }
              >
                Move →
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default LowStockCard;
