import React from "react";
import { Modal, Button } from "react-bootstrap";

const SlotDetailModal = ({ show, onClose, slot }) => {
  if (!slot) return null;

  const items = slot.items?.length > 0
    ? slot.items
    : slot.item
    ? [
        {
          name: slot.item.itemName,
          quantity: slot.item.total,
          remaining: slot.item.remaining,
        },
      ]
    : [];

  const remainingQty = (it) => Number(it.remaining || it.total || 0);

  return (
    <Modal show={show} onHide={onClose} centered size="lg">
      <Modal.Header closeButton>
        <Modal.Title>Slot {slot.slotNumber} — Details</Modal.Title>
      </Modal.Header>

      <Modal.Body style={{ backgroundColor: "hsl(215, 25%, 12%)", color: "white" }}>
        {items.length === 0 && (
          <p className="text-muted">This slot is empty.</p>
        )}

        {items.map((it, idx) => (
          <div
            key={idx}
            className="p-3 mb-3 rounded"
            style={{
              backgroundColor: "hsl(215, 25%, 16%)",
              border: "1px solid hsl(215, 20%, 25%)",
            }}
          >
            <strong>{it.name}</strong>
            <div className="small text-muted">Total: {it.quantity}</div>
            <div className="small">Remaining: {remainingQty(it)}</div>

            {slot.takenHistory?.length > 0 && (
              <div className="mt-3 small">
                <strong>Taken History:</strong>
                {slot.takenHistory.map((t, index) => (
                  <div key={index}>
                    • {t.userName} — Qty {t.quantity} —
                    {new Date(t.takenDate).toLocaleDateString()}{" "}
                    {t.returnDate
                      ? `(Return: ${new Date(t.returnDate).toLocaleDateString()})`
                      : "(Not returned)"}
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default SlotDetailModal;
