import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Check, X, Mail, Calendar, ClipboardList, MessageSquare } from "lucide-react";

const UserApproval = ({ pendingUsers = [], permissionRequests = [], onApprove, onReject }) => {
  // Approve/Reject Handlers
  const handleApprove = (user, type = "user") => {
    onApprove(user.id, type);
    alert(`${user.name || user.userName} has been approved successfully.`);
  };

  const handleReject = (user, type = "user") => {
    onReject(user.id, type);
    alert(`${user.name || user.userName} has been rejected.`);
  };

  const hasNoData =
    (!pendingUsers || pendingUsers.length === 0) &&
    (!permissionRequests || permissionRequests.length === 0);

  // âœ… When no approvals or permission requests exist
  if (hasNoData) {
    return (
      <div
        className="card shadow-sm my-3"
        style={{
          backgroundColor: "hsl(215, 25%, 14%)",
          color: "hsl(210, 40%, 98%)",
          border: "1px solid hsl(215, 20%, 25%)",
        }}
      >
        <div
          className="card-header"
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            color: "hsl(210, 40%, 98%)",
            borderBottom: "1px solid hsl(215, 20%, 25%)",
          }}
        >
          <h5 className="mb-0">Pending Approvals & Permissions</h5>
        </div>
        <div className="card-body text-center text-secondary py-4">
          No pending approvals or permission requests.
        </div>
      </div>
    );
  }

  return (
    <div
      className="card shadow-sm my-3"
      style={{
        backgroundColor: "hsl(215, 25%, 14%)",
        color: "hsl(210, 40%, 98%)",
        border: "1px solid hsl(215, 20%, 25%)",
      }}
    >
      {/* Header */}
      <div
        className="card-header d-flex justify-content-between align-items-center flex-wrap gap-2"
        style={{
          backgroundColor: "hsl(215, 25%, 12%)",
          color: "hsl(210, 40%, 98%)",
          borderBottom: "1px solid hsl(215, 20%, 25%)",
        }}
      >
        <h5 className="mb-0">Pending User & Permission Requests</h5>
        <span
          className="badge"
          style={{
            backgroundColor: "hsl(215, 20%, 20%)",
            color: "hsl(210, 40%, 98%)",
            border: "1px solid hsl(215, 20%, 25%)",
          }}
        >
          {(pendingUsers?.length || 0) + (permissionRequests?.length || 0)} pending
        </span>
      </div>

      {/* Card Content */}
      <div className="card-body">
        {/* ðŸ§‘â€ðŸ’» Pending Users Section */}
        {pendingUsers?.length > 0 && (
          <>
            <h6 className="text-info mb-3 d-flex align-items-center gap-1">
              <ClipboardList size={16} /> Pending Users
            </h6>
            {pendingUsers.map((user) => (
              <div
                key={user.id}
                className="d-flex flex-column flex-sm-row justify-content-between align-items-start align-items-sm-center border rounded p-3 mb-3 shadow-sm gap-3"
                style={{
                  backgroundColor: "hsl(215, 25%, 12%)",
                  borderColor: "hsl(215, 20%, 25%)",
                  transition: "background-color 0.2s ease, transform 0.2s ease",
                }}
                onMouseEnter={(e) =>
                  (e.currentTarget.style.backgroundColor = "hsl(215, 25%, 16%)")
                }
                onMouseLeave={(e) =>
                  (e.currentTarget.style.backgroundColor = "hsl(215, 25%, 12%)")
                }
              >
                <div className="flex-grow-1 text-start">
                  <p className="fw-semibold mb-1 text-light">{user.name}</p>
                  <div className="d-flex flex-wrap gap-3 small text-secondary">
                    <span className="d-flex align-items-center gap-1">
                      <Mail size={14} /> {user.email}
                    </span>
                    <span className="d-flex align-items-center gap-1">
                      <Calendar size={14} /> {user.registeredDate}
                    </span>
                  </div>
                </div>

                <div className="d-flex flex-wrap gap-2 justify-content-start justify-content-sm-end w-100 w-sm-auto">
                  <button
                    className="btn btn-success btn-sm d-flex align-items-center gap-1 flex-grow-1 flex-sm-grow-0"
                    onClick={() => handleApprove(user, "user")}
                    style={{
                      boxShadow: "0 0 10px rgba(25, 135, 84, 0.4)",
                    }}
                  >
                    <Check size={16} />
                    Approve
                  </button>
                  <button
                    className="btn btn-danger btn-sm d-flex align-items-center gap-1 flex-grow-1 flex-sm-grow-0"
                    onClick={() => handleReject(user, "user")}
                    style={{
                      boxShadow: "0 0 10px rgba(220, 53, 69, 0.4)",
                    }}
                  >
                    <X size={16} />
                    Reject
                  </button>
                </div>
              </div>
            ))}
          </>
        )}

        {/* ðŸŽ« Permission Requests Section */}
        {permissionRequests?.length > 0 && (
          <>
            <h6 className="text-warning mt-4 mb-3 d-flex align-items-center gap-1">
              <MessageSquare size={16} /> Permission Requests
            </h6>
            {permissionRequests.map((req) => (
              <div
                key={req.id}
                className="d-flex flex-column flex-sm-row justify-content-between align-items-start align-items-sm-center border rounded p-3 mb-3 shadow-sm gap-3"
                style={{
                  backgroundColor: "hsl(215, 25%, 12%)",
                  borderColor: "hsl(215, 20%, 25%)",
                }}
                onMouseEnter={(e) =>
                  (e.currentTarget.style.backgroundColor = "hsl(215, 25%, 16%)")
                }
                onMouseLeave={(e) =>
                  (e.currentTarget.style.backgroundColor = "hsl(215, 25%, 12%)")
                }
              >
                <div className="flex-grow-1 text-start">
                  <p className="fw-semibold mb-1 text-light">{req.userName}</p>
                  <div className="d-flex flex-wrap gap-3 small text-secondary mb-2">
                    <span className="d-flex align-items-center gap-1">
                      <Mail size={14} /> {req.userEmail}
                    </span>
                    <span className="d-flex align-items-center gap-1">
                      <Calendar size={14} /> {req.dateRequested}
                    </span>
                  </div>
                  <p className="small mb-1">
                    <strong>Type:</strong> {req.requestType}
                  </p>
                  <p className="text-muted small mb-0">{req.message}</p>
                </div>

                <div className="d-flex flex-wrap gap-2 justify-content-start justify-content-sm-end w-100 w-sm-auto">
                  <button
                    className="btn btn-success btn-sm d-flex align-items-center gap-1 flex-grow-1 flex-sm-grow-0"
                    onClick={() => handleApprove(req, "permission")}
                  >
                    <Check size={16} />
                    Approve
                  </button>
                  <button
                    className="btn btn-danger btn-sm d-flex align-items-center gap-1 flex-grow-1 flex-sm-grow-0"
                    onClick={() => handleReject(req, "permission")}
                  >
                    <X size={16} />
                    Reject
                  </button>
                </div>
              </div>
            ))}
          </>
        )}
      </div>
    </div>
  );
};

export default UserApproval;
