// src/pages/AllUsers.jsx
import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import api from "../api/axios";
import {
  User,
  Mail,
  Shield,
  Calendar,
  Trash2,
  PlusCircle,
  Key,
  Copy,
} from "lucide-react";

const generatePassword = (length = 12) => {
  const upper = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
  const lower = "abcdefghijklmnopqrstuvwxyz";
  const digits = "0123456789";
  const symbols = "!@#$%&*()_=+[]{}?";
  const all = upper + lower + digits + symbols;

  let pwd = [
    upper[Math.floor(Math.random() * upper.length)],
    lower[Math.floor(Math.random() * lower.length)],
    digits[Math.floor(Math.random() * digits.length)],
    symbols[Math.floor(Math.random() * symbols.length)],
  ];

  for (let i = pwd.length; i < length; i++) {
    pwd.push(all[Math.floor(Math.random() * all.length)]);
  }
  return pwd.sort(() => Math.random() - 0.5).join("");
};

const AllUsers = () => {
  const [users, setUsers] = useState([]);
  const [alert, setAlert] = useState(null);

  const [showModal, setShowModal] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editUserId, setEditUserId] = useState(null);

  const [form, setForm] = useState({
    name: "",
    email: "",
    role: "User",
    phone: "",
  });

  const [generatedPassword, setGeneratedPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});

  // ==================== LOAD USERS FROM API ====================
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await api.get("/admin/getAllUsers");

        const formattedUsers = response.data.data.map((u) => ({
          ...u,
          joinDate: u.joined ? u.joined.split("T")[0] : "-",
        }));

        setUsers(formattedUsers);
      } catch (error) {
        setAlert({
          type: "danger",
          message: error.response?.data?.message || "Failed to load users",
        });
      }
    };
    fetchUsers();
  }, []);

  const validate = () => {
    const e = {};
    if (!form.name.trim()) e.name = "Name required";
    if (!form.email.trim()) e.email = "Email required";
    if (!/^\S+@\S+\.\S+$/.test(form.email)) e.email = "Invalid email";
    if (!form.phone.trim()) e.phone = "Phone required";
    return e;
  };

  const resetForm = () => {
    setForm({ name: "", email: "", role: "User", phone: "" });
    setGeneratedPassword("");
    setShowPassword(false);
    setErrors({});
  };

  // ==================== ADD / EDIT USER ====================
  const handleUserSubmit = async (ev) => {
    ev.preventDefault();
    const e = validate();
    if (Object.keys(e).length > 0) return setErrors(e);

    const convertedRole = form.role.toLowerCase(); // User â†’ user, Manager â†’ manager

    try {
      if (editMode) {
        // UPDATE USER
        await api.put(`/admin/editUser/${editUserId}`, {
          name: form.name,
          email: form.email,
          phone: form.phone,
          role: convertedRole,
        });

        setUsers((prev) =>
          prev.map((u) =>
            u._id === editUserId
              ? { ...u, name: form.name, email: form.email, phone: form.phone, role: convertedRole }
              : u
          )
        );

        setAlert({ type: "success", message: "User updated successfully" });
      } else {
        // ADD USER
        const response = await api.post("/admin/addUser", {
          name: form.name,
          email: form.email,
          phone: form.phone,
          password: generatedPassword || generatePassword(),
          role: convertedRole,
        });

        const newUser = {
          ...response.data.data,
          joinDate: response.data.data.joined.split("T")[0],
        };

        setUsers((prev) => [newUser, ...prev]);
        setAlert({ type: "success", message: "User added successfully" });
      }
    } catch (error) {
      setAlert({
        type: "danger",
        message: error.response?.data?.message || "Action failed",
      });
      return;
    }

    resetForm();
    setEditMode(false);
    setShowModal(false);
    setTimeout(() => setAlert(null), 2000);
  };

  // DELETE USER
  const handleRemove = async (id) => {
    if (!window.confirm("Are you sure?")) return;
    try {
      await api.delete(`/admin/deleteUser/${id}`);
      setUsers((prev) => prev.filter((u) => u._id !== id));
      setAlert({ type: "success", message: "User removed" });
    } catch (error) {
      setAlert({
        type: "danger",
        message: error.response?.data?.message || "Delete failed",
      });
    }
  };

  return (
    <div className="container my-4">
      {/* Header */}
      <div className="d-flex justify-content-between align-items-start mb-4">
        <div>
          <h2>All Users</h2>
          <p className="text-muted mb-0">Manage access permissions</p>
        </div>

        <div className="d-flex gap-2 align-items-center">
          {alert && (
            <div className={`alert alert-${alert.type} py-2 mb-0`}>
              {alert.message}
            </div>
          )}
          <button
            className="btn btn-primary"
            onClick={() => {
              resetForm();
              setEditMode(false);
              setShowModal(true);
            }}
          >
            <PlusCircle size={16} className="me-2" />
            Add User
          </button>
        </div>
      </div>

      {/* ================= USER LIST ================= */}
      <div className="d-flex flex-column gap-3">
        {users.map((user) => (
          <div
            key={user._id}
            className="card p-3 shadow-sm"
            style={{ backgroundColor: "#141A28" }}
          >
            <div className="d-flex justify-content-between align-items-center flex-wrap">
              {/* User info */}
              <div className="d-flex align-items-center gap-3">
                <div
                  className="rounded-circle d-flex justify-content-center align-items-center"
                  style={{
                    width: 45,
                    height: 45,
                    backgroundColor: "#1E2635",
                  }}
                >
                  <User />
                </div>
                <div>
                  <h6 className="fw-bold mb-1">{user.name}</h6>
                  <small className="text-muted d-flex align-items-center gap-2">
                    <Mail size={14} /> {user.email}
                  </small>
                  <small className="text-muted d-flex align-items-center gap-2">
                    ðŸ“ž {user.phone}
                  </small>
                </div>
              </div>

              {/* Role + Date */}
              <div className="text-end">
                <div className="d-flex align-items-center justify-content-end gap-2 small">
                  <Shield size={14} />
                  {user.role}
                </div>
                <div className="d-flex align-items-center justify-content-end gap-2 text-muted small">
                  <Calendar size={14} />
                  Joined: {user.joinDate}

                </div>
              </div>

              {/* Buttons */}
              <div className="d-flex gap-2 mt-2">
                <button
                  className="btn btn-secondary btn-sm"
                  onClick={() => {
                    setEditMode(true);
                    setEditUserId(user._id);
                    setForm({
                      name: user.name,
                      email: user.email,
                      role: user.role.charAt(0).toUpperCase() + user.role.slice(1),
                      phone: user.phone,
                    });
                    setShowModal(true);
                  }}
                >
                  Edit
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleRemove(user._id)}
                >
                  <Trash2 size={14} className="me-1" />
                  Remove
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* ================= MODAL ================= */}
      {showModal && (
        <div className="modal d-block">
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
              <form onSubmit={handleUserSubmit}>
                <div className="modal-header">
                  <h5 className="modal-title">{editMode ? "Edit User" : "Add User"}</h5>
                  <button
                    type="button"
                    className="btn-close"
                    onClick={() => setShowModal(false)}
                  ></button>
                </div>

                <div className="modal-body">
                  <div className="row g-3">
                    <div className="col-md-6">
                      <label>Name</label>
                      <input
                        className="form-control"
                        value={form.name}
                        onChange={(e) =>
                          setForm({ ...form, name: e.target.value })
                        }
                      />
                    </div>

                    <div className="col-md-6">
                      <label>Email</label>
                      <input
                        className="form-control"
                        type="email"
                        value={form.email}
                        onChange={(e) =>
                          setForm({ ...form, email: e.target.value })
                        }
                      />
                    </div>

                    <div className="col-md-6">
                      <label>Role</label>
                      <select
                        className="form-select"
                        value={form.role}
                        onChange={(e) =>
                          setForm({ ...form, role: e.target.value })
                        }
                      >
                        <option>User</option>
                        <option>Manager</option>
                      </select>
                    </div>

                    <div className="col-md-6">
                      <label>Phone</label>
                      <input
                        className="form-control"
                        value={form.phone}
                        onChange={(e) =>
                          setForm({ ...form, phone: e.target.value })
                        }
                      />
                    </div>

                    {!editMode && (
                      <div className="col-12">
                        <label>Password (Auto)</label>
                        <div className="input-group">
                          <input
                            type={showPassword ? "text" : "password"}
                            className="form-control"
                            value={generatedPassword}
                            readOnly
                            placeholder="Click generate"
                          />
                          <button
                            type="button"
                            className="btn btn-outline-secondary"
                            onClick={() => {
                              setGeneratedPassword(generatePassword());
                              setShowPassword(true);
                            }}
                          >
                            <Key size={14} className="me-1" />
                            Generate
                          </button>
                          <button
                            type="button"
                            className="btn btn-outline-secondary"
                            disabled={!generatedPassword}
                            onClick={async () => {
                              await navigator.clipboard.writeText(
                                generatedPassword
                              );
                              setAlert({
                                type: "success",
                                message: "Password Copied",
                              });
                            }}
                          >
                            <Copy size={14} />
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                </div>

                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowModal(false)}
                  >
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    {editMode ? "Save Changes" : "Add User"}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AllUsers;
