import React from "react";
import { Modal, Button, Form } from "react-bootstrap";

const EditItemModal = ({
  show,
  onClose,
  editItem,
  setEditItem,
  onSave,
}) => {
  if (!editItem) return null;

  const category = editItem.itemCategory;

  return (
    <Modal show={show} onHide={onClose} centered size="lg" scrollable>
      <Modal.Header closeButton>
        <Modal.Title>Edit Item</Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <Form>
          {/* BASIC */}
          <Form.Group className="mb-3">
            <Form.Label>Item Name</Form.Label>
            <Form.Control
              value={editItem.name}
              onChange={(e) =>
                setEditItem({ ...editItem, name: e.target.value })
              }
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Total Quantity</Form.Label>
            <Form.Control
              type="number"
              min="1"
              value={editItem.quantity}
              onChange={(e) =>
                setEditItem({
                  ...editItem,
                  quantity: Number(e.target.value),
                })
              }
            />
          </Form.Group>

          {/* CATEGORY */}
          <Form.Group className="mb-3">
            <Form.Label>Item Category</Form.Label>
            <Form.Control value={category || "Others"} disabled />
          </Form.Group>

          {category && category !== "Others" && (
            <>
              <hr />
              <h6>Component Details</h6>

              {/* COMPONENT TYPE */}
              {(category === "Passive Component" ||
                category === "Discrete Component") && (
                <Form.Group className="mb-3">
                  <Form.Label>Component Type</Form.Label>
                  <Form.Control
                    value={editItem.componentType || ""}
                    onChange={(e) =>
                      setEditItem({
                        ...editItem,
                        componentType: e.target.value,
                      })
                    }
                  />
                </Form.Group>
              )}

              {/* MODULE */}
              {category === "Module" && (
                <Form.Group className="mb-3">
                  <Form.Label>Module Type</Form.Label>
                  <Form.Control
                    value={editItem.moduleType || ""}
                    onChange={(e) =>
                      setEditItem({
                        ...editItem,
                        moduleType: e.target.value,
                      })
                    }
                  />
                </Form.Group>
              )}

              {/* PACKAGE */}
              {category !== "Display" &&
                category !== "Protection Component" && (
                  <Form.Group className="mb-3">
                    <Form.Label>Package</Form.Label>
                    <Form.Control
                      value={editItem.package || ""}
                      onChange={(e) =>
                        setEditItem({
                          ...editItem,
                          package: e.target.value,
                        })
                      }
                    />
                  </Form.Group>
                )}

              {/* DISPLAY */}
              {category === "Display" && (
                <>
                  <Form.Group className="mb-3">
                    <Form.Label>Display Type</Form.Label>
                    <Form.Control
                      value={editItem.displayType || ""}
                      onChange={(e) =>
                        setEditItem({
                          ...editItem,
                          displayType: e.target.value,
                        })
                      }
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>Display Interface</Form.Label>
                    <Form.Control
                      value={editItem.displayInterface || ""}
                      onChange={(e) =>
                        setEditItem({
                          ...editItem,
                          displayInterface: e.target.value,
                        })
                      }
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>Display Color</Form.Label>
                    <Form.Control
                      value={editItem.displayColor || ""}
                      onChange={(e) =>
                        setEditItem({
                          ...editItem,
                          displayColor: e.target.value,
                        })
                      }
                    />
                  </Form.Group>
                </>
              )}

              {/* PROTECTION */}
              {category === "Protection Component" && (
                <>
                  <Form.Group className="mb-3">
                    <Form.Label>Protection Type</Form.Label>
                    <Form.Control
                      value={editItem.protectionType || ""}
                      onChange={(e) =>
                        setEditItem({
                          ...editItem,
                          protectionType: e.target.value,
                        })
                      }
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>Protection Location</Form.Label>
                    <Form.Control
                      value={editItem.protectionLocation || ""}
                      onChange={(e) =>
                        setEditItem({
                          ...editItem,
                          protectionLocation: e.target.value,
                        })
                      }
                    />
                  </Form.Group>
                </>
              )}

              {/* OPTIONAL COMMON */}
              <Form.Group className="mb-3">
                <Form.Label>Part Number</Form.Label>
                <Form.Control
                  value={editItem.partNumber || ""}
                  onChange={(e) =>
                    setEditItem({
                      ...editItem,
                      partNumber: e.target.value,
                    })
                  }
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Manufacturer</Form.Label>
                <Form.Control
                  value={editItem.manufacturer || ""}
                  onChange={(e) =>
                    setEditItem({
                      ...editItem,
                      manufacturer: e.target.value,
                    })
                  }
                />
              </Form.Group>
            </>
          )}
        </Form>
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={onSave}>
          Save
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default EditItemModal;
