import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Outlet } from "react-router-dom";
import Navbar from "../components/admin/Navbar";
import AppSidebar from "../components/admin/AppSidebar";
import { mockAdmin } from "../data/Mockdata";

const AdminDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  return (
    <div
      className="d-flex position-relative"
      style={{
        minHeight: "100vh",
        backgroundColor: "hsl(215, 30%, 10%)",
        color: "hsl(210, 40%, 98%)",
      }}
    >
      {/* Sidebar */}
      <AppSidebar isOpen={sidebarOpen} onClose={toggleSidebar} />

      {/* Main Content Area */}
      <div
        className="d-flex flex-column flex-grow-1 position-relative"
        style={{
          transition: "all 0.3s ease",
          marginLeft: sidebarOpen ? "240px" : "70px",
          zIndex: 1,
        }}
      >
        {/* Navbar */}
        <div
          className="position-sticky top-0"
          style={{
            zIndex: 1055,
          }}
        >
          <Navbar
            admin={mockAdmin}
            onMenuClick={toggleSidebar}
            sidebarOpen={sidebarOpen}
          />
        </div>

        {/* Page Content */}
        <main
          className="flex-grow-1 p-4"
          style={{
            overflowY: "auto",
            backgroundColor: "hsl(215, 25%, 14%)",
            color: "hsl(210, 40%, 98%)",
            minHeight: "calc(100vh - 70px)",
          }}
        >
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default AdminDashboard;
