// components/admin/racks/SlotDetailModal.jsx
// ------------------------------------------------------
// Slot Detail + Add Item Modal (combined logic)
// Works with new items[] structure
// Shows all items + their taken history
// Add new item to the slot
// ------------------------------------------------------

import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const SlotDetailModal = ({
  show,
  onClose,
  slot,
  containerId,
  onAddItem,
  onDeleteSlot,
}) => {
  const [showAddForm, setShowAddForm] = useState(false);

  const [newItem, setNewItem] = useState({
    name: "",
    quantity: 1,
    isReturnable: true,
    takenBy: "",
    takenQty: 0,
    takenDate: "",
    returnDate: "",
  });

  useEffect(() => {
    if (!show) {
      setShowAddForm(false);
      setNewItem({
        name: "",
        quantity: 1,
        isReturnable: true,
        takenBy: "",
        takenQty: 0,
        takenDate: "",
        returnDate: "",
      });
    }
  }, [show]);

  if (!slot) return null;

  const items = slot.items || [];

  // Calculate remaining quantity for an item
  const remainingQty = (item) => {
    const used =
      item.taken?.reduce((sum, t) => sum + Number(t.qty), 0) || 0;
    return item.quantity - used;
  };

  const submitAddItem = () => {
    if (!newItem.name.trim()) return;

    const itemObj = {
      id: Date.now().toString(),
      name: newItem.name,
      quantity: Number(newItem.quantity),
      isReturnable: newItem.isReturnable,
      taken: [],
    };

    // If user takes some quantity while adding
    if (newItem.takenBy && newItem.takenQty > 0) {
      itemObj.taken.push({
        user: newItem.takenBy,
        qty: Number(newItem.takenQty),
        date: newItem.takenDate,
        returnDate: newItem.returnDate,
      });
    }

    onAddItem(containerId, slot.slotNumber, itemObj);

    setShowAddForm(false);
    onClose();
  };

  return (
    <Modal show={show} onHide={onClose} centered size="lg">
      <Modal.Header closeButton>
        <Modal.Title>
          Slot {slot.slotNumber} — Details
        </Modal.Title>
      </Modal.Header>

      <Modal.Body
        style={{
          backgroundColor: "hsl(215, 25%, 12%)",
          color: "white",
        }}
      >
        {/* If slot has no items */}
        {items.length === 0 && !showAddForm && (
          <p className="text-muted">This slot is empty.</p>
        )}

        {/* SHOW ITEMS */}
        {!showAddForm &&
          items.map((item, index) => (
            <div
              key={index}
              className="p-3 mb-3 rounded"
              style={{
                backgroundColor: "hsl(215, 25%, 16%)",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            >
              <div className="d-flex justify-content-between">
                <div>
                  <strong>{item.name}</strong>
                  <div className="small text-muted">
                    Quantity: {item.quantity}
                  </div>
                </div>

                <div className="text-end small">
                  {item.isReturnable ? (
                    <span className="text-info">Returnable</span>
                  ) : (
                    <span className="text-warning">Non-returnable</span>
                  )}
                  <div>
                    Remaining: {remainingQty(item)}
                  </div>
                </div>
              </div>

              {/* TAKEN HISTORY */}
              {item.taken?.length > 0 && (
                <div className="mt-3 small">
                  <strong>Taken history:</strong>
                  {item.taken.map((t, i) => (
                    <div key={i} className="text-muted">
                      • {t.user} — Qty {t.qty} — {t.date}{" "}
                      {t.returnDate
                        ? `→ Return: ${t.returnDate}`
                        : "(Not returned)"}
                    </div>
                  ))}
                </div>
              )}
            </div>
          ))}

        {/* ADD NEW ITEM FORM */}
        {showAddForm && (
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Item Name</Form.Label>
              <Form.Control
                value={newItem.name}
                onChange={(e) =>
                  setNewItem({ ...newItem, name: e.target.value })
                }
                placeholder="Enter item name"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Quantity</Form.Label>
              <Form.Control
                type="number"
                min="1"
                value={newItem.quantity}
                onChange={(e) =>
                  setNewItem({
                    ...newItem,
                    quantity: Number(e.target.value),
                  })
                }
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Check
                type="switch"
                label={
                  newItem.isReturnable
                    ? "Returnable"
                    : "Non-returnable"
                }
                checked={newItem.isReturnable}
                onChange={(e) =>
                  setNewItem({
                    ...newItem,
                    isReturnable: e.target.checked,
                  })
                }
              />
            </Form.Group>

            {/* TAKEN OPTIONS (optional) */}
            <hr />
            <div className="small text-muted mb-2">
              Optional — mark quantity as taken:
            </div>

            <Form.Group className="mb-3">
              <Form.Label>Taken By</Form.Label>
              <Form.Control
                value={newItem.takenBy}
                onChange={(e) =>
                  setNewItem({
                    ...newItem,
                    takenBy: e.target.value,
                  })
                }
                placeholder="Enter user name"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Taken Quantity</Form.Label>
              <Form.Control
                type="number"
                min="0"
                value={newItem.takenQty}
                onChange={(e) =>
                  setNewItem({
                    ...newItem,
                    takenQty: Number(e.target.value),
                  })
                }
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Taken Date</Form.Label>
              <Form.Control
                type="date"
                value={newItem.takenDate}
                onChange={(e) =>
                  setNewItem({
                    ...newItem,
                    takenDate: e.target.value,
                  })
                }
              />
            </Form.Group>

            {newItem.isReturnable && (
              <Form.Group className="mb-3">
                <Form.Label>Return Date</Form.Label>
                <Form.Control
                  type="date"
                  value={newItem.returnDate}
                  onChange={(e) =>
                    setNewItem({
                      ...newItem,
                      returnDate: e.target.value,
                    })
                  }
                />
              </Form.Group>
            )}
          </Form>
        )}
      </Modal.Body>

      <Modal.Footer>
        {!showAddForm && (
          <>
            

            <Button
              variant="primary"
              onClick={() => setShowAddForm(true)}
            >
              + Add Item
            </Button>
          </>
        )}

        {showAddForm && (
          <>
            <Button variant="secondary" onClick={() => setShowAddForm(false)}>
              Back
            </Button>

            <Button variant="primary" onClick={submitAddItem}>
              Save Item
            </Button>
          </>
        )}
      </Modal.Footer>
    </Modal>
  );
};

export default SlotDetailModal;
