import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import SlotCard from "./SlotCard";
import { Modal, Button, Form } from "react-bootstrap";
import { Trash2 } from "lucide-react";

const ContainerGrid = ({
  container,
  activeFilter = "All",
  onAddSlot,
  onDeleteRack,
  onDeleteSlot,       // <-- IMPORTANT
  onExternalSlotClick
}) => {
  
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);

  const [newItem, setNewItem] = useState({
    name: "",
    quantity: "",
    isReturnable: true,
    returnDate: "",
  });

  const handleSlotClick = (slotNumber) => {
    setSelectedSlot(slotNumber);
    setIsAddModalOpen(true);
  };

  const selectedSlotData = container.slots.find(
    (s) => s.slotNumber === selectedSlot
  );

  const matchesFilter = (slot) => {
    const item = slot.items[0];

    switch (activeFilter) {
      case "Returnable":
        return item && item.isReturnable === true;
      case "Non-returnable":
        return item && item.isReturnable === false;
      case "Occupied":
        return !!item;
      case "Empty":
        return !item;
      default:
        return true;
    }
  };

  return (
    <>
      {/* Rack Card */}
      <div
        className="card shadow-sm my-3"
        style={{
          background:
            "linear-gradient(90deg, hsl(215, 25%, 12%) 0%, hsl(215, 25%, 10%) 100%)",
          color: "white",
          border: "1px solid hsl(215, 20%, 25%)",
          borderRadius: "10px",
        }}
      >
        {/* Header */}
        <div className="card-header d-flex justify-content-between">
          <h5 className="mb-0">{container.name}</h5>

          <div className="d-flex gap-2">
            <button
              className="btn btn-sm btn-outline-light"
              onClick={(e) => {
                e.stopPropagation();
                onAddSlot(container.id);
              }}
            >
              + Slot
            </button>

            {/* Delete Rack */}
            <button
              className="btn btn-sm btn-secondary"
              onClick={(e) => {
                e.stopPropagation();
                onDeleteRack(container.id);
              }}
            >
              <Trash2 size={14} />
            </button>
          </div>
        </div>

        {/* Body */}
        <div className="card-body">
          <div className="row g-3">
            {container.slots.map((slot) => {
              const slotId = `rack-${container.id}-slot-${slot.slotNumber}`;
              const visible = matchesFilter(slot);

              return (
                <div
                  key={slotId}
                  id={slotId}
                  className="col-12 col-sm-6 col-md-4 col-lg-3"
                  style={{ opacity: visible ? 1 : 0.35 }}
                  onClick={() => handleSlotClick(slot.slotNumber)}
                >
                  <SlotCard
                    slot={slot}
                    containerName={container.name}
                    onDeleteSlot={(slotNum) =>
                      onDeleteSlot(container.id, slotNum)
                    }
                  />
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {/* Add Item Modal */}
      <Modal
        show={isAddModalOpen}
        onHide={() => setIsAddModalOpen(false)}
        centered
      >
        <Modal.Header closeButton style={{ background: "#0b1622", color: "white" }}>
          <Modal.Title>Add Item</Modal.Title>
        </Modal.Header>

        <Modal.Body style={{ background: "#122035", color: "white" }}>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Item Name</Form.Label>
              <Form.Control
                type="text"
                value={newItem.name}
                onChange={(e) =>
                  setNewItem({ ...newItem, name: e.target.value })
                }
                placeholder="Enter item name"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Quantity</Form.Label>
              <Form.Control
                type="number"
                min="1"
                value={newItem.quantity}
                onChange={(e) =>
                  setNewItem({ ...newItem, quantity: e.target.value })
                }
              />
            </Form.Group>
          </Form>
        </Modal.Body>

        <Modal.Footer style={{ background: "#0b1622" }}>
          <Button variant="secondary" onClick={() => setIsAddModalOpen(false)}>
            Cancel
          </Button>

          <Button variant="primary">Add Item</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default ContainerGrid;
