import React, { useState, useEffect } from "react";
import { Modal, Button, Form, Row, Col } from "react-bootstrap";

/* ------------------ EMPTY STATE ------------------ */
const emptyState = {
  name: "",
  quantity: 1,
  itemCategory: "",
  componentType: "",
  componentTypeOther: "",
  package: "",
  packageOther: "",
  partNumber: "",
  manufacturer: "",
  value: "",
  tolerance: "",
  voltageRating: "",
  currentRating: "",
  displayType: "",
  displayTypeOther: "",
  displayInterface: "",
  displayInterfaceOther: "",
  displayColor: "",
  displayColorOther: "",
  protectionType: "",
  protectionTypeOther: "",
  protectionLocation: "",
  protectionLocationOther: "",
  moduleType: "",
  moduleTypeOther: "",
};

const AddItemModal = ({
  show,
  onClose,
  onSubmit,
  slotNumber,
  mode = "add",          // "add" | "edit"
  initialData = null,    // item from backend
}) => {
  const [data, setData] = useState(emptyState);

  /* ------------------ LOAD EDIT DATA ------------------ */
  useEffect(() => {
    if (mode === "edit" && initialData) {
      setData({
        ...emptyState,
        name: initialData.itemName || initialData.name || "",
        quantity: initialData.total || initialData.quantity || 1,

        itemCategory: initialData.itemCategory || "",
        componentType: initialData.componentType || "",
        package: initialData.package || "",
        partNumber: initialData.partNumber || "",
        manufacturer: initialData.manufacturer || "",
        value: initialData.value || "",
        tolerance: initialData.tolerance || "",
        voltageRating: initialData.voltageRating || "",
        currentRating: initialData.currentRating || "",

        displayType: initialData.displayType || "",
        displayInterface: initialData.displayInterface || "",
        displayColor: initialData.displayColor || "",

        protectionType: initialData.protectionType || "",
        protectionLocation: initialData.protectionLocation || "",

        moduleType: initialData.moduleType || "",
      });
    }

    if (mode === "add") {
      setData(emptyState);
    }
  }, [mode, initialData, show]);

  /* ------------------ OPTIONS ------------------ */
  const categories = [
    "Passive Component",
    "Discrete Component",
    "Power Component",
    "MCU / Processor",
    "Memory IC",
    "Interface / Support IC",
    "Analog IC",
    "Sensor",
    "Connector",
    "Switch / Button",
    "Module",
    "Display",
    "Protection Component",
    "Mechanical",
    "Cable / Wire",
    "Tool / Accessory",
    "Others",
  ];

  const componentTypeOptions = {
    "Passive Component": [
      "Resistor",
      "Capacitor",
      "Inductor",
      "Ferrite Bead",
      "Thermistor",
      "Varistor",
      "Crystal / Resonator",
    ],
    "Discrete Component": [
      "Diode",
      "Zener Diode",
      "LED",
      "Transistor BJT",
      "MOSFET",
      "IGBT",
    ],
  };

  /* ------------------ FLAGS ------------------ */
  const showBaseFields = data.itemCategory && data.itemCategory !== "Others";
  const isPassive = data.itemCategory === "Passive Component";
  const isDiscrete = data.itemCategory === "Discrete Component";
  const isDisplay = data.itemCategory === "Display";
  const isProtection = data.itemCategory === "Protection Component";
  const isModule = data.itemCategory === "Module";

  /* ------------------ SUBMIT ------------------ */
  const handleSubmit = () => {
    if (!data.name || !data.itemCategory || data.quantity < 1) {
      alert("Item Name, Category & Quantity required");
      return;
    }

    onSubmit({
      name: data.name,
      quantity: Number(data.quantity),
      itemCategory: data.itemCategory,
      componentType: data.componentType,
      package: data.package,
      partNumber: data.partNumber,
      manufacturer: data.manufacturer,
      value: data.value,
      tolerance: data.tolerance,
      voltageRating: data.voltageRating,
      currentRating: data.currentRating,
      displayType: data.displayType,
      displayInterface: data.displayInterface,
      displayColor: data.displayColor,
      protectionType: data.protectionType,
      protectionLocation: data.protectionLocation,
      moduleType: data.moduleType,
    });

    onClose();
  };

  /* ------------------ UI ------------------ */
  return (
    <Modal show={show} onHide={onClose} centered size="lg" scrollable>
      <Modal.Header closeButton>
        <Modal.Title>
          Slot {slotNumber} â€“ {mode === "edit" ? "Edit Item" : "Add Item"}
        </Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <Form>
          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Item Name *</Form.Label>
                <Form.Control
                  value={data.name}
                  onChange={(e) =>
                    setData({ ...data, name: e.target.value })
                  }
                />
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Item Category *</Form.Label>
                <Form.Select
                  value={data.itemCategory}
                  disabled={mode === "edit"}
                  onChange={(e) =>
                    setData({
                      ...data,
                      itemCategory: e.target.value,
                    })
                  }
                >
                  <option value="">Select</option>
                  {categories.map((c) => (
                    <option key={c} value={c}>
                      {c}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>

          <Form.Group className="mb-3">
            <Form.Label>Quantity *</Form.Label>
            <Form.Control
              type="number"
              min="1"
              value={data.quantity}
              onChange={(e) =>
                setData({ ...data, quantity: Number(e.target.value) })
              }
            />
          </Form.Group>

          {showBaseFields && (
            <>
              <hr />
              <h6>Component Details</h6>

              {(isPassive || isDiscrete) && (
                <Form.Group className="mb-3">
                  <Form.Label>Component Type</Form.Label>
                  <Form.Select
                    value={data.componentType}
                    onChange={(e) =>
                      setData({
                        ...data,
                        componentType: e.target.value,
                      })
                    }
                  >
                    <option value="">Select</option>
                    {componentTypeOptions[data.itemCategory]?.map((t) => (
                      <option key={t} value={t}>
                        {t}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              )}

              {isModule && (
                <Form.Group className="mb-3">
                  <Form.Label>Module Type</Form.Label>
                  <Form.Control
                    value={data.moduleType}
                    onChange={(e) =>
                      setData({ ...data, moduleType: e.target.value })
                    }
                  />
                </Form.Group>
              )}

              {isDisplay && (
                <>
                  <Form.Group className="mb-3">
                    <Form.Label>Display Type</Form.Label>
                    <Form.Control
                      value={data.displayType}
                      onChange={(e) =>
                        setData({ ...data, displayType: e.target.value })
                      }
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>Display Interface</Form.Label>
                    <Form.Control
                      value={data.displayInterface}
                      onChange={(e) =>
                        setData({
                          ...data,
                          displayInterface: e.target.value,
                        })
                      }
                    />
                  </Form.Group>
                </>
              )}

              {isProtection && (
                <>
                  <Form.Group className="mb-3">
                    <Form.Label>Protection Type</Form.Label>
                    <Form.Control
                      value={data.protectionType}
                      onChange={(e) =>
                        setData({
                          ...data,
                          protectionType: e.target.value,
                        })
                      }
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>Protection Location</Form.Label>
                    <Form.Control
                      value={data.protectionLocation}
                      onChange={(e) =>
                        setData({
                          ...data,
                          protectionLocation: e.target.value,
                        })
                      }
                    />
                  </Form.Group>
                </>
              )}
            </>
          )}
        </Form>
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          {mode === "edit" ? "Update Item" : "Save Item"}
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddItemModal;
