// Slot Detail + Add Item Modal + Return Item
import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import { Package, Trash2 } from "lucide-react";
import api from "../../../api/axios";
import NotificationToast from "../../common/NotificationToast";


const SlotDetailModal = ({
  show,
  onClose,
  slot,
  containerId,
  onAddItem,
  refresh,
}) => {
  const [showAddForm, setShowAddForm] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editItem, setEditItem] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [deleteItem, setDeleteItem] = useState(null);

  const [newItem, setNewItem] = useState({
    name: "",
    quantity: 1,
  });
  //////////////////edit slot name /////////////////////////////
  const [isEditingSlotName, setIsEditingSlotName] = useState(false);
  const [slotNameInput, setSlotNameInput] = useState("");
  ////////////////////////////////////////////////////////////////

  const [showReturnModal, setShowReturnModal] = useState(false);
  const [returnContext, setReturnContext] = useState(null);
  const [returnQty, setReturnQty] = useState(1);
  const [selectedBorrower, setSelectedBorrower] = useState("");
  const [toast, setToast] = useState({
    show: false,
    message: "",
    bg: "success",
  });
  useEffect(() => {
    console.log("SLOT RECEIVED (frontend):", slot);
  }, [slot]);

  useEffect(() => {
    if (!show) {
      setShowAddForm(false);
      setNewItem({ name: "", quantity: 1 });
      setShowReturnModal(false);
      setReturnContext(null);
      setReturnQty(1);
      setSelectedBorrower("");
    }
  }, [show]);
  useEffect(() => {
    console.log(" SLOT LOADED:", slot);
  }, [slot]);
  useEffect(() => {
  if (slot?.slotName) {
    setSlotNameInput(slot.slotName);
  }
}, [slot]);


  if (!slot) return null;

  const items =
    Array.isArray(slot.items) && slot.items.length > 0
      ? slot.items
      : slot.item
        ? [
          {
            id: slot.item.itemId,
            name: slot.item.itemName,
            quantity: slot.item.total,
            remaining: slot.item.remaining,
            takenHistory: slot.item.takenHistory || [],
          },
        ]
        : [];

  const remainingQty = (it) =>
    Number(
      it.remaining !== undefined && it.remaining !== null
        ? it.remaining
        : it.quantity !== undefined
          ? it.quantity
          : it.total !== undefined
            ? it.total
            : 0
    );

  const totalQty = (it) =>
    Number(
      it.quantity !== undefined
        ? it.quantity
        : it.total !== undefined
          ? it.total
          : remainingQty(it)
    );

  const submitAddItem = () => {
    if (!newItem.name.trim()) {
    setToast({
      show: true,
      message: "Please enter an item name",
      bg: "danger",
    });
    return;
  }
    if (!slot.slotId) return;

    onAddItem(containerId, slot.slotId, {
      name: newItem.name.trim(),
      quantity: Number(newItem.quantity),
    });
    refresh();
    setShowAddForm(false);
    onClose();
  };

  // ------------------------
  // OPEN RETURN MODAL
  // ------------------------
  const openReturnModal = (item) => {
    const itemHistory = item.takenHistory || [];

    if (itemHistory.length === 0) {
      setToast({
        show: true,
        message: "No borrow history found for this item.",
        bg: "warning",
      });
      return;
    }

    setReturnContext({
      item,
      historyList: itemHistory
    });

    setShowReturnModal(true);
  };
  //Item Edit----------------------------------------------------------
  const openEditModal = (item) => {
    setEditItem({
      ...item,
      name: item.name || item.itemName,
      quantity: totalQty(item),
    });
    setShowEditModal(true);
  };

  //delete item------------------------------------------------------
  const handleDeleteItem = async () => {
    if (!deleteItem) return;

    try {
      await api.delete(
        `/racks/deleteItem/${containerId}/${slot.slotId}/${deleteItem.itemId}`
      );
      console.log("DELETE:", {
        rackId: slot.rackId,
        slotId: slot.slotId,
        itemId: deleteItem.itemId,
      });


      setToast({
        show: true,
        message: "Item deleted successfully",
        bg: "success",
      });

      setShowDeleteModal(false);
      setDeleteItem(null);

      setShowEditModal(false);
      setEditItem(null);

      refresh(); // reload racks from backend
      onClose();

    } catch (err) {
      console.error("Delete item error:", err);
      setToast({
        show: true,
        message: "Failed to delete item",
        bg: "danger",
      });
    }
  };


  const openDeleteModal = (item) => {
    setDeleteItem(item);
    setShowDeleteModal(true);
  };
  const handleEditSave = async () => {
    if (!editItem) return;

    try {
      await api.put(
        `/racks/updateItem/${containerId}/${slot.slotId}/${editItem.itemId}`,
        {
          itemName: editItem.name,
          total: editItem.quantity,
        }
      );
      // console.log("EDIT:", {
      //   rackId: slot.rackId,
      //   slotId: slot.slotId,
      //   itemId: editItem.itemId,
      // });
    

      setShowDeleteModal(false);
      setDeleteItem(null);
      setShowEditModal(false);
      setEditItem(null);
      refresh();
      onClose();

      setToast({
        show: true,
        message: "Item updated successfully",
        bg: "success",
      });

      
      

    } catch (err) {
      console.error("Edit item error:", err);
      setToast({
        show: true,
        message: err.response?.data?.message || "Update failed",
        bg: "danger",
      });
    }
  };

  ///edit slot name save///
  // ------------------------
  // CONFIRM RETURN
  // ------------------------
  const confirmReturn = async () => {
    if (!returnContext) return;

    const borrower = returnContext.historyList.find(
      (h) => h.borrowId === selectedBorrower
    );

    if (!borrower) {
      setToast({
        show: true,
        message: "Select a borrower first.",
        bg: "warning",
      });
      return;
    }

    const qty = Number(returnQty);

    if (qty < 1 || qty > borrower.quantity) {
      setToast({
        show: true,
        message: `Enter valid quantity (1 .. ${borrower.quantity})`,
        bg: "danger",
      });
      return;
    }
    const payload = {
      rackId: slot.rackId,
      rackName: slot.rackName,
      slotNumber: slot.slotNumber,
      returnQuantity: qty
    };
    console.log("SENDING RETURN PAYLOAD:", payload);

    try {
      await api.post(`/manager/returnItem/${borrower.borrowId}`, {
        rackId: slot.rackId,
        rackName: slot.rackName,
        slotNumber: slot.slotNumber,
        returnQuantity: qty,
      });


      setToast({
        show: true,
        message: "Item returned successfully!",
        bg: "success",
      });
      refresh();
      setShowReturnModal(false);
      setReturnContext(null);
      setReturnQty(1);
      setSelectedBorrower("");  //
      onClose();
    } catch (err) {
      console.error("Return failed:", err);
      setToast({
        show: true,
        message: "Failed to return item.",
        bg: "danger",
      });
    }

  };


  return (
    <>
      <NotificationToast
        show={toast.show}
        message={toast.message}
        bg={toast.bg}
        onClose={() => setToast((prev) => ({ ...prev, show: false }))}
      />
      {/* MAIN MODAL */}
      <Modal show={show} onHide={onClose} centered size="lg">
        <Modal.Header closeButton>
          <Modal.Title className="d-flex align-items-center gap-2">
  {!isEditingSlotName ? (
    <>
      <span>{slot.slotName || `Slot ${slot.slotNumber}`}</span>

      <Button
        size="sm"
        variant="outline-secondary"
        onClick={() => setIsEditingSlotName(true)}
      >
        ✏️
      </Button>
    </>
  ) : (
    <>
      <Form.Control
        size="sm"
        value={slotNameInput}
        onChange={(e) => setSlotNameInput(e.target.value)}
        style={{ maxWidth: "200px" }}
      />

      <Button
        size="sm"
        variant="success"
        onClick={handleSaveSlotName}
      >
        Save
      </Button>

      <Button
        size="sm"
        variant="secondary"
        onClick={() => {
          setIsEditingSlotName(false);
          setSlotNameInput(slot.slotName);
        }}
      >
        Cancel
      </Button>
    </>
  )}
</Modal.Title>

        </Modal.Header>

        <Modal.Body
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            color: "white",
          }}
        >
          {items.length === 0 && !showAddForm && (
            <p className="text-muted">This slot is empty.</p>
          )}

          {!showAddForm &&
            items.map((it) => (
              <div
                key={it.id || it.itemId}
                className="p-3 mb-3 rounded"
                style={{
                  backgroundColor: "hsl(215, 25%, 16%)",
                  border: "1px solid hsl(215, 20%, 25%)",
                }}
              >
                <div className="d-flex justify-content-between">
                  <div>
                    <strong>{it.name || it.itemName}</strong>
                    <div className="small text-muted">Total: {totalQty(it)}</div>
                  </div>

                  <div className="text-end small">
                    <div>Remaining: {remainingQty(it)}</div>
                  </div>
                </div>

                {it.takenHistory?.length > 0 && (
                  <div className="mt-3 small">
                    <strong>Taken History:</strong>

                    {[...it.takenHistory]
                      .slice(-10)
                      .reverse()
                      .map((t, idx) => (
                        <div key={idx} className="text-muted">
                          • {t.userName} — Qty {t.quantity} —{" "}
                          {new Date(t.takenDate).toLocaleDateString("en-GB")}{" "}
                          {t.returnDate
                            ? `→ Return: ${new Date(
                              t.returnDate
                            ).toLocaleDateString("en-GB")}`
                            : "(Not returned)"}
                        </div>
                      ))}
                  </div>
                )}

                <div className="d-flex justify-content-end mt-2 gap-2">
                  <Button
                    size="sm"
                    variant="outline-primary"
                    onClick={() => openEditModal(it)}
                  >
                    Edit
                  </Button>

                  <Button
                    size="sm"
                    variant="outline-danger"
                    onClick={() => openDeleteModal(it)}
                  >
                    <Trash2 size={16} />
                  </Button>

                  {remainingQty(it) < totalQty(it) && (
                    <Button
                      size="sm"
                      variant="warning"
                      onClick={() => openReturnModal(it)}
                    >
                      Return
                    </Button>
                  )}
                </div>

              </div>
            ))}

          {showAddForm && (
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Item Name</Form.Label>
                <Form.Control
                  value={newItem.name}
                  onChange={(e) =>
                    setNewItem({ ...newItem, name: e.target.value })
                  }
                  placeholder="Enter item name"
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Quantity</Form.Label>
                <Form.Control
                  type="number"
                  min="1"
                  value={newItem.quantity}
                  onChange={(e) =>
                    setNewItem({
                      ...newItem,
                      quantity: Number(e.target.value),
                    })
                  }
                />
              </Form.Group>
            </Form>
          )}
        </Modal.Body>

        <Modal.Footer>
          {!showAddForm && (
            <Button variant="primary" onClick={() => setShowAddForm(true)}>
              + Add Item
            </Button>
          )}

          {showAddForm && (
            <>
              <Button type="button"   variant="secondary" onClick={() => setShowAddForm(false)}>
                Back
              </Button>
              <Button  type="button"  variant="primary" onClick={submitAddItem}>
                Save Item
              </Button>
            </>
          )}

          {/* <Button variant="secondary" onClick={onClose}>
            Close
          </Button> */}
        </Modal.Footer>
      </Modal>

      {/* RETURN MODAL */}
      <Modal
        show={showReturnModal}
        onHide={() => setShowReturnModal(false)}
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>Return Item</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          {returnContext && (
            <>
              <p>
                <strong>Item:</strong>{" "}
                {returnContext.item.itemName || returnContext.item.name}
              </p>

              {/* DROPDOWN LIST OF BORROWERS */}
              <Form.Group className="mb-3">
                <Form.Label>Select Borrower</Form.Label>
                <Form.Control
                  as="select"
                  value={selectedBorrower}
                  onChange={(e) => {
                    setSelectedBorrower(e.target.value);

                    const userRec = returnContext.historyList.find(
                      (r) => r.borrowId === e.target.value
                    );
                    if (userRec) setReturnQty(userRec.quantity);
                  }}
                >
                  <option value="">-- Select User --</option>
                  {returnContext.historyList.map((h, idx) => (
                    <option key={idx} value={h.borrowId}>
                      {h.userName} (Borrowed: {h.quantity})
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Quantity to Return</Form.Label>
                <Form.Control
                  type="number"
                  min="1"
                  value={returnQty}
                  onChange={(e) => setReturnQty(Number(e.target.value))}
                />
              </Form.Group>
            </>
          )}
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowReturnModal(false)}>
            Cancel
          </Button>
          <Button variant="success" onClick={confirmReturn}>
            Confirm Return
          </Button>
        </Modal.Footer>
      </Modal>
      {/* EDIT ITEM MODAL */}
      <Modal
        show={showEditModal}
        onHide={() => setShowEditModal(false)}
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>Edit Item</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          {editItem && (
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Item Name</Form.Label>
                <Form.Control
                  value={editItem.name}
                  onChange={(e) =>
                    setEditItem({ ...editItem, name: e.target.value })
                  }
                />
              </Form.Group>

              <Form.Group>
                <Form.Label>Total Quantity</Form.Label>
                <Form.Control
                  type="number"
                  min="1"
                  value={editItem.quantity}
                  onChange={(e) =>
                    setEditItem({
                      ...editItem,
                      quantity: Number(e.target.value),
                    })
                  }
                />
              </Form.Group>
            </Form>
          )}
        </Modal.Body>

        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => setShowEditModal(false)}
          >
            Cancel
          </Button>

          <Button type="button" variant="primary" onClick={handleEditSave}>
            Save
          </Button>
        </Modal.Footer>
      </Modal>
      {/* DELETE CONFIRMATION MODAL */}
      <Modal
        show={showDeleteModal}
        onHide={() => setShowDeleteModal(false)}
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>Confirm Delete</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p>
            Are you sure you want to delete{" "}
            <strong>
              {deleteItem?.name || deleteItem?.itemName}
            </strong>
            ?
          </p>
          {/* <p className="text-danger small">
            This action cannot be undone.
          </p> */}
        </Modal.Body>

        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => setShowDeleteModal(false)}
          >
            Cancel
          </Button>

          <Button variant="danger" onClick={handleDeleteItem}>
            Yes, Delete
          </Button>
        </Modal.Footer>
      </Modal>


    </>
  );
};

export default SlotDetailModal;
