import React from "react";
import {
  mockAdmin,
  mockManager,
  mockUsers,
} from "../../data/Mockdata";

const ManagerUsers = () => {
  // Admin user
  const adminUser = {
    id: "admin",
    name: mockAdmin.name,
    email: mockAdmin.email,
    phone: "Not Provided",
    role: "Admin",
  };

  // Manager user
  const managerUser = {
    id: "manager",
    name: mockManager.name,
    email: mockManager.email,
    phone: "Not Provided",
    role: "Manager",
  };

  // Normal registered users (pending users)
  const registeredUsers = mockPendingUsers.map((u) => ({
    id: u.id,
    name: u.name,
    email: u.email,
    phone: "Not Provided",
    role: "User",
  }));

  // Approved request users (also users)
  const approvedUsers = mockPermissionRequests
    .filter((req) => req.status === "Approved")
    .map((r) => ({
      id: r.id,
      name: r.userName,
      email: r.userEmail,
      phone: "Not Provided",
      role: "User",
    }));

  // Combine all
  const allUsers = [adminUser, managerUser, ...registeredUsers, ...approvedUsers];

  return (
    <div className="container my-4">
      <h2 className="fw-bold mb-3">All Users</h2>

      <div
        className="card"
        style={{
          background: "hsl(215,25%,12%)",
          border: "1px solid hsl(215,20%,25%)",
          color: "white",
        }}
      >
        <div className="card-header fw-bold">User List</div>

        <table className="table table-dark table-striped mb-0" style={{ backgroundColor: "#191f24" }}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Role</th>
              <th>Email</th>
              <th>Phone</th>
            </tr>
          </thead>

          <tbody>
            {allUsers.map((u) => (
              <tr key={u.id}>
                <td>{u.name}</td>
                <td>{u.role}</td>
                <td>{u.email}</td>
                <td>{u.phone}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManagerUsers;
