import React from "react";
import { Nav } from "react-bootstrap";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const loc = useLocation(); // Get current location

  return (
    <div className="sidebar bg-dark text-white d-flex flex-column p-3">
      <div className="mb-4 text-center">
        <img src="/logo1.png" alt="logo" className="side-logo mb-2" />
        <div className="h6">Gandeevan</div>
      </div>

      <Nav className="flex-column">
        <Nav.Link
          as={Link}
          to="/admin/home"
          active={loc.pathname === "/admin/home"}
          className="text-white"
        >
          Dashboard
        </Nav.Link>
        <Nav.Link
          as={Link}
          to="/admin/containers"
          active={loc.pathname.startsWith("/admin/containers")}
          className="text-white"
        >
          Containers
        </Nav.Link>
        <Nav.Link
          as={Link}
          to="/admin/users"
          active={loc.pathname === "/admin/users"}
          className="text-white"
        >
          User Requests
        </Nav.Link>
        <Nav.Link
          as={Link}
          to="/admin/history"
          active={loc.pathname === "/admin/history"}
          className="text-white"
        >
          History
        </Nav.Link>
      </Nav>

      <div className="mt-auto small text-muted">Â© Gandeevan</div>
    </div>
  );
};

export default Sidebar;
