import React from "react";
import { Card, Button, Row, Col, Badge } from "react-bootstrap";

const ContainerData = [
  { name: "Container A", slots: "12", occoccupied: "8", empty: "4", },
  { name: "Container B", slots: "12", occupied: "11", empty: "1" },
  { name: "Container C", slots: "12", occupied: "7", empty: "5" },
];

const ContainerSlot = () => {
    return (
    <Card className="bg-dark text-white border-0 shadow-sm p-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h4 className="fw-bold">Container and Slot Overview</h4>
        <Badge bg="secondary" pill>
          {ContainerData.length} Containers
        </Badge>
      </div>    
        {ContainerData.map((container, index) => (      
            <Card key={index} className="bg-secondary border-0 mb-3 text-white p-3 rounded-4">          
                <Row className="align-items-center">
                    <Col md={3}>
                        <h5 className="fw-semibold">{container.name}</h5>
                    </Col>
                    <Col md={3}>            
                        <div className="text-light small">
                            Total Slots: {container.slots}
                        </div>
                    </Col>  
                    <Col md={3}>
                        <div className="text-light small">
                            Occupied Slots: {container.occupied}
                        </div>
                    </Col>  
                    <Col md={3}>
                        <div className="text-light small">  
                            Empty Slots: {container.empty}
                        </div>
                    </Col>
                </Row>
            </Card>
        ))}      
    </Card>
  );
}

export default ContainerSlot;
