// Enhanced modal for adding inventory items with category-based dynamic fields
// Simple version - only item details, no returnable/taken history

import React, { useState } from "react";
import { Modal, Button, Form, Row, Col } from "react-bootstrap";

const AddItemModal = ({  show, onClose,onSubmit,slotNumber, mode = "add",initialData = null}) => {
 const [data, setData] = useState(
  mode === "edit" && initialData
    ? {
        ...emptyState,
        ...initialData,
        name: initialData.itemName || initialData.name,
        quantity: initialData.total || initialData.quantity,
      }
    : emptyState
);


  // Category options
  const categories = [
    "Passive Component",
    "Discrete Component",
    "Power Component",
    "MCU / Processor",
    "Memory IC",
    "Interface / Support IC",
    "Analog IC",
    "Sensor",
    "Connector",
    "Switch / Button",
    "Module",
    "Display",
    "Protection Component",
    "Mechanical",
    "Cable / Wire",
    "Tool / Accessory",
    "Others"
  ];

  // Component types based on category
  const componentTypeOptions = {
    "Passive Component": [
      "Resistor", "Capacitor", "Inductor", "Ferrite Bead", 
      "Thermistor", "Varistor", "Crystal / Resonator"
    ],
    "Discrete Component": [
      "Diode", "Zener Diode", "LED", "Transistor BJT", 
      "MOSFET", "IGBT"
    ]
  };

  // Module types
  const moduleTypes = [
    "Wireless Module", "Power Module", "Sensor Module", 
    "Communication Module", "Display Module"
  ];

  // Package options
  const packageOptions = [
    "0201", "0402", "0603", "0805", "1206", 
    "SOT-23", "SOIC-8", "QFN-32", "QFN-48", 
    "LQFP-64", "LQFP-100", "BGA", "WLCSP"
  ];

  // Display options
  const displayTypes = [
    "LCD", "OLED", "TFT LCD", "E-Ink", "LED Matrix", "7-Segment"
  ];
  
  const displayInterfaces = [
    "SPI", "I2C", "Parallel", "LVDS", "MIPI-DSI"
  ];
  
  const displayColors = [
    "Monochrome", "RGB", "RGB565", "RGB888"
  ];

  // Protection options
  const protectionTypes = [
    "TVS Diode", "ESD Diode", "Polyfuse (PTC)", "Fuse", "MOV"
  ];
  
  const protectionLocations = [
    "Power Line", "Signal Line", "USB", "Ethernet", "CAN", "RS485"
  ]; 

  const emptyState = {
  name: "",
  quantity: 1,
  itemCategory: "",
  componentType: "",
  componentTypeOther: "",
  package: "",
  packageOther: "",
  partNumber: "",
  manufacturer: "",
  value: "",
  tolerance: "",
  voltageRating: "",
  currentRating: "",
  displayType: "",
  displayTypeOther: "",
  displayInterface: "",
  displayInterfaceOther: "",
  displayColor: "",
  displayColorOther: "",
  protectionType: "",
  protectionTypeOther: "",
  protectionLocation: "",
  protectionLocationOther: "",
  moduleType: "",
  moduleTypeOther: "",
};


  // Reset form
  const resetForm = () => {
    setData({
      name: "",
      quantity: 1,
      itemCategory: "",
      componentType: "",
      componentTypeOther: "",
      package: "",
      packageOther: "",
      partNumber: "",
      manufacturer: "",
      value: "",
      tolerance: "",
      voltageRating: "",
      currentRating: "",
      displayType: "",
      displayTypeOther: "",
      displayInterface: "",
      displayInterfaceOther: "",
      displayColor: "",
      displayColorOther: "",
      protectionType: "",
      protectionTypeOther: "",
      protectionLocation: "",
      protectionLocationOther: "",
      moduleType: "",
      moduleTypeOther: "",
    });
  };

  // Validation and submit
  const handleSubmit = () => {
    // Mandatory fields check
    if (!data.name.trim() || !data.itemCategory || data.quantity < 1) {
      alert("Please fill in Item Name, Category, and Quantity");
      return;
    }

    // For non-Others categories, check category-specific required fields
    if (data.itemCategory !== "Others") {
      const needsComponentType = ["Passive Component", "Discrete Component"].includes(data.itemCategory);
      const needsModuleType = data.itemCategory === "Module";
      const needsDisplay = data.itemCategory === "Display";
      const needsProtection = data.itemCategory === "Protection Component";

      if (needsComponentType && !data.componentType) {
        alert("Please select Component Type");
        return;
      }
      if (needsModuleType && !data.moduleType) {
        alert("Please select Module Type");
        return;
      }
      if (needsDisplay && (!data.displayType || !data.displayInterface || !data.displayColor)) {
        alert("Please fill all Display fields");
        return;
      }
      if (needsProtection && (!data.protectionType || !data.protectionLocation)) {
        alert("Please fill all Protection fields");
        return;
      }
    
    }

    // Build item object
    const newItem = {
      name: data.name,
      quantity: Number(data.quantity),
      
      // Inventory details
      itemCategory: data.itemCategory,
      componentType: data.componentType === "Other" ? data.componentTypeOther : data.componentType,
      package: data.package === "Other" ? data.packageOther : data.package,
      partNumber: data.partNumber,
      manufacturer: data.manufacturer,
      value: data.value,
      tolerance: data.tolerance,
      voltageRating: data.voltageRating,
      currentRating: data.currentRating,
      
      // Display
      displayType: data.displayType === "Other" ? data.displayTypeOther : data.displayType,
      displayInterface: data.displayInterface === "Other" ? data.displayInterfaceOther : data.displayInterface,
      displayColor: data.displayColor === "Other" ? data.displayColorOther : data.displayColor,
      
      // Protection
      protectionType: data.protectionType === "Other" ? data.protectionTypeOther : data.protectionType,
      protectionLocation: data.protectionLocation === "Other" ? data.protectionLocationOther : data.protectionLocation,
      
      // Module
      moduleType: data.moduleType === "Other" ? data.moduleTypeOther : data.moduleType,
    };

    onSubmit(newItem);
    resetForm();
    onClose();
  };

  // Check if category needs base electronic fields
  const showBaseFields = data.itemCategory && data.itemCategory !== "Others";
  
  // Check specific categories
  const isPassiveOrDiscrete = ["Passive Component", "Discrete Component"].includes(data.itemCategory);
  const isPassive = data.itemCategory === "Passive Component";
  const isDiscrete = data.itemCategory === "Discrete Component";
  const isModule = data.itemCategory === "Module";
  const isDisplay = data.itemCategory === "Display";
  const isProtection = data.itemCategory === "Protection Component";

  return (
    <Modal show={show} onHide={onClose} centered size="lg" scrollable>
      <Modal.Header closeButton>
        <Modal.Title>Slot {slotNumber} - Add Item</Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <Form>
          <Row>
            {/* Item Name - MANDATORY */}
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Item Name <span className="text-danger">*</span></Form.Label>
                <Form.Control
                  value={data.name}
                  onChange={(e) => setData({ ...data, name: e.target.value })}
                  placeholder="Enter item name"
                />
              </Form.Group>
            </Col>

            {/* Item Category - MANDATORY */}
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Item Category <span className="text-danger">*</span></Form.Label>
                <Form.Select
                  value={data.itemCategory}
                  onChange={(e) => setData({ 
                    ...data, 
                    itemCategory: e.target.value,
                    componentType: "",
                    moduleType: "",
                  })}
                >
                  <option value="">Select category</option>
                  {categories.map((cat) => (
                    <option key={cat} value={cat}>{cat}</option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>

          {/* Quantity - MANDATORY */}
          <Form.Group className="mb-3">
            <Form.Label>Quantity <span className="text-danger">*</span></Form.Label>
            <Form.Control
              type="number"
              min="1"
              value={data.quantity}
              onChange={(e) => setData({ ...data, quantity: Number(e.target.value) })}
            />
          </Form.Group>

          {/* DYNAMIC FIELDS BASED ON CATEGORY */}
          {showBaseFields && (
            <>
              <hr className="my-4" />
              <h6 className="mb-3">Component Details</h6>

              <Row>
                {/* Component Type - for Passive/Discrete */}
                {isPassiveOrDiscrete && (
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Component Type <span className="text-danger">*</span></Form.Label>
                      <Form.Select
                        value={data.componentType}
                        onChange={(e) => setData({ ...data, componentType: e.target.value })}
                      >
                        <option value="">Select type</option>
                        {componentTypeOptions[data.itemCategory].map((type) => (
                          <option key={type} value={type}>{type}</option>
                        ))}
                        <option value="Other">Other</option>
                      </Form.Select>
                    </Form.Group>
                    {data.componentType === "Other" && (
                      <Form.Group className="mb-3">
                        <Form.Control
                          value={data.componentTypeOther}
                          onChange={(e) => setData({ ...data, componentTypeOther: e.target.value })}
                          placeholder="Enter custom component type (optional)"
                        />
                      </Form.Group>
                    )}
                  </Col>
                )}

                {/* Module Type - for Module category */}
                {isModule && (
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Module Type <span className="text-danger">*</span></Form.Label>
                      <Form.Select
                        value={data.moduleType}
                        onChange={(e) => setData({ ...data, moduleType: e.target.value })}
                      >
                        <option value="">Select module type</option>
                        {moduleTypes.map((type) => (
                          <option key={type} value={type}>{type}</option>
                        ))}
                        <option value="Other">Other</option>
                      </Form.Select>
                    </Form.Group>
                    {data.moduleType === "Other" && (
                      <Form.Group className="mb-3">
                        <Form.Control
                          value={data.moduleTypeOther}
                          onChange={(e) => setData({ ...data, moduleTypeOther: e.target.value })}
                          placeholder="Enter custom module type (optional)"
                        />
                      </Form.Group>
                    )}
                  </Col>
                )}

                {/* Package - for all except Display and Protection */}
                {!isDisplay && !isProtection && (
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Package <span className="text-danger">*</span></Form.Label>
                      <Form.Select
                        value={data.package}
                        onChange={(e) => setData({ ...data, package: e.target.value })}
                      >
                        <option value="">Select package</option>
                        {packageOptions.map((pkg) => (
                          <option key={pkg} value={pkg}>{pkg}</option>
                        ))}
                        <option value="Other">Other</option>
                      </Form.Select>
                    </Form.Group>
                    {data.package === "Other" && (
                      <Form.Group className="mb-3">
                        <Form.Control
                          value={data.packageOther}
                          onChange={(e) => setData({ ...data, packageOther: e.target.value })}
                          placeholder="Enter custom package (optional)"
                        />
                      </Form.Group>
                    )}
                  </Col>
                )}
              </Row>

              {/* Display-specific fields */}
              {isDisplay && (
                <Row>
                  <Col md={4}>
                    <Form.Group className="mb-3">
                      <Form.Label>Display Type <span className="text-danger">*</span></Form.Label>
                      <Form.Select
                        value={data.displayType}
                        onChange={(e) => setData({ ...data, displayType: e.target.value })}
                      >
                        <option value="">Select type</option>
                        {displayTypes.map((type) => (
                          <option key={type} value={type}>{type}</option>
                        ))}
                        <option value="Other">Other</option>
                      </Form.Select>
                    </Form.Group>
                    {data.displayType === "Other" && (
                      <Form.Group className="mb-3">
                        <Form.Control
                          value={data.displayTypeOther}
                          onChange={(e) => setData({ ...data, displayTypeOther: e.target.value })}
                          placeholder="Custom type (optional)"
                        />
                      </Form.Group>
                    )}
                  </Col>

                  <Col md={4}>
                    <Form.Group className="mb-3">
                      <Form.Label>Display Interface <span className="text-danger">*</span></Form.Label>
                      <Form.Select
                        value={data.displayInterface}
                        onChange={(e) => setData({ ...data, displayInterface: e.target.value })}
                      >
                        <option value="">Select interface</option>
                        {displayInterfaces.map((intf) => (
                          <option key={intf} value={intf}>{intf}</option>
                        ))}
                        <option value="Other">Other</option>
                      </Form.Select>
                    </Form.Group>
                    {data.displayInterface === "Other" && (
                      <Form.Group className="mb-3">
                        <Form.Control
                          value={data.displayInterfaceOther}
                          onChange={(e) => setData({ ...data, displayInterfaceOther: e.target.value })}
                          placeholder="Custom interface (optional)"
                        />
                      </Form.Group>
                    )}
                  </Col>

                  <Col md={4}>
                    <Form.Group className="mb-3">
                      <Form.Label>Display Color <span className="text-danger">*</span></Form.Label>
                      <Form.Select
                        value={data.displayColor}
                        onChange={(e) => setData({ ...data, displayColor: e.target.value })}
                      >
                        <option value="">Select color</option>
                        {displayColors.map((color) => (
                          <option key={color} value={color}>{color}</option>
                        ))}
                        <option value="Other">Other</option>
                      </Form.Select>
                    </Form.Group>
                    {data.displayColor === "Other" && (
                      <Form.Group className="mb-3">
                        <Form.Control
                          value={data.displayColorOther}
                          onChange={(e) => setData({ ...data, displayColorOther: e.target.value })}
                          placeholder="Custom color (optional)"
                        />
                      </Form.Group>
                    )}
                  </Col>
                </Row>
              )}

              {/* Protection-specific fields */}
              {isProtection && (
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Protection Type <span className="text-danger">*</span></Form.Label>
                      <Form.Select
                        value={data.protectionType}
                        onChange={(e) => setData({ ...data, protectionType: e.target.value })}
                      >
                        <option value="">Select type</option>
                        {protectionTypes.map((type) => (
                          <option key={type} value={type}>{type}</option>
                        ))}
                        <option value="Other">Other</option>
                      </Form.Select>
                    </Form.Group>
                    {data.protectionType === "Other" && (
                      <Form.Group className="mb-3">
                        <Form.Control
                          value={data.protectionTypeOther}
                          onChange={(e) => setData({ ...data, protectionTypeOther: e.target.value })}
                          placeholder="Custom type (optional)"
                        />
                      </Form.Group>
                    )}
                  </Col>

                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Protection Location <span className="text-danger">*</span></Form.Label>
                      <Form.Select
                        value={data.protectionLocation}
                        onChange={(e) => setData({ ...data, protectionLocation: e.target.value })}
                      >
                        <option value="">Select location</option>
                        {protectionLocations.map((loc) => (
                          <option key={loc} value={loc}>{loc}</option>
                        ))}
                        <option value="Other">Other</option>
                      </Form.Select>
                    </Form.Group>
                    {data.protectionLocation === "Other" && (
                      <Form.Group className="mb-3">
                        <Form.Control
                          value={data.protectionLocationOther}
                          onChange={(e) => setData({ ...data, protectionLocationOther: e.target.value })}
                          placeholder="Custom location (optional)"
                        />
                      </Form.Group>
                    )}
                  </Col>
                </Row>
              )}

              {/* Optional base fields for all electronics */}
              <Row>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Part Number</Form.Label>
                    <Form.Control
                      value={data.partNumber}
                      onChange={(e) => setData({ ...data, partNumber: e.target.value })}
                      placeholder="Optional"
                    />
                  </Form.Group>
                </Col>

                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Manufacturer</Form.Label>
                    <Form.Control
                      value={data.manufacturer}
                      onChange={(e) => setData({ ...data, manufacturer: e.target.value })}
                      placeholder="Optional"
                    />
                  </Form.Group>
                </Col>
              </Row>

              {/* Passive Component specific optional fields */}
              {isPassive && (
                <Row>
                  <Col md={4}>
                    <Form.Group className="mb-3">
                      <Form.Label>Value</Form.Label>
                      <Form.Control
                        value={data.value}
                        onChange={(e) => setData({ ...data, value: e.target.value })}
                        placeholder="e.g., 10k, 100nF"
                      />
                    </Form.Group>
                  </Col>

                  <Col md={4}>
                    <Form.Group className="mb-3">
                      <Form.Label>Tolerance</Form.Label>
                      <Form.Control
                        value={data.tolerance}
                        onChange={(e) => setData({ ...data, tolerance: e.target.value })}
                        placeholder="e.g., 5%, 1%"
                      />
                    </Form.Group>
                  </Col>

                  <Col md={4}>
                    <Form.Group className="mb-3">
                      <Form.Label>Voltage Rating</Form.Label>
                      <Form.Control
                        value={data.voltageRating}
                        onChange={(e) => setData({ ...data, voltageRating: e.target.value })}
                        placeholder="e.g., 50V"
                      />
                    </Form.Group>
                  </Col>
                </Row>
              )}

              {/* Discrete Component specific optional fields */}
              {isDiscrete && (
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Current Rating</Form.Label>
                      <Form.Control
                        value={data.currentRating}
                        onChange={(e) => setData({ ...data, currentRating: e.target.value })}
                        placeholder="e.g., 1A, 500mA"
                      />
                    </Form.Group>
                  </Col>

                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Voltage Rating</Form.Label>
                      <Form.Control
                        value={data.voltageRating}
                        onChange={(e) => setData({ ...data, voltageRating: e.target.value })}
                        placeholder="e.g., 30V"
                      />
                    </Form.Group>
                  </Col>
                </Row>
              )}
            </>
          )}
        </Form>
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>
          Back
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          Save Item
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddItemModal;
