import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Package, LogOut, Menu } from "lucide-react";

const Navbar = ({ admin, onMenuClick, sidebarOpen }) => {
  // Get initials from admin name
  const getInitials = (name) => {
    if (!name) return "A";
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase();
  };

  return (
    <header className="border-bottom bg-light py-3 px-3 px-md-4 shadow-sm">
      <div className="d-flex align-items-center justify-content-between">
        {/* Left Section */}
        <div className="d-flex align-items-center gap-3">
          {/* Mobile Menu Toggle */}
          <button
            className="btn btn-outline-secondary d-lg-none p-2"
            onClick={onMenuClick}
            title={sidebarOpen ? "Close menu" : "Open menu"}
          >
            <Menu
              size={20}
              className={`transition-transform ${sidebarOpen ? "rotate-90" : ""}`}
            />
          </button>

          {/* Logo */}
          <Package size={28} className="text-primary" />
          <div>
            <h5 className="mb-0 fw-bold">ContainerHub</h5>
            <small className="text-muted d-none d-sm-block">Admin Portal</small>
          </div>
        </div>

        {/* Right Section */}
        <div className="d-flex align-items-center gap-3">
          {/* Admin Info */}
          <div className="text-end d-none d-md-block">
            <p className="mb-0 fw-semibold">{admin?.name || "Admin User"}</p>
            <small className="text-muted">{admin?.email || "admin@system.com"}</small>
          </div>

          {/* Avatar */}
          <div
            className="rounded-circle bg-primary text-white border border-primary d-flex justify-content-center align-items-center fw-bold"
            style={{
              width: "40px",
              height: "40px",
              fontSize: "14px",
            }}
          >
            {getInitials(admin?.name)}
          </div>

          {/* Logout Button */}
          <button
            className="btn btn-outline-danger p-2 d-flex align-items-center justify-content-center"
            title="Logout"
          >
            <LogOut size={18} />
          </button>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
