import React from "react";

/**
 * UserHistory shows only history relevant to the current user.
 * Props:
 *  - containers (to derive taken history) OR separate history list
 */
const UserHistory = ({ containers = [] }) => {
  const currentUserEmail = "user@gmail.com";
  // Build rows: we look for taken entries where t.user matches current user (we earlier recorded t.user name)
  const rows = [];

  containers.forEach((rack) => {
    rack.slots.forEach((slot) => {
      (slot.items || []).forEach((item) => {
        (item.taken || []).forEach((t) => {
          // We only have t.user name; we assume it matches "User A" here
          if (t.user && (t.user === "User A" || t.user === currentUserEmail)) {
            rows.push({
              itemName: item.name,
              qty: t.qty,
              date: t.date,
              returnDate: t.returnDate || "—",
              rack: rack.name,
              slot: slot.slotNumber,
            });
          }
        });
      });
    });
  });

  return (
    <div className="container my-4">
      <h2 className="fw-bold mb-3">My History</h2>
      <p className="text-muted">Items you have taken / returned</p>

      <div className="card shadow-sm" style={{ background: "hsl(215,25%,12%)", color: "white" }}>
        <div className="card-body" style={{ maxHeight: 480, overflowY: "auto" }}>
          {rows.length === 0 ? (
            <p className="text-muted">No history available.</p>
          ) : (
            <div className="list-group">
              {rows.map((r, i) => (
                <div key={i} className="list-group-item" style={{ background: "hsl(215,25%,16%)", border: "1px solid hsl(215,20%,25%)" }}>
                  <div className="d-flex justify-content-between">
                    <div>
                      <div className="fw-semibold">{r.itemName} • Qty: {r.qty}</div>
                      <div className="text-muted small">{r.date} — Return: {r.returnDate}</div>
                    </div>
                    <div className="text-muted small">{r.rack} • Slot {r.slot}</div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserHistory;
