import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import { Pencil, Trash2 } from "lucide-react";

const PurchaseItemModal = ({ show, onClose }) => {
  const [items, setItems] = useState([
    {
      id: 1,
      name: "LEDs",
      qty: 50,
      description: "Purchased from local shop",
    },
    {
      id: 2,
      name: "Resistors",
      qty: 100,
      description: "Bulk purchase",
    },
  ]);

  const [form, setForm] = useState({
    name: "",
    qty: "",
    description: "",
  });

  const [editId, setEditId] = useState(null);

  /* ---------------- ADD / UPDATE ITEM ---------------- */
  const handleSave = () => {
    if (!form.name || !form.qty) return;

    if (editId) {
      setItems((prev) =>
        prev.map((it) =>
          it.id === editId ? { ...it, ...form } : it
        )
      );
    } else {
      setItems((prev) => [
        ...prev,
        { id: Date.now(), ...form },
      ]);
    }

    setForm({ name: "", qty: "", description: "" });
    setEditId(null);
  };

  /* ---------------- EDIT ---------------- */
  const handleEdit = (item) => {
    setForm(item);
    setEditId(item.id);
  };

  /* ---------------- DELETE ---------------- */
  const handleDelete = (id) => {
    setItems((prev) => prev.filter((i) => i.id !== id));
  };

  return (
    <Modal show={show} onHide={onClose} centered>
      <Modal.Header
        closeButton
        className="text-white"
        style={{ background: "hsl(215,25%,10%)" }}
      >
        <Modal.Title>Purchase New Items</Modal.Title>
      </Modal.Header>

      <Modal.Body
        style={{
          background: "hsl(215,25%,20%)",
          color: "white",
        }}
      >
        {/* -------- FORM -------- */}
        <Form>
          <Form.Group className="mb-2">
            <Form.Label>Item Name</Form.Label>
            <Form.Control
              value={form.name}
              onChange={(e) =>
                setForm({ ...form, name: e.target.value })
              }
              placeholder="Enter item name"
            />
          </Form.Group>

          <Form.Group className="mb-2">
            <Form.Label>Quantity</Form.Label>
            <Form.Control
              type="number"
              value={form.qty}
              onChange={(e) =>
                setForm({ ...form, qty: e.target.value })
              }
              placeholder="Enter quantity"
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Description</Form.Label>
            <Form.Control
              size="sm"
              value={form.description}
              onChange={(e) =>
                setForm({
                  ...form,
                  description: e.target.value,
                })
              }
              placeholder="Small description"
            />
          </Form.Group>

          <Button
            variant="success"
            size="sm"
            onClick={handleSave}
          >
            {editId ? "Update Item" : "Add Item"}
          </Button>
        </Form>

        <hr />

        {/* -------- ITEM LIST (MAX 2 VISIBLE) -------- */}
        <div
          style={{
            maxHeight: "160px", // ðŸ‘ˆ shows only 2 rows
            overflowY: "auto",
          }}
        >
          {items.map((item) => (
            <div
              key={item.id}
              className="d-flex justify-content-between align-items-start p-2 mb-2 rounded"
              style={{
                background: "hsl(215,25%,16%)",
                border: "1px solid hsl(215,20%,25%)",
              }}
            >
              <div>
                <div className="fw-semibold">
                  {item.name}
                  <span className="ms-2 text-info">
                    (Qty: {item.qty})
                  </span>
                </div>
                <div className="small text-muted">
                  {item.description}
                </div>
              </div>

              <div className="d-flex gap-2">
                <Button
                  size="sm"
                  variant="outline-primary"
                  onClick={() => handleEdit(item)}
                >
                  <Pencil size={14} />
                </Button>
                <Button
                  size="sm"
                  variant="outline-danger"
                  onClick={() => handleDelete(item.id)}
                >
                  <Trash2 size={14} />
                </Button>
              </div>
            </div>
          ))}
        </div>
      </Modal.Body>

      <Modal.Footer
        style={{
          background: "hsl(215,25%,15%)",
          borderTop: "1px solid hsl(215,20%,25%)",
        }}
      >
        <Button variant="secondary" onClick={onClose}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default PurchaseItemModal;
