import React from "react";

const ItemDueCard = ({ containers }) => {
  const rows = [];

  containers.forEach((rack) => {
    rack.slots.forEach((slot) => {
      slot.items?.forEach((item) => {
        item.taken?.forEach((t) => {
          const today = new Date();
          const returnDate = t.returnDate ? new Date(t.returnDate) : null;

          let overdue = 0;
          if (returnDate) {
            overdue = Math.max(
              0,
              Math.floor((today - returnDate) / (1000 * 60 * 60 * 24))
            );
          }

          rows.push({
            itemName: item.name,
            user: t.user,
            takenDate: t.date,
            returnDate: t.returnDate || "-",
            overdue,
          });
        });
      });
    });
  });

  return (
    <div
      className="card shadow-sm"
      style={{
        background: "hsl(215, 25%, 12%)",
        border: "1px solid hsl(215, 20%, 25%)",
        color: "white",
        height: "100%",
      }}
    >
      <div className="card-header fw-bold" style={{ borderColor: "hsl(215,25%,25%)" }}>
        Item Due Dates
      </div>

      <div
        className="card-body"
        style={{
          maxHeight: "320px",
          overflowY: "auto",
          paddingRight: "6px",
        }}
      >
        <table className="table table-secondary table-striped table-sm">
          <thead>
            <tr>
              <th>Item</th>
              <th>Taken By</th>
              <th>Taken</th>
              <th>Return</th>
              <th>Overdue</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {rows.map((r, i) => (
              <tr key={i}>
                <td>{r.itemName}</td>
                <td>{r.user}</td>
                <td>{r.takenDate}</td>
                <td>{r.returnDate}</td>
                <td>{r.overdue} days</td>
                <td>
                  <button className="btn btn-primary btn-sm">Reminder</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ItemDueCard;
