import React from "react";
import { Nav } from "react-bootstrap";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const loc = useLocation();
  return (
    <div className="sidebar bg-dark text-white px-3 py-4 d-flex flex-column justify-content-between position-fixed" style={{ width: 330, minHeight: "100vh", boxShadow: "0 4px 24px #191e32" }}>
      <div>
        <div className="mb-4 text-center">
          <img src="/Gtlogo2.png" alt="logo" style={{ maxWidth: 60 }} className="mb-3" />
          <div className="h5 fw-bold">GANDEEVAN</div>
        </div>
        <Nav className="flex-column gap-1">
          <Nav.Link as={Link} to="/admin/home" active={loc.pathname === "/admin/home"} className="text-white nav-link-custom">Dashboard</Nav.Link>
          <Nav.Link as={Link} to="/admin/containers" active={loc.pathname.startsWith("/admin/containers")} className="text-white nav-link-custom">Containers</Nav.Link>
          <Nav.Link as={Link} to="/admin/users" active={loc.pathname === "/admin/users"} className="text-white nav-link-custom">User Requests</Nav.Link>
          <Nav.Link as={Link} to="/admin/history" active={loc.pathname === "/admin/history"} className="text-white nav-link-custom">History</Nav.Link>
        </Nav>
      </div>
      <div className="small text-muted text-center py-3">Â© Gandeevan</div>
    </div>
  );
};

export default Sidebar;
