import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const LowStockCard = ({ containers = [], onJumpToSlot }) => {
  const lowStock = [];

  containers.forEach((rack) => {
    rack.slots.forEach((slot) => {
      slot.items?.forEach((item) => {
        const used = item.taken?.reduce((s, t) => s + Number(t.qty), 0) || 0;
        const remaining = item.quantity - used;

        if (remaining < 6) {
          lowStock.push({
            name: item.name,
            remaining,
            rack: rack.name,
            slot: slot.slotNumber,
            containerId: rack.id,
          });
        }
      });
    });
  });

  return (
    <div
      className="card shadow-sm mb-4"
      style={{
        backgroundColor: "hsl(215, 25%, 14%)",
        color: "white",
        border: "1px solid hsl(215, 20%, 25%)",
      }}
    >
      <div className="card-header">
        <h5 className="mb-0">Limited Stock Items</h5>
      </div>

      <div
        className="card-body"
        style={{ maxHeight: "230px", overflowY: "auto" }}
      >
        {lowStock.length === 0 ? (
          <p className="text-muted">No low-stock items.</p>
        ) : (
          lowStock.map((item, index) => (
            <div
              key={index}
              className="p-2 mb-2 rounded"
              style={{
                backgroundColor: "hsl(215, 25%, 16%)",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            >
              <div className="d-flex justify-content-between">
                <div>
                  <strong>{item.name}</strong>
                  <div className="small text-muted">
                    Remaining: {item.remaining}
                  </div>
                  <div className="small text-muted">
                    {item.rack} • Slot {item.slot}
                  </div>
                </div>

                <button
                  className="btn btn-primary btn-sm"
                  onClick={() =>
                    onJumpToSlot(item.containerId, item.slot)
                  }
                >
                  ➕ Add
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default LowStockCard;
