// src/pages/users/UserDashboard.jsx
import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";

/**
 * StatusTag - small colored pill for status
 */
const StatusTag = ({ status }) => {
  const base = {
    padding: "4px 10px",
    borderRadius: 14,
    fontSize: 12,
    fontWeight: 700,
    display: "inline-block",
    minWidth: 72,
    textAlign: "center",
  };

  if (status === "Pending")
    return <span style={{ ...base, background: "hsl(45,100%,65%)", color: "#222" }}>{status}</span>;
  if (status === "Approved")
    return <span style={{ ...base, background: "hsl(140,40%,30%)", color: "white" }}>{status}</span>;
  // Rejected / other
  return <span style={{ ...base, background: "hsl(10,80%,40%)", color: "white" }}>{status}</span>;
};

/**
 * UserDashboard
 *
 * Props:
 *  - permissionRequests: array
 *  - onCreateRequest: fn(form)
 *  - onCancelRequest: fn(requestId)
 *  - currentUser: { name, email, ... }
 */
const UserDashboard = ({ permissionRequests = [], onCreateRequest, onCancelRequest, currentUser }) => {
  // Request modal (create)
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [form, setForm] = useState({
    itemName: "",
    quantity: 1,
    projectName: "",
    returnable: false,
    returnDate: "",
    message: "",
  });

  // Detail modal (view single request)
  const [selectedReq, setSelectedReq] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);

  const userRequests = permissionRequests || [];

  // Create handler
  const handleCreateSubmit = (e) => {
    e.preventDefault();
    if (!form.itemName || !form.quantity || !form.projectName) {
      alert("Please fill required fields");
      return;
    }
    onCreateRequest(form);
    setShowCreateModal(false);
    setForm({
      itemName: "",
      quantity: 1,
      projectName: "",
      returnable: false,
      returnDate: "",
      message: "",
    });
  };

  // Open detail modal
  const openDetail = (req) => {
    setSelectedReq(req);
    setShowDetailModal(true);
  };

  // Cancel request helper (works from card or modal)
  const cancelRequest = (id) => {
    if (!window.confirm("Cancel this request?")) return;
    onCancelRequest(id);
    // close modal if it was open
    if (selectedReq && selectedReq.id === id) {
      setShowDetailModal(false);
      setSelectedReq(null);
    }
  };

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-start mb-3">
        <div>
          <p className="text-muted">User Dashboard</p>
          <h2 className="fw-bold mb-1">Welcome, {currentUser?.name || "User"}</h2>
        </div>

        <div>
          <Button onClick={() => setShowCreateModal(true)} className="btn-primary">
            Request New Item
          </Button>
        </div>
      </div>

      {/* My Requests */}
      <div
  className="card mb-3"
  style={{
    background: "hsl(215,25%,12%)",
    border: "1px solid hsl(215,20%,25%)",
    color: "white",
    borderRadius: 10,
    boxShadow: "0 4px 24px rgba(0,0,0,0.10)",
  }}
>
  {permissionRequests.length === 0 ? (
    <p className="text-muted p-3">No requests found.</p>
  ) : (
    permissionRequests.map((req) => (
      <div
        key={req.id}
        className="d-flex align-items-center justify-content-between p-3 mb-2"
        style={{
          background: "hsl(215,25%,16%)",
          borderRadius: 8,
          border: "1px solid hsl(215,20%,22%)",
        }}
      >
        {/* LEFT: Avatar and Name */}
        <div className="d-flex align-items-center" style={{ minWidth: 220 }}>
          <div
            className="rounded-circle d-flex align-items-center justify-content-center"
            style={{
              width: 36,
              height: 36,
              background: "hsl(215,25%,18%)",
              marginRight: 12,
              fontSize: 21,
              color: "#ccc",
            }}
          >
            {/* Replace with icon if needed */}
            <i className="bi bi-person" />
          </div>
          <span className="fw-bold">{req.userName}</span>
        </div>

        {/* MIDDLE: Info grid (item, date, qty, return) */}
        <div
          className="d-flex flex-column flex-grow-1"
          style={{ fontSize: 15, minWidth: 330, marginLeft: 22 }}
        >
          <div style={{ marginBottom: 5 }}>{req.itemName}</div>
          <div className="d-flex gap-3 flex-wrap text-muted small">
            <div>{req.date}</div>
            <div>Qty: <span className="fw-bold">{req.quantity}</span></div>
            <div>Return: <span>{req.returnDate ? req.returnDate : "No"}</span></div>
          </div>
        </div>

        {/* RIGHT: Buttons and details */}
        <div
          style={{ minWidth: 180, textAlign: "right", display: "flex", flexDirection: "column", alignItems: "end" }}
        >
          <div className="mb-2">
            <Button
              variant="success"
              size="sm"
              className="me-2"
              onClick={() => approveRequest(req.id)}
            >
              Approve
            </Button>
            <Button
              variant="danger"
              size="sm"
              onClick={() => rejectRequest(req.id)}
            >
              Reject
            </Button>
          </div>
          <span className="text-muted small clickable">
            Click for details â†’
          </span>
        </div>
      </div>
    ))
  )}
</div>


      {/* ---------- CREATE REQUEST MODAL ---------- */}
      <Modal show={showCreateModal} onHide={() => setShowCreateModal(false)} centered>
        <Modal.Header closeButton style={{ background: "hsl(215,25%,12%)", color: "white" }}>
          <Modal.Title>Request New Item</Modal.Title>
        </Modal.Header>
        <Modal.Body style={{ background: "hsl(215,25%,14%)", color: "white" }}>
          <Form onSubmit={handleCreateSubmit}>
            <Form.Group className="mb-2">
              <Form.Label>Item Name *</Form.Label>
              <Form.Control
                value={form.itemName}
                onChange={(e) => setForm({ ...form, itemName: e.target.value })}
                style={{ background: "hsl(215,25%,12%)", color: "white", border: "1px solid hsl(215,20%,25%)" }}
              />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Quantity *</Form.Label>
              <Form.Control
                type="number"
                min={1}
                value={form.quantity}
                onChange={(e) => setForm({ ...form, quantity: e.target.value })}
                style={{ background: "hsl(215,25%,12%)", color: "white", border: "1px solid hsl(215,20%,25%)" }}
              />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Project Name *</Form.Label>
              <Form.Control
                value={form.projectName}
                onChange={(e) => setForm({ ...form, projectName: e.target.value })}
                style={{ background: "hsl(215,25%,12%)", color: "white", border: "1px solid hsl(215,20%,25%)" }}
              />
            </Form.Group>

            <Form.Group className="mb-2 d-flex align-items-center">
              <Form.Check
                type="checkbox"
                label="Returnable?"
                checked={form.returnable}
                onChange={(e) => setForm({ ...form, returnable: e.target.checked })}
              />
              {form.returnable && (
                <input
                  type="date"
                  className="form-control ms-2"
                  value={form.returnDate}
                  onChange={(e) => setForm({ ...form, returnDate: e.target.value })}
                  style={{
                    maxWidth: 220,
                    background: "hsl(215,25%,12%)",
                    color: "white",
                    border: "1px solid hsl(215,20%,25%)",
                  }}
                />
              )}
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Message (optional)</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={form.message}
                onChange={(e) => setForm({ ...form, message: e.target.value })}
                style={{ background: "hsl(215,25%,12%)", color: "white", border: "1px solid hsl(215,20%,25%)" }}
              />
            </Form.Group>

            <div className="d-flex gap-2 justify-content-end mt-3">
              <Button variant="secondary" onClick={() => setShowCreateModal(false)}>
                Close
              </Button>
              <Button variant="primary" type="submit">
                Submit Request
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>

      {/* ---------- DETAIL MODAL (card click) ---------- */}
      <Modal show={showDetailModal} onHide={() => setShowDetailModal(false)} centered>
        <Modal.Header closeButton style={{ background: "hsl(215,25%,12%)", color: "white" }}>
          <Modal.Title>Request Details</Modal.Title>
        </Modal.Header>
        <Modal.Body style={{ background: "hsl(215,25%,14%)", color: "white" }}>
          {!selectedReq ? (
            <p className="text-muted">No item selected.</p>
          ) : (
            <div>
              <p>
                <strong>Requested by:</strong> {selectedReq.userName} <br />
                <small className="text-muted">{selectedReq.userEmail}</small>
              </p>

              <hr />

              <p>
                <strong>Item:</strong> {selectedReq.itemName}
              </p>
              <p>
                <strong>Quantity:</strong> {selectedReq.quantity}
              </p>
              <p>
                <strong>Project:</strong> {selectedReq.whichProject}
              </p>
              <p>
                <strong>Returnable:</strong> {selectedReq.itemType === "Returnable" ? "Yes" : "No"}
              </p>
              {selectedReq.itemType === "Returnable" && (
                <p>
                  <strong>Return Date:</strong> {selectedReq.returnDate}
                </p>
              )}

              <p>
                <strong>Requested on:</strong> {selectedReq.dateRequested}
              </p>

              <p>
                <strong>Status:</strong> <StatusTag status={selectedReq.status} />
              </p>

              <hr />

              <p>
                <strong>Message:</strong>
              </p>
              <div
                style={{
                  background: "hsl(215,25%,12%)",
                  color: "hsl(210,40%,98%)",
                  padding: 10,
                  borderRadius: 6,
                  border: "1px solid hsl(215,20%,25%)",
                }}
              >
                {selectedReq.message || "No message provided"}
              </div>
            </div>
          )}
        </Modal.Body>
        <Modal.Footer style={{ background: "hsl(215,25%,12%)" }}>
          <Button variant="secondary" onClick={() => setShowDetailModal(false)}>
            Close
          </Button>

          {/* If pending, allow cancel from modal */}
          {selectedReq?.status === "Pending" && (
            <Button
              variant="danger"
              onClick={() => {
                cancelRequest(selectedReq.id);
              }}
            >
              Cancel Request
            </Button>
          )}
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default UserDashboard;
