import React from "react";
import { Navbar, Container, NavDropdown, Button } from "react-bootstrap";
import { FaUserCircle } from "react-icons/fa";
import { LogOut, Menu, Package } from "lucide-react";

const TopNavbar = ({ admin, onMenuClick, sidebarOpen }) => {
  const getInitials = (name) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase();
  };

  return (
    <>
      <Navbar bg="dark" variant="dark" expand="md" className="px-3 py-2 border-bottom">
        <Container fluid className="d-flex align-items-center justify-content-between">
          {/* Left Side */}
          <div className="d-flex align-items-center gap-2">
            {/* Mobile Menu Icon */}
            <Button
              variant="outline-light"
              size="sm"
              className="d-lg-none me-2"
              onClick={onMenuClick}
            >
              <Menu
                className={`transition-transform ${sidebarOpen ? "rotate-90" : ""}`}
                size={18}
              />
            </Button>

            {/* Logo + Title */}
            <div className="d-flex align-items-center">
              <Package size={28} className="text-primary me-2" />
              <span className="navbar-brand mb-0 fs-5 fw-bold">ContainerHub</span>
              <small className="text-muted d-none d-sm-inline ps-2">
                Admin Portal
              </small>
            </div>
          </div>

          {/* Right Side */}
          <div className="d-flex align-items-center gap-3">
            {/* Admin Info */}
            <div className="text-end d-none d-md-block">
              <p className="mb-0 fw-semibold text-white">{admin?.name || "Admin"}</p>
              <small className="text-secondary">{admin?.email || "admin@example.com"}</small>
            </div>

            {/* Avatar (Initials) */}
            <div
              className="rounded-circle border border-primary bg-primary text-white d-flex justify-content-center align-items-center fw-bold"
              style={{ width: 40, height: 40 }}
            >
              {getInitials(admin?.name || "A")}
            </div>

            {/* Dropdown / Logout */}
            <NavDropdown
              align="end"
              title={<FaUserCircle size={28} color="#f8f9fa" />}
              id="user-dropdown"
              menuVariant="dark"
            >
              <NavDropdown.Item href="#profile">Profile</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item
                href="#logout"
                className="text-danger d-flex align-items-center gap-2"
              >
                <LogOut size={18} /> Logout
              </NavDropdown.Item>
            </NavDropdown>
          </div>
        </Container>
      </Navbar>
    </>
  );
};

export default TopNavbar;
