import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Check, X, Mail, Calendar, User } from "lucide-react";
import { Modal, Button } from "react-bootstrap";

const UserApproval = ({ pendingUsers, onApprove, onReject }) => {
  const [selectedUser, setSelectedUser] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const handleCardClick = (user) => {
    setSelectedUser(user);
    setShowModal(true);
  };

  const handleApprove = () => {
    onApprove(selectedUser.id);
    alert(`${selectedUser.name} has been approved successfully.`);
    setShowModal(false);
  };

  const handleReject = () => {
    onReject(selectedUser.id);
    alert(`${selectedUser.name} has been rejected.`);
    setShowModal(false);
  };

  if (!pendingUsers || pendingUsers.length === 0) {
    return (
      <div
        className="card shadow-sm my-3"
        style={{
          backgroundColor: "hsl(215, 25%, 14%)",
          color: "hsl(210, 40%, 98%)",
          border: "1px solid hsl(215, 20%, 25%)",
        }}
      >
        <div
          className="card-header"
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            color: "hsl(210, 40%, 98%)",
            borderBottom: "1px solid hsl(215, 20%, 25%)",
          }}
        >
          <h5 className="mb-0">Pending User Approvals</h5>
        </div>
        <div className="card-body text-center text-secondary py-4">
          No pending approvals
        </div>
      </div>
    );
  }

  return (
    <>
      <div
        className="card shadow-sm my-3"
        style={{
          backgroundColor: "hsl(215, 25%, 14%)",
          color: "hsl(210, 40%, 98%)",
          border: "1px solid hsl(215, 20%, 25%)",
        }}
      >
        {/* Header */}
        <div
          className="card-header d-flex justify-content-between align-items-center flex-wrap gap-2"
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            color: "hsl(210, 40%, 98%)",
            borderBottom: "1px solid hsl(215, 20%, 25%)",
          }}
        >
          <h5 className="mb-0">Pending User Approvals</h5>
          <span
            className="badge"
            style={{
              backgroundColor: "hsl(215, 20%, 20%)",
              color: "hsl(210, 40%, 98%)",
              border: "1px solid hsl(215, 20%, 25%)",
            }}
          >
            {pendingUsers.length} pending
          </span>
        </div>

        {/* Card List */}
        <div className="card-body">
          {pendingUsers.map((user) => (
            <div
              key={user.id}
              className="d-flex justify-content-between align-items-center border rounded p-3 mb-3 shadow-sm"
              style={{
                backgroundColor: "hsl(215, 25%, 12%)",
                borderColor: "hsl(215, 20%, 25%)",
                cursor: "pointer",
                transition: "background-color 0.2s ease",
              }}
              onMouseEnter={(e) =>
                (e.currentTarget.style.backgroundColor = "hsl(215, 25%, 16%)")
              }
              onMouseLeave={(e) =>
                (e.currentTarget.style.backgroundColor = "hsl(215, 25%, 12%)")
              }
              onClick={() => handleCardClick(user)}
            >
              <div>
                <p className="fw-semibold mb-1 text-light d-flex align-items-center gap-2">
                  <User size={16} />
                  {user.name}
                </p>
                <div className="d-flex flex-wrap gap-3 small text-secondary">
                  <span className="d-flex align-items-center gap-1">
                    <Mail size={14} /> {user.email}
                  </span>
                  <span className="d-flex align-items-center gap-1">
                    <Calendar size={14} /> {user.registeredDate}
                  </span>
                </div>
              </div>
              <div className="text-end small text-muted">Click for details â†’</div>
            </div>
          ))}
        </div>
      </div>

      {/* ðŸŸ¦ Modal Popup */}
      <Modal
        show={showModal}
        onHide={() => setShowModal(false)}
        centered
        backdrop="static"
        className="text-light"
      >
        <Modal.Header
          closeButton
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            borderBottom: "1px solid hsl(215, 20%, 25%)",
          }}
        >
          <Modal.Title>Add Item to {selectedUser?.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body
          style={{
            backgroundColor: "hsl(215, 25%, 14%)",
            color: "hsl(210, 40%, 98%)",
          }}
        >
          {selectedUser && (
            <>
              <div className="mb-3">
                <label className="form-label">Name</label>
                <input
                  type="text"
                  className="form-control bg-dark text-light border-secondary"
                  value={selectedUser.name}
                  disabled
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Email</label>
                <input
                  type="text"
                  className="form-control bg-dark text-light border-secondary"
                  value={selectedUser.email}
                  disabled
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Registered Date</label>
                <input
                  type="text"
                  className="form-control bg-dark text-light border-secondary"
                  value={selectedUser.registeredDate}
                  disabled
                />
              </div>
            </>
          )}
        </Modal.Body>
        <Modal.Footer
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            borderTop: "1px solid hsl(215, 20%, 25%)",
          }}
        >
          <Button
            variant="secondary"
            onClick={() => setShowModal(false)}
            className="px-3"
          >
            Cancel
          </Button>
          <Button
            variant="danger"
            onClick={handleReject}
            className="px-3"
          >
            <X size={16} className="me-1" /> Reject
          </Button>
          <Button
            variant="primary"
            onClick={handleApprove}
            className="px-3"
          >
            <Check size={16} className="me-1" /> Approve
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default UserApproval;
